INSERT INTO `creature_text` VALUES (92001, 3, 0, 'Приветствую тебя, $gпутник:путешественница;. Наша роща знавала и более мирные времена, но друиды когтя защищают ее всеми силами.', 12, 0, 100, 0, 0, 0, 0, 828842, 828842, 0, 'Саша Утренняя Роса to Player');
INSERT INTO `creature_text` VALUES (92034, 3, 0, 'Мы не потерпим воровства, раб.', 12, 0, 100, 0, 0, 0, 94078, 172449, 172449, 0, 'Охотник за рабами Зсанеш to Player');
INSERT INTO `creature_text` VALUES (92038, 0, 0, 'Вам не удержать нас!', 14, 0, 100, 565, 0, 50360, 94300, 0, 0, 0, 'Истекающий слюной кровопийца to 0');
INSERT INTO `creature_text` VALUES (92041, 0, 0, 'Теперь умрите!', 14, 0, 100, 0, 0, 0, 98255, 0, 0, 0, 'to Player');
INSERT INTO `creature_text` VALUES (92104, 3, 0, '$GОн повверил:Она поверила;!', 12, 0, 100, 0, 0, 0, 94215, 2247, 2247, 0, 'Плут из племени Листа Чертополоха to Player');
INSERT INTO `creature_text` VALUES (92104, 3, 0, 'Что, $gдумал:думала;, все будет так просто?', 12, 0, 100, 0, 0, 0, 94216, 2161, 2161, 0, 'Плут из племени Листа Чертополоха to Player');
INSERT INTO `creature_text` VALUES (92111, 3, 0, 'Мы справились! Карликовые совы этой рощи теперь в безопасности – и все благодаря тебе!', 12, 0, 100, 0, 0, 0, 96056, 377660, 377660, 0, 'Лорель Перо Мудрости to Player');
INSERT INTO `creature_text` VALUES (92111, 3, 0, 'Приготовься, $gгерой:воительница;. Он спускается!', 12, 0, 100, 0, 0, 0, 96055, 11757, 11757, 0, 'Лорель Перо Мудрости to Player');
INSERT INTO `creature_text` VALUES (92122, 0, 0, 'Structural integrity at 0%%!', 12, 0, 100, 0, 0, 70946, 99197, 0, 0, 0, 'Гномреганский боевой механик to Ожившее пламя Скверны');
INSERT INTO `creature_text` VALUES (92130, 0, 0, 'Давай поторопимся с этим. Гул\'дан превратил доки в вербовочный лагерь.', 12, 0, 100, 1, 0, 50549, 94253, 0, 0, 0, 'Солог Рырк to Player');
INSERT INTO `creature_text` VALUES (92130, 1, 0, '%s забирает ружье с трупа поверженного врага.', 16, 0, 100, 0, 0, 0, 94251, 0, 0, 0, 'Солог Рырк to Player');
INSERT INTO `creature_text` VALUES (92130, 2, 0, 'Чернорук приказал соорудить огромную пушку. Я построил для нее гигантский корабль. Но больше – не значит лучше, и битва у Шаттрата доказала это.', 12, 0, 100, 1, 0, 50550, 94256, 0, 0, 0, 'Солог Рырк to Player');
INSERT INTO `creature_text` VALUES (92130, 3, 0, 'Этими крючьями мы затягиваем корабли в гавань.', 12, 0, 100, 1, 0, 50551, 94257, 0, 0, 0, 'Солог Рырк to Player');
INSERT INTO `creature_text` VALUES (92130, 4, 0, 'Вот поэтому на постройках нужны шипы!', 12, 0, 100, 0, 0, 50552, 94258, 0, 0, 0, 'Солог Рырк to Player');
INSERT INTO `creature_text` VALUES (92130, 5, 0, 'I will guard. Get the blueprints.', 12, 0, 100, 1, 0, 50553, 94271, 0, 0, 0, 'Солог Рырк to Player');
INSERT INTO `creature_text` VALUES (92142, 0, 0, 'Мой меч жаждет отмщения!', 14, 0, 100, 0, 0, 50405, 96274, 0, 0, 0, 'Мастер клинка Джубей\'тос to 0');
INSERT INTO `creature_text` VALUES (92142, 1, 0, '|TInterface\\Icons\\Spell_Frost_WindWalkOn:20|t %s активирует способность |cFFFF0404|Hspell:183480|h[\"Ветроступ\"]|h|r, оставляя на своем месте зеркальные изображения!', 41, 0, 100, 0, 0, 50405, 94867, 0, 0, 0, 'Мастер клинка Джубей\'тос to 0');
INSERT INTO `creature_text` VALUES (92142, 2, 0, '%s выходит из тени!', 41, 0, 100, 0, 0, 0, 96134, 0, 0, 0, 'Мастер клинка Джубей\'тос to Мастер клинка Джубей\'тос');
INSERT INTO `creature_text` VALUES (92142, 3, 0, 'Гори! Гори!', 14, 0, 100, 0, 0, 50412, 96278, 0, 0, 0, 'Мастер клинка Джубей\'тос to 0');
INSERT INTO `creature_text` VALUES (92142, 4, 0, 'Я - вечное пламя!', 14, 0, 100, 0, 0, 50406, 96625, 0, 0, 0, 'Мастер клинка Джубей\'тос to 0');
INSERT INTO `creature_text` VALUES (92142, 5, 0, 'Ваша тьма пылает во мне.', 14, 0, 100, 0, 0, 50414, 96621, 0, 0, 0, 'Мастер клинка Джубей\'тос to Диа Зловещий Шепот');
INSERT INTO `creature_text` VALUES (92144, 0, 0, 'Она терзает. Бескрайняя и вечная.', 14, 0, 100, 0, 0, 50772, 96251, 0, 0, 0, 'Диа Зловещий Шепот to 0');
INSERT INTO `creature_text` VALUES (92144, 1, 0, '%s |cFFFF0404|Hspell:184476|h[пожинает]|h|r всех отмеченных противников!', 41, 0, 100, 0, 0, 50772, 95369, 0, 0, 0, 'Диа Зловещий Шепот to 0');
INSERT INTO `creature_text` VALUES (92144, 2, 0, 'И мертвые вновь восстанут из могил.', 14, 0, 100, 0, 0, 50774, 96253, 0, 0, 0, 'Диа Зловещий Шепот to Диа Зловещий Шепот');
INSERT INTO `creature_text` VALUES (92144, 3, 0, '%s призывает ужасы из |cFFFF0404|Hspell:184681|h[Тьмы]|h|r!', 41, 0, 100, 0, 0, 50774, 95262, 0, 0, 0, 'Диа Зловещий Шепот to Диа Зловещий Шепот');
INSERT INTO `creature_text` VALUES (92144, 4, 0, 'Бездна жаждет.', 14, 0, 100, 0, 0, 50773, 96252, 0, 0, 0, 'Диа Зловещий Шепот to 0');
INSERT INTO `creature_text` VALUES (92144, 5, 0, 'Их души будут запятнаны тьмой.', 14, 0, 100, 0, 0, 50771, 96250, 0, 0, 0, 'Диа Зловещий Шепот to 0');
INSERT INTO `creature_text` VALUES (92144, 6, 0, '%s отмечает половину своих противников |cFFFF0404|Hspell:184449|h[\"Знаком некроманта\"]|h|r!', 41, 0, 100, 0, 0, 50771, 95261, 0, 0, 0, 'Диа Зловещий Шепот to 0');
INSERT INTO `creature_text` VALUES (92144, 7, 0, 'Милосердие смерти.', 14, 0, 100, 0, 0, 28232, 96246, 0, 0, 0, 'Диа Зловещий Шепот to Player');
INSERT INTO `creature_text` VALUES (92145, 3, 0, 'А вы знали, что $gВерховный лорд однажды убил:Верховная леди однажды убила; пятьсот демонов одним ударом?', 12, 0, 100, 1, 0, 0, 94443, 10786, 10786, 0, 'Заступник из ордена Серебряной Длани to Player');
INSERT INTO `creature_text` VALUES (92145, 3, 0, 'Паладины со всего света наконец-то собрались вместе... у Легиона нет ни единого шанса.', 12, 0, 100, 1, 0, 0, 94448, 3020, 3020, 0, 'Заступник из ордена Серебряной Длани to Player');
INSERT INTO `creature_text` VALUES (92145, 3, 0, 'Я где-то читал, что $gВерховный лорд:Верховная леди; $p $gубил:убила; повелителя огня Рагнароса... дважды!', 12, 0, 100, 1, 0, 0, 94445, 16934, 16934, 0, 'Заступник из ордена Серебряной Длани to Player');
INSERT INTO `creature_text` VALUES (92146, 0, 0, 'Я выпущу вам кишки!', 14, 0, 100, 0, 0, 50401, 96272, 0, 0, 0, 'Гуртогг Кипящая Кровь to Player');
INSERT INTO `creature_text` VALUES (92146, 1, 0, 'Больше! Хочу больше!', 14, 0, 100, 0, 0, 50402, 96273, 0, 0, 0, 'Гуртогг Кипящая Кровь to 0');
INSERT INTO `creature_text` VALUES (92146, 2, 0, '|TInterface\\Icons\\inv_misc_boilingblood.blp:20|t %s начинает использовать |cFFFF0404|Hspell:184357|h[\"Оскверненную кровь\"]|h|r!', 41, 0, 100, 0, 0, 50402, 95260, 0, 0, 0, 'Гуртогг Кипящая Кровь to 0');
INSERT INTO `creature_text` VALUES (92146, 3, 0, 'Выпью кровь! Пожру плоть!', 14, 0, 100, 0, 0, 50400, 96271, 0, 0, 0, 'Гуртогг Кипящая Кровь to Player');
INSERT INTO `creature_text` VALUES (92146, 4, 0, 'АрРРрргх...', 14, 0, 100, 0, 0, 50389, 96257, 0, 0, 0, 'Гуртогг Кипящая Кровь to Player');
INSERT INTO `creature_text` VALUES (92146, 5, 0, 'Железная Орда сокрушит вас!', 14, 0, 100, 0, 0, 50388, 96256, 0, 0, 0, 'Гуртогг Кипящая Кровь to Гуртогг Кипящая Кровь');
INSERT INTO `creature_text` VALUES (92147, 0, 0, 'Приветствую, $gВерховный лорд:Верховная леди;.', 12, 0, 100, 66, 0, 0, 101800, 0, 0, 0, 'Заступница из ордена Серебряной Длани to Player');
INSERT INTO `creature_text` VALUES (92147, 1, 0, 'Приветствую, |3-6($c). Верховный лорд сказал, что мы должны принять тебя как дорогого гостя, если, конечно, ты будешь вести себя подобающе.', 12, 0, 100, 1, 0, 0, 121418, 0, 0, 0, 'Заступница из ордена Серебряной Длани to Player');
INSERT INTO `creature_text` VALUES (92148, 0, 0, 'Тебе не кажется, что $gВерховный лорд:Верховная леди; $p – $gполубог:полубогиня;?', 12, 0, 100, 6, 0, 0, 94450, 0, 0, 0, 'Заступник из ордена Серебряной Длани to Player');
INSERT INTO `creature_text` VALUES (92149, 0, 0, 'Как думаешь, меня возьмут на следующее задание? Я слышала, $gВерховный лорд:Верховная леди; даже позволяет оставить себе кое-какие трофеи!', 12, 0, 100, 0, 0, 0, 94447, 0, 0, 0, 'Заступник из ордена Серебряной Длани to Player');
INSERT INTO `creature_text` VALUES (92150, 3, 0, 'Приветствую, $gВерховный лорд:Верховная леди;.', 12, 0, 100, 66, 0, 0, 101800, 350053, 350053, 0, 'Страж крови to Player');
INSERT INTO `creature_text` VALUES (92151, 0, 0, 'Приветствую, $gВерховный лорд:Верховная леди;.', 12, 0, 100, 66, 0, 0, 101800, 0, 0, 0, 'Стражница крови to Player');
INSERT INTO `creature_text` VALUES (92151, 3, 0, 'Приветствую, |3-6($c). Верховный лорд сказал, что мы должны принять тебя как дорогого гостя, если, конечно, ты будешь вести себя подобающе.', 12, 0, 100, 1, 0, 0, 121418, 2527, 2527, 0, 'Стражница крови to Player');
INSERT INTO `creature_text` VALUES (92153, 3, 0, 'Hail, Highlord.', 12, 0, 100, 66, 0, 0, 101800, 10110, 10110, 0, 'Defender of Argus to Player');
INSERT INTO `creature_text` VALUES (92180, 0, 0, 'Пфф! Для моей коллекции ты слишком $gуродлив:уродлива;. Лучше просто убить тебя!', 12, 0, 100, 0, 0, 0, 94262, 0, 0, 0, 'Сирсея to Player');
INSERT INTO `creature_text` VALUES (92180, 1, 0, 'Моя коллекция... это все, что ...у меня было...', 12, 0, 100, 0, 0, 0, 94263, 0, 0, 0, 'Сирсея to Player');
INSERT INTO `creature_text` VALUES (92183, 0, 0, 'Go see if you can find a frost mage to help us, <name>. If we can get our quenching trough cool enough, we can perform this leysmithing technique.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (92184, 1, 1, 'Да, у меня есть несколько манекенов про запас, но я пока не готова с ними расстаться. Разве что...', 12, 0, 0, 0, 0, 0, 101088, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (92184, 1, 2, 'Не хочешь быстренько сбегать по одному небольшому поручению? Если все сделаешь, манекены твои.', 12, 0, 0, 0, 0, 0, 101089, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (92184, 1, 3, 'I want to give my dress to Shandi. It was all smeared with soot.', 12, 0, 0, 0, 0, 0, 101090, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (92184, 2, 0, 'Ну, Шанди молодец... знает, как вывести с одежи кровищу! Вот, держи свои манекены – все как было обещано.', 12, 0, 0, 0, 0, 0, 101091, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (92191, 0, 0, 'А что эта погань делает на моей пристани?!', 12, 0, 100, 5, 0, 0, 94273, 0, 0, 0, 'Адмирал Нар to Player');
INSERT INTO `creature_text` VALUES (92191, 1, 0, 'Гул\'дан... положит этому конец...', 12, 0, 100, 0, 0, 0, 94274, 0, 0, 0, 'Адмирал Нар to Player');
INSERT INTO `creature_text` VALUES (92194, 0, 0, 'Извлекатель данных 3000, возможно, расшифрует этот блок. Лучше скрестить пальцы, чтобы он не взорвался.', 12, 0, 100, 0, 0, 0, 98376, 0, 0, 0, 'Тимофей Ошенко to Player');
INSERT INTO `creature_text` VALUES (92194, 3, 0, 'Этот блок содержит какой-то текст на древнем диалекте высокорожденных, которого я не знаю. Возможно, профессор Палин в Скриптории сможет перевести его.', 12, 0, 100, 30, 0, 0, 98378, 17203, 17203, 0, 'Тимофей Ошенко to Player');
INSERT INTO `creature_text` VALUES (92195, 0, 0, 'Not down here? Alright then, go ahead and use the ladder to check the upper scribrary.', 12, 0, 100, 0, 0, 0, 109459, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (92195, 2, 2, 'Don\'t give up hope, %s. Keep milling, and inspect your inks for unusual properties. Together, you and \r\nI may be able to fill in the gaps where this book is lacking.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (92195, 3, 3, 'Don\'t bother reading it or anything.', 12, 0, 100, 0, 0, 0, 108962, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (92195, 4, 4, 'Just go ahead and sign it.', 12, 0, 100, 0, 0, 0, 108963, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (92195, 5, 5, 'Right there on the line.', 12, 0, 100, 0, 0, 0, 108964, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (92195, 6, 6, 'I too would like to experiment with this pigment. Let\'s see here...', 12, 0, 100, 467, 0, 0, 99692, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (92195, 7, 7, 'That went better than expected. Yes, this pigment WILL work out nicely.', 12, 0, 100, 0, 0, 0, 99693, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (92195, 8, 8, 'THEY THREW AWAY A... errr... I mean, let\'s see what I can do with these.', 12, 0, 100, 1, 0, 0, 99723, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (92195, 9, 9, 'This fish pigment really is something. I should start selling it...', 12, 0, 100, 1, 0, 0, 99724, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (92195, 10, 10, 'And for the final touch, just a bit of magic and...', 12, 0, 100, 1, 0, 0, 99725, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (92195, 11, 11, 'Voila! Finished.', 12, 0, 100, 1, 0, 0, 99726, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (92195, 12, 12, 'I suppese I do owe that blowhard a favor. Lucky for you, translation is my specialty!', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (92195, 13, 13, 'This is ancient vrykul script, the property of someone named \"Svena\". A rare end intriguing find indeed. Tell Deucus he is now in MY debt!', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (92201, 3, 0, 'Потерпи, Дуболист. Знаю, что больно, но ты все же потерпи, ладно?', 12, 0, 100, 0, 0, 0, 94277, 341649, 341649, 0, 'Итанил Бич Тени to Player');
INSERT INTO `creature_text` VALUES (92201, 3, 0, 'Тьма, явись!', 12, 0, 100, 0, 0, 0, 94281, 14605, 14605, 0, 'Итанил Бич Тени to Player');
INSERT INTO `creature_text` VALUES (92202, 3, 0, '%s стонет в агонии.', 16, 0, 100, 0, 0, 0, 108347, 8743, 8743, 0, 'Дуболист to Player');
