INSERT INTO `creature_text` VALUES (92208, 0, 0, '|TINTERFACE\\ICONS\\ability_fixated_state_red.blp:20|t Дух Рокового Огня |cFFF00000|Hspell:182879|h[сосредотачивает внимание]|h|r на вас!', 42, 0, 100, 0, 0, 0, 96743, 0, 0, 0, 'Archimonde to Player');
INSERT INTO `creature_text` VALUES (92213, 0, 0, 'Передай привет Кордане. Она как-то отдалилась…', 12, 0, 100, 6, 0, 50644, 96636, 0, 0, 0, 'Верховный маг Кадгар to Player');
INSERT INTO `creature_text` VALUES (92213, 3, 0, 'Мы отправим всю мерзость Гул\'дана на дно!', 12, 0, 100, 1, 0, 50641, 98667, 32065, 32065, 0, 'Верховный маг Кадгар to Player');
INSERT INTO `creature_text` VALUES (92213, 3, 0, 'С каждой минутой Гул\'дан все сильнее. Но он больше не крадется, скрываясь за тенями, — мы знаем, где он, и что он задумал. Пора нанести удар! И да будет Свет милостлив к нашим душам.', 12, 0, 100, 1, 0, 50647, 95764, 87172, 87172, 0, 'Верховный маг Кадгар to Player');
INSERT INTO `creature_text` VALUES (92218, 0, 0, 'Take wing with Vethir, friend, and show these vrykul the true fury of the storm!', 12, 0, 100, 0, 0, 0, 98432, 0, 0, 0, 'Trimjaris to Player');
INSERT INTO `creature_text` VALUES (92219, 0, 0, 'Everything rests upon your shoulders, commander. Build us a navy so we can assault Gul\'dan from all sides. Good luck!', 12, 0, 100, 1, 0, 51321, 94424, 0, 0, 0, 'Король Вариан Ринн to Player');
INSERT INTO `creature_text` VALUES (92242, 0, 0, 'I\'ll let the others know of your gift.. $n. Thank you again.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (92242, 1, 1, 'Hey, you lot! If you need a quick pick-me-up, come and get a Rivermane Remedy!', 12, 0, 100, 1, 0, 0, 98755, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (92242, 2, 2, 'You\'ll find a pumpkin patch at the Rivermane\'s camp. Choose the ripest ones for their juice.', 12, 0, 0, 0, 0, 0, 108552, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (92243, 0, 0, 'Is that the best you can do? We\'ll have to do better than that. It won\'t work that way.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Muirn Iron Horn to Player');
INSERT INTO `creature_text` VALUES (92243, 0, 0, 'И это все, на что ты $gспособен:способна,? Придется постараться получше. Так не пойдет.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Муирн Железный Рог to Player');
INSERT INTO `creature_text` VALUES (92243, 1, 0, 'Come on, smith! KICK! HIT WITH ALL YOUR MIGHT!', 12, 0, 100, 0, 0, 0, 95065, 0, 0, 0, 'Muirn Iron Horn to Player');
INSERT INTO `creature_text` VALUES (92243, 1, 0, 'Ну же, кузнец! БЕЙ! БЕЙ СО ВСЕЙ СИЛЫ!', 12, 0, 100, 0, 0, 0, 95065, 0, 0, 0, 'Муирн Железный Рог to Player');
INSERT INTO `creature_text` VALUES (92243, 2, 0, 'Now hit the hot metal with a hammer with all your might, $p.', 12, 0, 100, 0, 0, 0, 95063, 0, 0, 0, 'Muirn Iron Horn to Player');
INSERT INTO `creature_text` VALUES (92243, 2, 0, 'Теперь бей по раскаленному металлу молотом со всей силы, $p.', 12, 0, 100, 0, 0, 0, 95063, 0, 0, 0, 'Муирн Железный Рог to Player');
INSERT INTO `creature_text` VALUES (92245, 3, 0, 'Неплохо. Ты делаешь успехи.', 12, 0, 100, 1, 0, 0, 94506, 6175, 6175, 0, 'Тала Стальной Тотем');
INSERT INTO `creature_text` VALUES (92245, 3, 0, 'Этот металл довольно неподатлив. Бей сильнее.', 12, 0, 100, 1, 0, 0, 94423, 19527, 19527, 0, 'Тала Стальной Тотем');
INSERT INTO `creature_text` VALUES (92264, 0, 0, 'Придется работать очень быстро. У тебя будет очень мало времени, чтобы выковать из раскаленного докрасна камня силы нужные заготовки.', 12, 0, 100, 0, 0, 0, 95033, 0, 0, 0, 'Кузнец Скверны Нал\'рисса to Player');
INSERT INTO `creature_text` VALUES (92274, 0, 0, 'Мм-м! Очередной мир, в котором нам будет чем полакомиться!', 12, 0, 100, 0, 0, 0, 94384, 0, 0, 0, 'Госпожа боли Селора');
INSERT INTO `creature_text` VALUES (92302, 3, 0, 'Тебя ждет гонец. Да пребудут с тобой штормовые ветра.', 12, 0, 100, 0, 0, 53030, 98438, 112939, 112939, 0, 'Ветир to Player');
INSERT INTO `creature_text` VALUES (92302, 4, 0, 'Шторм очистит эти земли от Скверны!', 12, 0, 100, 0, 0, 53015, 98433, 17115, 17115, 0, 'Ветир to Player');
INSERT INTO `creature_text` VALUES (92302, 4, 0, 'Штормовые ветра сметут недостойных!', 12, 0, 100, 0, 0, 53016, 98434, 44053, 59798, 0, 'Ветир to Player');
INSERT INTO `creature_text` VALUES (92307, 1, 0, 'Думаете, у вас есть шанс только потому, что вы дошли сюда? Вы ничтожные черви!', 14, 0, 100, 0, 0, 61399, 113052, 0, 0, 0, 'Король-бог Сковальд');
INSERT INTO `creature_text` VALUES (92307, 3, 0, 'Эгида будет моя, болваны. Вам не остановить меня.', 14, 0, 100, 0, 0, 61400, 113012, 1614646, 1614646, 0, 'Король-бог Сковальд');
INSERT INTO `creature_text` VALUES (92307, 4, 0, 'Мои войска неисчислимы!', 14, 0, 100, 0, 0, 61397, 113055, 20615, 20615, 0, 'Король-бог Сковальд to Демонические врата');
INSERT INTO `creature_text` VALUES (92307, 4, 0, 'Тысячи миров покорятся мне!', 14, 0, 100, 0, 0, 61404, 113059, 35560, 35560, 0, 'Король-бог Сковальд to Демонические врата');
INSERT INTO `creature_text` VALUES (92307, 4, 0, 'Хватит! Мне надоело!', 14, 0, 100, 0, 0, 61401, 113007, 61090, 61090, 0, 'Король-бог Сковальд');
INSERT INTO `creature_text` VALUES (92318, 0, 0, 'I heard Kilrogg. What happened?', 12, 0, 100, 15, 0, 50313, 94365, 0, 0, 0, 'Ариок to Player');
INSERT INTO `creature_text` VALUES (92330, 4, 0, 'I\'m stronger than ever!', 14, 0, 100, 0, 0, 50846, 95126, 54899, 102400, 0, 'Душа Сокретара');
INSERT INTO `creature_text` VALUES (92330, 4, 0, 'Вы все умрете!', 14, 0, 100, 0, 0, 50850, 95134, 149858, 423877, 0, 'Душа Сокретара');
INSERT INTO `creature_text` VALUES (92330, 4, 0, 'Нет! Пустота...  поглощает…', 14, 0, 100, 0, 0, 50838, 95137, 450277, 450277, 0, 'Душа Сокретара to Player');
INSERT INTO `creature_text` VALUES (92330, 4, 0, 'Ползите прочь, глупцы!', 14, 0, 100, 0, 0, 50849, 95133, 53363, 198214, 0, 'Душа Сокретара');
INSERT INTO `creature_text` VALUES (92330, 4, 0, 'Раздели со мной смерть!', 14, 0, 100, 0, 0, 50848, 95132, 158996, 158996, 183330, 'Душа Сокретара to Player');
INSERT INTO `creature_text` VALUES (92330, 4, 0, 'Этот голем не для тебя! Он МОЙ!', 14, 0, 100, 0, 0, 50836, 95135, 224228, 224228, 183331, 'Душа Сокретара to Ритуальный голем');
INSERT INTO `creature_text` VALUES (92334, 3, 0, '$gГерой:Воительница;! Отомсти за Быстролета!', 12, 0, 100, 0, 0, 0, 95420, 5910, 5910, 0, 'Элиндия Перо Света to Player');
INSERT INTO `creature_text` VALUES (92334, 3, 0, 'О, нет! Что они с тобой сделали?', 12, 0, 100, 0, 0, 0, 95353, 20851, 20851, 0, 'Элиндия Перо Света to Player');
INSERT INTO `creature_text` VALUES (92334, 3, 0, 'Спасибо, $gгерой:воительница;. Без тебя я бы не справилась.', 12, 0, 100, 0, 0, 0, 95433, 38699, 38699, 0, 'Элиндия Перо Света to Player');
INSERT INTO `creature_text` VALUES (92343, 0, 0, 'Шустрее стучите молотками, олухи! План горит!', 14, 0, 100, 0, 0, 0, 94389, 0, 0, 0, 'Капитан Томас');
INSERT INTO `creature_text` VALUES (92346, 0, 0, 'Да, военный совет – хорошая идея. Легион продвигается по Расколотым островам. Нам нельзя терять позиции.', 12, 0, 100, 5, 0, 68367, 114985, 0, 0, 0, 'Леди Лиадрин to Player');
INSERT INTO `creature_text` VALUES (92346, 1, 0, 'Рыцари крови с тобой, $gВерховный лорд:Верховная леди; $p. Азерот не падет!', 12, 0, 100, 5, 0, 0, 98600, 0, 0, 0, 'Леди Лиадрин to Player');
INSERT INTO `creature_text` VALUES (92400, 0, 0, 'Орда рассчитывает на тебя, командир. Найди корабела, и мы окружим Танаан!', 12, 0, 100, 1, 0, 50608, 94467, 0, 0, 0, 'Вол\'джин to Player');
INSERT INTO `creature_text` VALUES (92411, 0, 0, 'Ты смеешь вторгаться в мой лагерь? Я с огромным удовольствием разорву тебя... на мелкие-мелкие кусочки.', 14, 0, 100, 0, 0, 0, 94433, 0, 0, 0, 'Властитель Ма\'грут to Сквернокровный осквернитель');
INSERT INTO `creature_text` VALUES (92414, 3, 0, 'А-а-а! Ох... <выдыхает> так-то лучше...', 12, 0, 100, 0, 0, 0, 94697, 10224, 10224, 0, 'Рыцарь-капитан Родос to Player');
INSERT INTO `creature_text` VALUES (92419, 1, 0, 'Хочешь погостить в моей тюрьме? Сейчас я с тобой разберусь.', 12, 0, 100, 0, 0, 52210, 96913, 0, 0, 0, 'Араксас to Player');
INSERT INTO `creature_text` VALUES (92419, 2, 0, 'Господин, я подвел тебя...', 12, 0, 100, 0, 0, 52198, 97565, 0, 0, 0, 'Араксас to Player');
INSERT INTO `creature_text` VALUES (92423, 0, 0, 'А ты кто такая? Не подходи! Я буду защищать королеву до последнего вздоха!', 12, 0, 100, 0, 0, 0, 96282, 0, 0, 0, 'Териссия to Player');
INSERT INTO `creature_text` VALUES (92438, 0, 0, 'You need my help in the blacksmithing shop? Fine, but every mage has her price... and my price happens to be candy.', 12, 0, 100, 1, 0, 0, 94455, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (92438, 1, 0, 'Consider me hired. See you there!', 12, 0, 100, 1, 0, 0, 94457, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (92445, 0, 0, 'Сейчас ты познаешь истинную силу Скверны!', 12, 0, 100, 0, 0, 55190, 96661, 0, 0, 0, 'Страж Скверны - завоеватель to Player');
INSERT INTO `creature_text` VALUES (92445, 0, 1, 'Охотники на демонов? Как вы сюда попали?', 12, 0, 100, 37, 0, 55187, 94934, 0, 0, 0, 'Страж Скверны - завоеватель to Player');
INSERT INTO `creature_text` VALUES (92447, 0, 0, 'А вот и наш обед пожаловал!', 12, 0, 100, 0, 0, 55203, 96654, 0, 0, 0, 'Порождение Хаоса to Player');
INSERT INTO `creature_text` VALUES (92450, 0, 0, 'Мы очистим ваш мир!', 12, 0, 100, 0, 0, 56970, 105114, 0, 0, 0, 'Эредар - ткач разломов to Player');
INSERT INTO `creature_text` VALUES (92450, 0, 1, 'Отдай нам Приливный камень, |3-6($r).', 12, 0, 100, 0, 0, 56970, 95072, 0, 0, 0, 'Эредар - ткач разломов to 0');
INSERT INTO `creature_text` VALUES (92450, 0, 2, 'Как ты смеешь?!', 12, 0, 100, 0, 0, 56982, 129829, 0, 0, 0, 'Эредар - ткач разломов to Player');
INSERT INTO `creature_text` VALUES (92450, 0, 3, 'Да исполнится воля Легиона!', 12, 0, 100, 0, 0, 56967, 105111, 0, 0, 0, 'Эредар - ткач разломов to Player');
INSERT INTO `creature_text` VALUES (92450, 0, 4, 'Ты нас не остановишь! Нам нет конца.', 12, 0, 100, 0, 0, 56967, 95073, 0, 0, 0, 'Эредар - ткач разломов to 0');
INSERT INTO `creature_text` VALUES (92450, 0, 5, 'Уничтожу!', 12, 0, 100, 0, 0, 0, 94473, 0, 0, 0, 'Эредар - ткач разломов to Player');
INSERT INTO `creature_text` VALUES (92456, 0, 0, 'Whoah! Neat recipe. As long as the herbalists keep bringing me fresh foxflower, I can certainly make this preparation for you. No problem. ', 12, 0, 100, 1, 0, 0, 94508, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (92457, 0, 0, 'I\'d be happy to! Have a look at my stock, $n.', 12, 0, 100, 1, 0, 0, 98779, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (92457, 1, 1, 'Yes, yes... We all know how attached you are to your alchemy table!', 12, 0, 100, 1, 0, 0, 98781, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (92458, 0, 0, 'Patricia! Would you be so kind as to show our friend here the newest recipes we\'ve discovered?', 12, 0, 100, 1, 0, 0, 98778, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (92458, 1, 1, 'Let\'s try the recipe for this \"Draught of Wild Magic\", shall we?', 12, 0, 100, 1, 0, 0, 97202, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (92458, 2, 2, 'I suppose I should dear away this mess and make room for the new insruments.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (92458, 3, 3, 'You should talk to Patricia. I believe she can offer you a new recipe.', 12, 0, 100, 1, 0, 0, 98208, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (92458, 4, 4, 'Nicely done! Patricia has a new recipe for you, $n.', 12, 0, 100, 1, 0, 0, 97051, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (92458, 5, 5, 'Excellent work. Speak to Patricia about a new recipe.', 12, 0, 100, 1, 0, 0, 98207, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (92458, 6, 6, 'Join me over here, $n. I believe we have some of the reagents here on this shelf.', 12, 0, 100, 1, 0, 0, 97203, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (92458, 7, 7, 'We\'ll be fine, Archmage, but... look what that wretched creature did to my alchemy table!', 12, 0, 100, 1, 0, 0, 97209, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (92458, 8, 8, 'Just make sure $G he : she; doesn\'t make a mess on my alchemy table!', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (92458, 9, 9, 'Ah yes, I have one right here. Now be VERY careful with that. Spilling a drop of it can result in, well, unfortunate results.', 12, 0, 0, 0, 0, 0, 104734, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (92460, 0, 0, 'Приветствую тебя, |3-6($c).', 12, 0, 100, 0, 0, 0, 33548, 0, 0, 0, 'Эдвард Эган to Player');
INSERT INTO `creature_text` VALUES (92463, 3, 0, '|TINTERFACE\\ICONS\\TRADE_ENGINEERING.BLP:50|tTime is running out, $n. You are needed at the Grove of Cenarius... we must act swiftly.', 42, 0, 100, 0, 0, 52736, 94504, 122350, 122350, 0, 'Кролик to Player');
INSERT INTO `creature_text` VALUES (92464, 0, 0, 'Of course. Our herbalists bring in plenty of foxflower. I will make sure to set some aside for your metalwork.', 12, 0, 100, 1, 0, 0, 94507, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (92475, 3, 0, '|TINTERFACE\\ICONS\\ACHIEVEMENT_CHARACTER_GNOME_MALE.BLP:40|t К тебе летят нетопыри! Держись, а мы с адмиралом попробуем забраться наверх!', 42, 0, 100, 0, 0, 53059, 94572, 2164, 2164, 0, 'Пульт управления усилителем сигнала to Player');
INSERT INTO `creature_text` VALUES (92489, 0, 0, 'You need your mount shod? Let\'s see if I can make these hoofplates fit...', 12, 0, 100, 1, 0, 0, 94518, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (92489, 1, 0, 'All done! You take care of that mount now. It\'s a beauty.', 12, 0, 100, 1, 0, 0, 94519, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (92539, 0, 0, 'In Haustvald, the Bonespeakers keep the dead. They will not help you.', 12, 0, 100, 0, 0, 0, 98443, 0, 0, 0, 'Javi to Player');
INSERT INTO `creature_text` VALUES (92539, 1, 0, 'Their ways are twisted. They have forgotten the old vows.', 12, 0, 100, 0, 0, 0, 98444, 0, 0, 0, 'Javi to Player');
INSERT INTO `creature_text` VALUES (92539, 2, 0, 'But there is one who remembers. One who is rooted in the old ways.', 12, 0, 100, 0, 0, 0, 98445, 0, 0, 0, 'Javi to Player');
INSERT INTO `creature_text` VALUES (92539, 3, 0, 'Seek Vydhar! Earn your judgment, and pass the Trial of Valor.', 12, 0, 100, 0, 0, 0, 98446, 0, 0, 0, 'Javi to Player');
INSERT INTO `creature_text` VALUES (92539, 4, 0, 'Веками Хелия похищала души павших врайкулов для противостояния валарьярам.', 12, 0, 100, 0, 0, 78317, 123440, 0, 0, 0, 'Хави to Player');
INSERT INTO `creature_text` VALUES (92539, 5, 0, 'Похоже, морская ведьма торопится начать свой поход с захвата Расколотых островов.', 12, 0, 100, 0, 0, 78319, 123494, 0, 0, 0, 'Хави to Player');
INSERT INTO `creature_text` VALUES (92539, 6, 0, 'Ее магия держит в плену Одина и валарьяров. Нам нужны герои, как ты, чтобы сдержать ее.', 12, 0, 100, 0, 0, 78320, 123495, 0, 0, 0, 'Хави to Player');
INSERT INTO `creature_text` VALUES (92539, 7, 0, 'Хелию нужно остановить. Настал час расплаты!', 12, 0, 100, 0, 0, 78321, 123496, 0, 0, 0, 'Хави to Player');
INSERT INTO `creature_text` VALUES (92539, 8, 0, 'Как смеет она бросать мне вызов!', 12, 0, 100, 0, 0, 78318, 123454, 0, 0, 0, 'Хави to Player');
INSERT INTO `creature_text` VALUES (92539, 9, 0, 'Мне надоели игры этой мерзкой колдуньи!', 14, 0, 100, 0, 0, 77637, 123497, 0, 0, 0, 'Хави to Player');
INSERT INTO `creature_text` VALUES (92539, 10, 0, 'Морская ведьма должна ответить за свою дерзость!', 12, 0, 100, 0, 0, 78208, 123498, 0, 0, 0, 'Хави to Player');
INSERT INTO `creature_text` VALUES (92552, 3, 0, 'Туннель должен оставаться открытым! Не отступать!', 14, 0, 100, 0, 0, 0, 94564, 4770084, 4770084, 0, 'Белгорк');
INSERT INTO `creature_text` VALUES (92554, 0, 0, '|TInterface\\Icons\\spell_fire_ragnaros_molteninferno.blp:20|t$n |cFFFF0000|Hspell:185828|h[разжигает]|h|r выжигателя Скверны!', 16, 0, 100, 0, 0, 0, 96139, 0, 0, 0, 'Мек\'бараш to Мек\'бараш');
INSERT INTO `creature_text` VALUES (92554, 1, 0, '|TInterface\\Icons\\spell_fire_ragnaros_molteninferno.blp:20|t$n |cFFFF0000|Hspell:185835|h[воспламеняет]|h|r всех находящихся поблизости выжигателей Скверны!', 16, 0, 100, 0, 0, 0, 96140, 0, 0, 0, 'Мек\'бараш to Мек\'бараш');
INSERT INTO `creature_text` VALUES (92556, 0, 0, 'Конечно, знаю! Ой! Ой-ой!', 14, 0, 100, 0, 0, 52979, 94554, 0, 0, 0, 'Мехмастер Замыкалец to Подчиненный грознокрыл');
INSERT INTO `creature_text` VALUES (92556, 1, 0, 'Так, начинаем плавную посадку... пла-а-вную!', 14, 0, 100, 0, 0, 51720, 94555, 0, 0, 0, 'Мехмастер Замыкалец to Подчиненный грознокрыл');
INSERT INTO `creature_text` VALUES (92557, 0, 0, 'Во что я ввязалась! Ты хоть знаешь, как управлять этим чудищем?', 14, 0, 100, 0, 0, 51716, 94552, 0, 0, 0, 'Небесный адмирал Роджерс to Подчиненный грознокрыл');
INSERT INTO `creature_text` VALUES (92557, 1, 0, 'Берегись! Сюда летит огромный дракон!', 14, 0, 100, 0, 0, 51717, 94553, 0, 0, 0, 'Небесный адмирал Роджерс to Подчиненный грознокрыл');
INSERT INTO `creature_text` VALUES (92561, 0, 0, 'Hey, it\'s you! I\'m getting stir-crazy here - please tell me we\'re going in!', 12, 0, 100, 0, 0, 0, 98419, 0, 0, 0, 'Жуткая наездница Куллен to Player');
INSERT INTO `creature_text` VALUES (92566, 0, 0, 'This way, there\'s a good spot right here.', 12, 0, 100, 0, 0, 0, 98420, 0, 0, 0, 'Жуткая наездница Куллен to Player');
INSERT INTO `creature_text` VALUES (92567, 3, 0, 'Эй, сюда! У меня есть план!', 12, 0, 100, 0, 0, 58281, 98421, 523535, 523535, 0, 'Жуткая наездница Куллен to Player');
INSERT INTO `creature_text` VALUES (92569, 4, 0, 'Это наш шанс! Быстрее наверх!', 12, 0, 100, 0, 0, 58284, 98748, 24387, 24387, 0, 'Жуткая наездница Куллен to Player');
INSERT INTO `creature_text` VALUES (92573, 3, 0, 'У него и дракон есть? Не нравится мне это.', 12, 0, 100, 0, 0, 58282, 98422, 35805, 35805, 0, 'Жуткая наездница Куллен to Player');
INSERT INTO `creature_text` VALUES (92575, 1, 0, 'Катра зил шукил! Ты заплатишь жизнью за свою дерзость!', 12, 0, 100, 0, 0, 0, 93943, 0, 0, 0, 'Саргерайский призыватель to Player');
INSERT INTO `creature_text` VALUES (92591, 1, 0, 'Победа во имя Сильваны!', 12, 0, 100, 0, 0, 0, 94579, 0, 0, 0, 'Грузило to Player');
INSERT INTO `creature_text` VALUES (92591, 3, 0, 'От тебя требовалось заарканить врайкула, а не его несчастную ящерицу.', 12, 0, 100, 0, 0, 0, 94578, 6781, 6781, 0, 'Грузило to Player');
INSERT INTO `creature_text` VALUES (92617, 3, 0, 'Ну вот, как всегда...', 12, 0, 100, 0, 0, 0, 100721, 16704, 16704, 0, 'Житель Браденсбрука to Player');
INSERT INTO `creature_text` VALUES (92618, 0, 0, 'Use that flare gun to mark the birds. That\'ll daze \'em long enough for me to take \'em out!', 12, 0, 100, 603, 0, 52264, 96224, 0, 0, 0, 'Бабушка Марла to Player');
INSERT INTO `creature_text` VALUES (92618, 1, 0, 'Somebody order a bullseye?', 12, 0, 100, 0, 0, 52259, 96171, 0, 0, 0, 'Бабушка Марла to Player');
INSERT INTO `creature_text` VALUES (92618, 2, 0, 'Pow! Right in the kisser!', 12, 0, 100, 0, 0, 52258, 96170, 0, 0, 0, 'Бабушка Марла to Player');
INSERT INTO `creature_text` VALUES (92618, 3, 0, 'Got that one, clear as day.', 12, 0, 100, 0, 0, 52257, 96169, 0, 0, 0, 'Бабушка Марла to Player');
INSERT INTO `creature_text` VALUES (92618, 5, 0, 'Got \'em in my sights!', 12, 0, 100, 0, 0, 52254, 96166, 0, 0, 0, 'Бабушка Марла to Player');
INSERT INTO `creature_text` VALUES (92619, 0, 0, 'Penelope? Thank goodness you\'re all right! I was so worried!', 12, 0, 100, 0, 0, 0, 96218, 0, 0, 0, 'Мэр Хизроу to Player');
INSERT INTO `creature_text` VALUES (92619, 1, 0, 'Lord Shadowsong, Oy was wrong about you. Everyone, put down your arms!', 12, 0, 100, 0, 0, 0, 97357, 0, 0, 0, 'Мэр Хизроу to Player');
INSERT INTO `creature_text` VALUES (92619, 2, 0, '$n, thank you for finding my daughter. Please, you are most welcome here.', 12, 0, 100, 0, 0, 0, 97359, 0, 0, 0, 'Мэр Хизроу to Player');
INSERT INTO `creature_text` VALUES (92620, 0, 0, 'Finish your business here, then meet me in the keep.', 12, 0, 100, 0, 0, 0, 94794, 0, 0, 0, 'Командир Джерод Песнь Теней to Player');
INSERT INTO `creature_text` VALUES (92620, 1, 0, 'Когда закончишь, найди меня в крепости.', 12, 0, 100, 1, 0, 52654, 94794, 0, 0, 0, 'Командир Джерод Песнь Теней to Player');
INSERT INTO `creature_text` VALUES (92621, 3, 0, 'Папа на днях выудил двухголовую рыбу. Такая странная – сиреневого цвета и жутко липкая.', 12, 0, 100, 0, 0, 0, 95591, 2579801, 2579801, 0, 'Проказница из Браденбрука');
INSERT INTO `creature_text` VALUES (92625, 3, 0, 'Тебе хватило дерзости выступить против Легиона?', 12, 0, 100, 15, 0, 0, 94604, 141, 141, 0, 'Страж ужаса – головорез to 90566');
INSERT INTO `creature_text` VALUES (92636, 0, 0, '$gПОПАЛСЯ:ПОПАЛАСЬ;, $n!', 14, 0, 100, 0, 0, 0, 94656, 0, 0, 0, 'Ужас ночи to Player');
INSERT INTO `creature_text` VALUES (92647, 1, 0, 'Наконец-то хоть кто-то развеет мою скуку!', 12, 0, 100, 0, 0, 0, 94625, 0, 0, 0, 'Кузнец Скверны Даморка to Player');
INSERT INTO `creature_text` VALUES (92647, 2, 0, 'Ошибка... в формуле...', 12, 0, 100, 0, 0, 0, 94629, 0, 0, 188486, 'Кузнец Скверны Даморка to Player');
INSERT INTO `creature_text` VALUES (92680, 3, 0, 'Наконец-то прибыла помощь! Хвала Элуне!', 12, 0, 100, 1, 0, 60224, 96419, 7460, 7460, 0, 'Лианис Закат Луны to Player');
INSERT INTO `creature_text` VALUES (92683, 3, 0, 'Да ладно тебе. Малышне вечно не сидится на месте.', 12, 0, 100, 0, 0, 0, 96135, 2858506, 2858506, 0, 'Эммелин to Робин Теккерей');
INSERT INTO `creature_text` VALUES (92683, 3, 0, 'Надеюсь, все они еще живы. Мэр сойдет с ума от горя, если мы не отыщем его дочку.', 12, 0, 100, 0, 0, 0, 96223, 68879, 68879, 0, 'Эммелин to Player');
INSERT INTO `creature_text` VALUES (92686, 3, 0, 'Вот так. И не высовывайтесь на улицу.', 12, 0, 100, 0, 0, 0, 96117, 22852, 22852, 0, 'Робин Теккерей');
INSERT INTO `creature_text` VALUES (92686, 3, 0, 'Хулиганье. Не беспокойся: я за ними присмотрю.', 12, 0, 100, 0, 0, 0, 96120, 18407, 18407, 0, 'Робин Теккерей');
INSERT INTO `creature_text` VALUES (92697, 0, 0, 'Шаладрассил... не может быть... это не может быть правдой!', 12, 0, 100, 18, 0, 0, 96427, 0, 0, 0, 'Изораен Ночная Звезда to Player');
INSERT INTO `creature_text` VALUES (92705, 0, 0, 'I\'ll cover you from the air. Use the power that I gave you.', 12, 0, 100, 1, 0, 52924, 94542, 0, 0, 0, 'Изера to Player');
INSERT INTO `creature_text` VALUES (92718, 0, 0, 'Возьми оружие, $n. Надеюсь, ты еще помнишь, как обращаться с клинками.', 12, 0, 100, 0, 0, 57336, 98731, 0, 0, 0, 'Майев Песнь Теней to Player');
INSERT INTO `creature_text` VALUES (92718, 1, 0, 'I want to stop Gul\'dan, and for this I need your help. Rather free the other illidari!', 12, 0, 100, 0, 0, 57338, 98734, 0, 0, 0, 'Майев Песнь Теней to Player');
INSERT INTO `creature_text` VALUES (92734, 0, 0, 'Thank you, $Gmister:miss;! I was so scared all by myself, but I know a shortcut back to town!', 12, 0, 100, 0, 0, 52781, 94709, 0, 0, 0, 'Пенелопа Хизроу to Player');
INSERT INTO `creature_text` VALUES (92738, 3, 0, '%s жалобно скулит.', 16, 0, 100, 0, 0, 9036, 95677, 74250, 74250, 0, 'Бука');
INSERT INTO `creature_text` VALUES (92748, 0, 0, 'Малфуриона ослепила ярость. У них с Ксавием давние счеты.', 12, 0, 100, 0, 0, 52935, 94738, 0, 0, 0, 'Изера to Player');
INSERT INTO `creature_text` VALUES (92748, 1, 0, 'Но в одиночку ему Ксавия не одолеть. Мы должны его найти.', 12, 0, 100, 0, 0, 52920, 94917, 0, 0, 0, 'Изера to Player');
INSERT INTO `creature_text` VALUES (92763, 0, 0, 'I accept your challenge.', 12, 0, 100, 0, 0, 0, 94732, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (92764, 1, 0, 'Да заберет тебя Эйир!', 12, 0, 100, 0, 0, 51673, 96504, 0, 0, 0, 'Валькира-претендентка to Player');
INSERT INTO `creature_text` VALUES (92764, 1, 0, 'Мой щит не подведет!', 12, 0, 100, 0, 0, 51676, 96507, 0, 0, 0, 'Валькира-претендентка to Sánquin');
INSERT INTO `creature_text` VALUES (92764, 1, 0, 'Сестры! К бою!', 12, 0, 100, 0, 0, 52416, 96509, 0, 0, 0, 'Валь\'кира-претендентка to Player');
INSERT INTO `creature_text` VALUES (92764, 1, 0, 'Ты оскверняешь эти земли!', 12, 0, 100, 0, 0, 51672, 96503, 0, 0, 0, 'Валькира-претендентка to Player');
INSERT INTO `creature_text` VALUES (92764, 2, 0, 'Эйир, дай мне крылья...', 12, 0, 100, 0, 0, 51678, 96510, 0, 0, 0, 'Валькира-претендентка to Player');
INSERT INTO `creature_text` VALUES (92765, 0, 0, 'Свобода! Наконец-то я вырвался из этого проклятого тела!', 12, 0, 100, 4, 0, 0, 94729, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (92765, 1, 0, 'Тебе удалось победить меня в битве, уничтожить мое тело и развеять гнусное проклятие, наложенное на меня мистиками.', 12, 0, 100, 1, 0, 0, 94730, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (92766, 0, 0, 'Акррило получает вызов на Арену!', 41, 0, 100, 0, 0, 0, 94740, 0, 0, 0, 'Акррило to Player');
INSERT INTO `creature_text` VALUES (92767, 1, 0, 'Саргерайский доминатор ограждает Сокретара |cFFFF0000|Hspell:184053|h[\"Барьером Скверны\"]|h|r!', 41, 0, 100, 0, 0, 0, 95354, 0, 0, 184053, 'Саргерайский доминатор');
