INSERT INTO `creature_text` VALUES (92769, 8, 0, 'Свет… сильнее.', 14, 0, 100, 0, 0, 50564, 94801, 0, 0, 0, 'Экзарх Ирель to Архимонд');
INSERT INTO `creature_text` VALUES (92776, 0, 0, 'Вы падете, как до вас пали Стражи!', 12, 0, 100, 0, 0, 57384, 101977, 0, 0, 0, 'Страж Скверны - штурмовик to Дреланим Шелест Ветра');
INSERT INTO `creature_text` VALUES (92776, 1, 0, 'Тебе не остановить Легион!', 12, 0, 100, 0, 0, 57381, 101974, 0, 0, 0, 'Страж Скверны - штурмовик to Освобожденная иллидари');
