INSERT INTO `creature_text` VALUES (92776, 4, 0, 'И это – защитники этого мира? Жалкое зрелище.', 12, 0, 100, 0, 0, 57383, 101976, 0, 0, 0, 'Страж Скверны - штурмовик to Освобожденный иллидари');
