INSERT INTO `creature_text` VALUES (92788, 1, 0, 'Вспомнят ли о тебе ночные эльфы?', 12, 0, 100, 0, 0, 52076, 121982, 0, 0, 0, 'Мракобес - исказитель снов to Храмовая стражница');
