INSERT INTO `creature_text` VALUES (92789, 1, 0, 'You would die for this pathetic land?', 12, 0, 100, 0, 0, 52079, 121985, 0, 0, 0, 'Мракобес-ужасень to Храмовая стражница');
INSERT INTO `creature_text` VALUES (92789, 1, 0, 'Нас не остановить. Мы – Легион.', 12, 0, 100, 0, 0, 52345, 121981, 0, 0, 0, 'Мракобес-ужасень to Player');
