INSERT INTO `creature_text` VALUES (92802, 0, 0, 'At long last... destruction has come to this pathetic world!', 14, 0, 100, 0, 0, 0, 96422, 0, 0, 0, 'Гравакс Осквернитель to Гравакс Осквернитель');
INSERT INTO `creature_text` VALUES (92802, 2, 0, 'Enjoy this moment, mortals... It shall be your last!', 14, 0, 100, 0, 0, 0, 96421, 0, 0, 0, 'Гравакс Осквернитель to Гравакс Осквернитель');
INSERT INTO `creature_text` VALUES (92802, 4, 0, 'Никто не пробудится от Кошмара!', 14, 0, 100, 0, 0, 52270, 96424, 0, 0, 0, 'Гравакс Осквернитель to Гравакс Осквернитель');
INSERT INTO `creature_text` VALUES (92802, 5, 0, 'Tremble beneath the might of the Burning Legion!', 14, 0, 100, 0, 0, 52266, 96420, 0, 0, 0, 'Гравакс Осквернитель to Гравакс Осквернитель');
INSERT INTO `creature_text` VALUES (92802, 6, 0, 'Pray to your pathetic goddess... let her listen to your cries of agony!', 14, 0, 100, 0, 0, 52272, 96426, 0, 0, 0, 'Гравакс Осквернитель to Гравакс Осквернитель');
INSERT INTO `creature_text` VALUES (92802, 7, 0, 'Rend the flesh from their bones... and drain the life from their mortal bodies!', 14, 0, 100, 0, 0, 52271, 96425, 0, 0, 0, 'Гравакс Осквернитель to Гравакс Осквернитель');
INSERT INTO `creature_text` VALUES (92802, 9, 0, 'Yes... struggle... your meager attempts to survive amuse me!', 14, 0, 100, 0, 0, 52269, 96423, 0, 0, 0, 'Гравакс Осквернитель to Гравакс Осквернитель');
INSERT INTO `creature_text` VALUES (92802, 20, 0, 'Bear witness, foolish elves. I will crush your champion.', 12, 0, 100, 0, 0, 52156, 97934, 0, 0, 0, 'Гравакс Осквернитель to Player');
INSERT INTO `creature_text` VALUES (92802, 27, 0, 'None shall awake from the Nightmare!', 14, 0, 100, 0, 0, 0, 96424, 0, 0, 0, 'Гравакс Осквернитель to Гравакс Осквернитель');
INSERT INTO `creature_text` VALUES (92803, 3, 0, 'Темная Госпожа, эти врайкулы оказались противниками куда более грозными, чем мы полагали.', 12, 0, 100, 0, 0, 0, 97225, 10242, 10242, 0, 'Капитан следопытов Арейел to Player');
