INSERT INTO `creature_text` VALUES (92847, 0, 0, 'По нам дали залп! Крепитесь!', 14, 0, 100, 0, 0, 0, 94789, 0, 0, 0, 'Дозорный Сунь-Жо to Невинный кролик');
INSERT INTO `creature_text` VALUES (92850, 3, 0, 'I will stay with the prisoner to ease his last minutes. Find Maiev.', 12, 0, 100, 1, 0, 52651, 97005, 93642, 93642, 0, 'Командир Джерод Песнь Теней to Player');
