INSERT INTO `creature_text` VALUES (92879, 0, 0, 'Сила печати Хранительницы слабеет.', 16, 0, 100, 0, 0, 81788, 132851, 0, 0, 0, 'SLG Generic Stalker (Gigantic AOI) to Player');
INSERT INTO `creature_text` VALUES (92889, 0, 0, 'To walk the high halls is the greatest honor of the valorous.', 12, 0, 100, 1, 0, 52946, 95185, 0, 0, 0, 'Хеймир Черный Кулак to Player');
INSERT INTO `creature_text` VALUES (92889, 1, 0, 'A pity you will never set foot beyond its gates!', 12, 0, 100, 274, 0, 52947, 95188, 0, 0, 0, 'Хеймир Черный Кулак to Player');
INSERT INTO `creature_text` VALUES (92909, 0, 0, 'My sincere apologies for the insistence, $p, but we must speak immediately.', 12, 0, 100, 0, 0, 0, 114359, 0, 0, 0, 'Лорд Максвелл Тиросс to Player');
INSERT INTO `creature_text` VALUES (92909, 1, 0, 'Excellent. Meet me at Krasus\' Landing when you are ready.', 12, 0, 100, 0, 0, 0, 114364, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (92911, 0, 0, 'В атаку!', 14, 0, 100, 0, 0, 50497, 139449, 0, 0, 0, 'Огромный берсерк to Огромный берсерк');
INSERT INTO `creature_text` VALUES (92911, 1, 0, 'Вперед!', 14, 0, 100, 0, 0, 50496, 131943, 0, 0, 0, 'Огромный берсерк to Огромный берсерк');
INSERT INTO `creature_text` VALUES (92911, 3, 0, 'Ой!', 14, 0, 100, 0, 0, 50498, 144842, 0, 0, 0, 'Огромный берсерк to Огромный берсерк');
INSERT INTO `creature_text` VALUES (92911, 9, 0, 'Выдвигаемся!', 14, 0, 100, 0, 0, 50495, 95599, 0, 0, 0, 'Огромный берсерк to Огромный берсерк');
INSERT INTO `creature_text` VALUES (92918, 0, 0, 'What\'s this? An outsider seeks my judgment?', 12, 0, 100, 1, 0, 51660, 95192, 0, 0, 0, 'Райтас Провидец to Player');
INSERT INTO `creature_text` VALUES (92918, 1, 0, 'Insolent worm!  You would disgrace the Halls of Valor with your presence!', 12, 0, 100, 25, 0, 51661, 95193, 0, 0, 0, 'Райтас Провидец to Player');
INSERT INTO `creature_text` VALUES (92920, 0, 0, 'Come then, show me your fury!', 12, 0, 100, 0, 0, 0, 95196, 0, 0, 0, 'Октель Драконья Кровь to Player');
INSERT INTO `creature_text` VALUES (92927, 3, 0, 'Кровь и честь, командир.', 12, 0, 100, 0, 0, 49536, 94882, 119805, 119805, 0, 'Ариок to Player');
INSERT INTO `creature_text` VALUES (92930, 3, 0, 'Прекратить обстрел!', 14, 0, 100, 5, 0, 51760, 94971, 13675, 13675, 0, 'Небесный адмирал Роджерс');
INSERT INTO `creature_text` VALUES (92932, 3, 0, 'Бесполезно. Климатические установки Штормхейма блокируют сигнал.', 12, 0, 100, 22, 0, 51759, 95894, 7635, 7635, 0, 'Мехмастер Замыкалец');
INSERT INTO `creature_text` VALUES (92936, 0, 0, 'Glaciela, I swear, your teeth are going to fall out one day from eating all my delicious candies.', 12, 0, 100, 1, 0, 0, 98999, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (92938, 3, 0, 'Ну что ж, считай, что мы договорились. До скорой встречи!', 12, 0, 100, 0, 0, 0, 94457, 1323, 1323, 0, 'Глациэла Ледохлоп to Player');
INSERT INTO `creature_text` VALUES (92941, 2, 0, 'Я буду преследовать тебя... вечно...', 12, 0, 100, 0, 0, 0, 95158, 0, 0, 165662, 'Горабош to Player');
INSERT INTO `creature_text` VALUES (92941, 3, 0, 'Это священная земля! Готовься к смерти!', 12, 0, 100, 0, 0, 0, 95157, 36422, 36422, 0, 'Горабош to Player');
INSERT INTO `creature_text` VALUES (92950, 3, 0, 'Парашюты внизу.', 12, 0, 100, 0, 0, 0, 95525, 271536, 271536, 0, 'Пехотинец \"Небесного огня\" to Player');
INSERT INTO `creature_text` VALUES (92951, 1, 0, 'Ага! Отлично! Свежий источник. Отвечай, куда подевалась твоя королева банши?', 12, 0, 100, 0, 0, 0, 94854, 0, 0, 0, 'Псарь Элай to Player');
INSERT INTO `creature_text` VALUES (92951, 3, 0, 'Где Сильвана? Если понадобится, мы весь лес прочешем.', 12, 0, 100, 0, 0, 0, 94850, 96050, 96050, 0, 'Псарь Элай');
INSERT INTO `creature_text` VALUES (92951, 3, 0, 'Лучше расскажи мне все, что тебе известно. А то мои псы уже проголодались.', 12, 0, 100, 0, 0, 0, 94849, 22475, 22475, 0, 'Псарь Элай');
INSERT INTO `creature_text` VALUES (92951, 3, 0, 'Ну говори, говори, не стесняйся. Если все расскажешь, я, может быть, сохраню тебе жизнь.', 12, 0, 100, 0, 0, 0, 94856, 4559, 4559, 0, 'Псарь Элай');
INSERT INTO `creature_text` VALUES (92951, 3, 0, 'Просто скажи мне, где сейчас Сильвана, и все закончится быстро.', 12, 0, 100, 0, 0, 0, 94848, 167816, 167816, 0, 'Псарь Элай');
INSERT INTO `creature_text` VALUES (92951, 3, 0, 'Просто скажи, где Сильвана. Не заставляй меня делать тебе больно. Говори, и избавишь нас обоих от множества неудобств.', 12, 0, 100, 0, 0, 0, 94853, 115654, 115654, 0, 'Псарь Элай');
INSERT INTO `creature_text` VALUES (92954, 1, 0, 'Мой клинок жаждет крови – на этот раз твоей.', 12, 0, 100, 0, 0, 60984, 123076, 0, 0, 0, 'Восставший солдат to Player');
INSERT INTO `creature_text` VALUES (92954, 1, 0, 'Я дарую тебе быструю смерть.', 12, 0, 100, 36, 0, 995, 123078, 0, 0, 0, 'Восставший солдат to Player');
INSERT INTO `creature_text` VALUES (92954, 1, 0, 'Я долго к этому готовился!', 12, 0, 100, 0, 0, 60988, 96915, 0, 0, 0, 'Восставший солдат to Player');
INSERT INTO `creature_text` VALUES (92954, 2, 0, 'Лорд Гребень Ворона, я верно служил тебе.', 12, 0, 100, 1, 0, 52241, 96584, 0, 0, 0, 'Восставший солдат to Player');
INSERT INTO `creature_text` VALUES (92954, 2, 0, 'Ты не пройдешь к башне Гребня Ворона.', 12, 0, 100, 1, 0, 52244, 96587, 0, 0, 0, 'Восставший солдат to Player');
INSERT INTO `creature_text` VALUES (92954, 2, 0, 'Хочешь спасти Стражницу? Слишком поздно!', 12, 0, 100, 1, 0, 52240, 96583, 0, 0, 0, 'Восставший солдат to Player');
INSERT INTO `creature_text` VALUES (92957, 4, 0, 'Леденящий устрашитель намертво замораживает орудие!', 42, 0, 100, 0, 0, 0, 95511, 27195, 27195, 0, 'Палубное орудие \"Небесного огня\" to Player');
INSERT INTO `creature_text` VALUES (92963, 1, 0, 'Лейтенант приказал не щадить захватчиков!', 12, 0, 100, 0, 0, 52232, 97007, 0, 0, 0, 'Старлис Крепкий Лук to Player');
INSERT INTO `creature_text` VALUES (92963, 2, 0, 'Лорд Гребень Ворона... я подвела тебя...', 12, 0, 100, 0, 0, 52200, 97567, 0, 0, 0, 'Старлис Крепкий Лук to 35189');
INSERT INTO `creature_text` VALUES (92966, 1, 0, 'Мой молот расколет тебя на части!', 12, 0, 100, 0, 0, 52234, 97009, 0, 0, 0, 'Трелан Раскалыватель Щитов to Player');
INSERT INTO `creature_text` VALUES (92966, 2, 0, 'I underestimated your strength ...', 12, 0, 100, 0, 0, 52199, 97566, 0, 0, 0, 'Трелан Раскалыватель Щитов to Player');
INSERT INTO `creature_text` VALUES (92969, 3, 0, 'Обороняйте стратегическую позицию!', 41, 0, 100, 0, 0, 0, 94852, 164569, 164569, 0, 'Опорный пункт to Player');
INSERT INTO `creature_text` VALUES (92969, 3, 0, 'Победа! Герои смогли потеснить войска Гул\'дана.', 41, 0, 100, 0, 0, 0, 94892, 45859, 45859, 184333, 'Опорный пункт to Player');
INSERT INTO `creature_text` VALUES (92971, 1, 0, 'Мы расставили часовых на стенах! Тебе не уйти, демон!', 12, 0, 100, 0, 0, 52233, 97008, 0, 0, 0, 'Кестер Ищущий Даль to Player');
INSERT INTO `creature_text` VALUES (92971, 2, 0, 'Нужно... поднять... тревогу...', 12, 0, 100, 0, 0, 52201, 97568, 0, 0, 0, 'Кестер Ищущий Даль to Player');
INSERT INTO `creature_text` VALUES (92977, 2, 0, 'Ничто... не остановит... нас...', 14, 0, 100, 0, 0, 0, 94866, 0, 0, 0, 'Железный псарь to Player');
INSERT INTO `creature_text` VALUES (92977, 3, 0, 'Выпускайте зверей!', 14, 0, 100, 22, 0, 0, 94859, 94, 94, 0, 'Железный псарь to Player');
INSERT INTO `creature_text` VALUES (92980, 0, 0, 'Зачем Майев освобождать нас? На это может быть только одна причина...', 12, 0, 100, 0, 0, 57387, 101987, 0, 0, 0, 'Кайн Ярость Солнца to Player');
INSERT INTO `creature_text` VALUES (92980, 1, 0, 'Демоны хотят уничтожить наш мир. Мы должны освободить наших союзников.', 12, 0, 100, 0, 0, 57340, 98738, 0, 0, 0, 'Кайн Ярость Солнца to Player');
INSERT INTO `creature_text` VALUES (92986, 0, 0, 'Легион уже здесь.', 12, 0, 100, 0, 0, 57388, 101988, 0, 0, 0, 'Алтруис Страдалец to Player');
INSERT INTO `creature_text` VALUES (92986, 1, 0, 'Остальным займемся позже. Сейчас нужно убивать демонов.', 12, 0, 100, 0, 0, 57339, 98737, 0, 0, 0, 'Алтруис Страдалец to Player');
INSERT INTO `creature_text` VALUES (92986, 2, 0, 'Бестолочь! Кто же верит Стражам? Больше нас она ненавидит только Легион.', 12, 0, 100, 0, 0, 57389, 101995, 0, 0, 0, 'Алтруис Страдалец to Player');
INSERT INTO `creature_text` VALUES (92987, 0, 0, 'Мои доспехи и оружие...', 12, 0, 100, 0, 0, 0, 97714, 0, 0, 0, 'Майев Песнь Теней to Player');
INSERT INTO `creature_text` VALUES (92987, 1, 0, 'Спасибо тебе, $n! Я и не надеялась, что меня спасут. А где мой брат?', 12, 0, 100, 0, 0, 0, 96997, 0, 0, 0, 'Майев Песнь Теней to Player');
INSERT INTO `creature_text` VALUES (92989, 0, 0, 'Зачем я сопротивлялся зову Ксавия?.. Узри мою силу!', 12, 0, 100, 0, 0, 52146, 97768, 0, 0, 0, 'Варетос');
INSERT INTO `creature_text` VALUES (92989, 2, 0, 'Валь\'шара... погибнет!', 12, 0, 100, 0, 0, 52147, 97769, 0, 0, 0, 'Варетос to Player');
INSERT INTO `creature_text` VALUES (92990, 0, 0, '|cFFFF0000$n|r attacks the enemy without the effect |cFFFF0000|Hspell:199556|h[\"Brutal Attack\"]|h|r!', 42, 0, 100, 0, 0, 0, 104480, 0, 0, 0, 'Молотитель to Player');
INSERT INTO `creature_text` VALUES (92996, 3, 0, 'Обороняйте стратегическую позицию!', 41, 0, 100, 0, 0, 0, 94852, 119879, 119879, 0, 'Опорный пункт to Player');
INSERT INTO `creature_text` VALUES (92996, 3, 0, 'Победа! Герои смогли потеснить войска Гул\'дана.', 41, 0, 100, 0, 0, 0, 94892, 223049, 223049, 184333, 'Опорный пункт to Player');
INSERT INTO `creature_text` VALUES (93001, 2, 0, 'Моя сила ничтожна в сравнении с мощью... Гул\'дана...', 14, 0, 100, 0, 0, 0, 94868, 0, 0, 117526, 'Зирек Порочный to Player');
INSERT INTO `creature_text` VALUES (93001, 3, 0, 'Да... Да... Пусть земля горит у них под ногами!', 14, 0, 100, 11, 0, 0, 94878, 25601, 25601, 0, 'Зирек Порочный to 78116');
INSERT INTO `creature_text` VALUES (93001, 3, 0, 'Еще одна победа за нами. Гул\'дан будет доволен.', 14, 0, 100, 11, 0, 0, 94881, 162, 162, 184868, 'Зирек Порочный to 90482');
INSERT INTO `creature_text` VALUES (93001, 3, 0, 'И все же они стоят насмерть... АРТИЛЛЕРИЯ! По их позициям – прямой наводкой! Превратите их в кровавое месиво!', 14, 0, 100, 5, 0, 0, 94873, 19019, 19019, 0, 'Зирек Порочный to 78116');
INSERT INTO `creature_text` VALUES (93001, 3, 0, 'Ну хорошо. Значит, придется снизойти до вас самому.', 14, 0, 100, 5, 0, 0, 94880, 70548, 70548, 0, 'Зирек Порочный to 78116');
INSERT INTO `creature_text` VALUES (93001, 3, 0, 'Они приближаются! Скверноподданные, атакуйте их. Покажите им вашу новообретенную силу.', 14, 0, 100, 5, 0, 0, 94869, 148, 148, 184868, 'Зирек Порочный to Player');
INSERT INTO `creature_text` VALUES (93001, 3, 0, 'Скверноподданные, повелитель требует от нас решительных действий! Верните то, что по праву принадлежит нам!', 14, 0, 100, 5, 0, 0, 94879, 59912, 59912, 0, 'Зирек Порочный to 78116');
INSERT INTO `creature_text` VALUES (93023, 0, 0, 'Слушайте меня, воины. Наши враги уже у стен великой цитадели.  Будьте наготове.', 14, 0, 100, 0, 0, 50532, 95201, 0, 0, 0, 'Осадный мастер Мар\'так to Player');
INSERT INTO `creature_text` VALUES (93023, 1, 0, 'Эй, ты, проверь укрепления цитадели.', 14, 0, 100, 25, 0, 50536, 95204, 0, 0, 0, 'Осадный мастер Мар\'так to Связанный кровью осквернитель');
INSERT INTO `creature_text` VALUES (93023, 2, 0, 'Что там с машинами?', 14, 0, 100, 25, 0, 50537, 95205, 0, 0, 0, 'Осадный мастер Мар\'так to Связанный кровью осквернитель');
INSERT INTO `creature_text` VALUES (93023, 3, 0, 'Вижу, у нас гости. К оружию!', 14, 0, 100, 0, 0, 50538, 95206, 0, 0, 0, 'Осадный мастер Мар\'так to Связанный кровью осквернитель');
INSERT INTO `creature_text` VALUES (93023, 4, 0, 'Вы решили потягаться с Ордой Скверны? Ко мне, воины! Вышвырнем этих непрошеных гостей!', 14, 0, 100, 0, 0, 50517, 95207, 0, 0, 0, 'Осадный мастер Мар\'так to Player');
INSERT INTO `creature_text` VALUES (93023, 5, 0, 'Отведайте топора!', 14, 0, 100, 0, 0, 50542, 95211, 0, 0, 0, 'Осадный мастер Мар\'так to Player');
INSERT INTO `creature_text` VALUES (93023, 6, 0, 'Я вернусь...', 14, 0, 100, 0, 0, 50519, 95214, 0, 0, 0, 'Осадный мастер Мар\'так to Осадный мастер Мар\'так');
INSERT INTO `creature_text` VALUES (93023, 7, 0, 'Разрушение!', 14, 0, 100, 0, 0, 50529, 95224, 0, 0, 0, 'Осадный мастер Мар\'так to Осадный мастер Мар\'так');
INSERT INTO `creature_text` VALUES (93023, 8, 0, 'Батарея, огонь!', 14, 0, 100, 0, 0, 50732, 95215, 0, 0, 0, 'Осадный мастер Мар\'так to Осадный мастер Мар\'так');
INSERT INTO `creature_text` VALUES (93023, 9, 0, 'Пришлите буравчик!', 14, 0, 100, 0, 0, 50738, 95221, 0, 0, 0, 'Осадный мастер Мар\'так to Осадный мастер Мар\'так');
INSERT INTO `creature_text` VALUES (93023, 10, 0, 'За Гул\'дана!', 14, 0, 100, 0, 0, 50540, 93781, 0, 0, 0, 'Осадный мастер Мар\'так to Связанная кровью чародейка Скверны');
INSERT INTO `creature_text` VALUES (93023, 11, 0, 'Давите их!', 14, 0, 100, 0, 0, 50735, 95218, 0, 0, 0, 'Осадный мастер Мар\'так to Осадный мастер Мар\'так');
INSERT INTO `creature_text` VALUES (93023, 12, 0, 'Смерть приходит ниоткуда… ', 14, 0, 100, 0, 0, 50737, 95220, 0, 0, 0, 'Осадный мастер Мар\'так to Осадный мастер Мар\'так');
INSERT INTO `creature_text` VALUES (93023, 13, 0, 'Будет жарко!', 14, 0, 100, 0, 0, 50733, 95216, 0, 0, 0, 'Осадный мастер Мар\'так to Осадный мастер Мар\'так');
INSERT INTO `creature_text` VALUES (93023, 14, 0, 'Ха! Потопчитесь на их трупах!', 14, 0, 100, 0, 0, 50539, 95208, 0, 0, 0, 'Осадный мастер Мар\'так to Железный драгун');
INSERT INTO `creature_text` VALUES (93023, 15, 0, 'Спалите их дотла!', 14, 0, 100, 0, 0, 50828, 95223, 0, 0, 0, 'Осадный мастер Мар\'так to Осадный мастер Мар\'так');
INSERT INTO `creature_text` VALUES (93023, 16, 0, 'Если хочешь что-то сделать хорошо, сделай это сама…', 14, 0, 100, 0, 0, 50518, 95213, 0, 0, 0, 'Осадный мастер Мар\'так to Осадный мастер Мар\'так');
INSERT INTO `creature_text` VALUES (93023, 17, 0, 'Мы победили!', 14, 0, 100, 0, 0, 50541, 95210, 0, 0, 0, 'Осадный мастер Мар\'так to Осадный мастер Мар\'так');
INSERT INTO `creature_text` VALUES (93023, 18, 0, 'Пушки скоро прибудут.', 14, 0, 100, 0, 0, 50736, 95219, 0, 0, 0, 'Осадный мастер Мар\'так to Осадный мастер Мар\'так');
INSERT INTO `creature_text` VALUES (93029, 0, 0, 'I almost... made it... out...', 12, 0, 100, 0, 0, 0, 96933, 0, 0, 0, 'Ардуен Клинок Души to Player');
INSERT INTO `creature_text` VALUES (93030, 1, 0, 'Насекомые! Я раздавлю вас!', 12, 0, 100, 0, 0, 52148, 97770, 0, 0, 0, 'Железнодрев to Player');
INSERT INTO `creature_text` VALUES (93030, 2, 0, 'Гр-р… Мои листья… опали…', 12, 0, 100, 0, 0, 52149, 97771, 0, 0, 0, 'Железнодрев to Player');
INSERT INTO `creature_text` VALUES (93031, 0, 0, 'Гребень Ворона не сдастся! Мы еще победим!', 12, 0, 100, 0, 0, 52316, 94966, 0, 0, 0, 'Лейтенант Десдел Звездный Глаз to Player');
INSERT INTO `creature_text` VALUES (93065, 2, 0, 'I did not know ... that this is not a dream ...', 12, 0, 100, 0, 0, 52070, 98162, 0, 0, 0, 'Изера to Player');
INSERT INTO `creature_text` VALUES (93065, 4, 0, '|cFFFF0000%s|r begins to cast a spell |cFFFF0000|Hspell:192754|h[\"Дыхание Кошмара\"]|h|r!', 41, 0, 100, 0, 0, 0, 97534, 5441, 81874, 0, 'Изера');
INSERT INTO `creature_text` VALUES (93066, 1, 0, 'I will crush your bones!', 12, 0, 100, 0, 0, 52447, 98072, 0, 0, 0, 'Рунический топороносец из клана Говорящих с костями to Player');
INSERT INTO `creature_text` VALUES (93066, 1, 0, 'Будет сегодня пир воронью!', 12, 0, 100, 0, 0, 52443, 98074, 0, 0, 0, 'Рунический топороносец из клана Говорящих с костями to Player');
INSERT INTO `creature_text` VALUES (93066, 1, 0, 'Твоя кровь украсит меня!', 12, 0, 100, 0, 0, 52447, 98073, 0, 0, 0, 'Рунический топороносец из клана Говорящих с костями to Player');
INSERT INTO `creature_text` VALUES (93066, 2, 0, 'I\'m ... not defeated ...', 12, 0, 100, 0, 0, 52442, 98075, 0, 0, 0, 'Рунический топороносец из клана Говорящих с костями to Player');
INSERT INTO `creature_text` VALUES (93066, 2, 0, 'Смерть меня не успокоит...', 12, 0, 100, 0, 0, 52441, 98076, 0, 0, 0, 'Рунический топороносец из клана Говорящих с костями to Player');
INSERT INTO `creature_text` VALUES (93066, 4, 0, 'Славная... смерть...', 12, 0, 100, 273, 0, 52439, 98077, 11690, 11690, 0, 'Рунический топороносец из клана Говорящих с костями to Player');
INSERT INTO `creature_text` VALUES (93068, 0, 0, 'Я могу столько вам показать…', 14, 0, 100, 0, 0, 50611, 96875, 0, 0, 0, 'Ксул\'горак to 0');
