INSERT INTO `creature_text` VALUES (93428, 3, 0, 'Он использует еще один камень!', 12, 0, 100, 0, 0, 53048, 98466, 59842, 59842, 0, 'Ашильдир to Руновидец Фальяр');
INSERT INTO `creature_text` VALUES (93428, 3, 0, 'Рунный камень придает ему сил! Разбей его!', 12, 0, 100, 0, 0, 53045, 98462, 381628, 381628, 0, 'Ашильдир to Руновидец Фальяр');
INSERT INTO `creature_text` VALUES (93437, 0, 0, 'Готово! Легиону не выстоять против твоей силы!', 12, 0, 100, 0, 0, 68714, 117132, 0, 0, 0, 'Верховный лорд Дарион Могрейн to Player');
INSERT INTO `creature_text` VALUES (93444, 1, 0, 'Это наши драконы!', 12, 0, 100, 0, 0, 61502, 93802, 0, 0, 0, 'Плясун из племени Листа Чертополоха to Player');
INSERT INTO `creature_text` VALUES (93444, 2, 0, 'На этот раз... твоя взяла.', 12, 0, 100, 0, 0, 61493, 93805, 0, 0, 0, 'Плясун из племени Листа Чертополоха to Player');
INSERT INTO `creature_text` VALUES (93444, 2, 0, 'Это... так больно...', 12, 0, 100, 0, 0, 61501, 93806, 0, 0, 0, 'Плясун из племени Листа Чертополоха to Player');
INSERT INTO `creature_text` VALUES (93462, 3, 0, 'Да. |3-2($n) удалось раздобыть его в Нордсколе. Тот самый чародейский клинок, который Дат\'Ремар обратил против Легиона во время Войны древних.', 12, 0, 100, 1, 0, 0, 110910, 107046, 107046, 0, 'Стражница Лорлатила to Player');
INSERT INTO `creature_text` VALUES (93468, 3, 0, 'Пожалуй, мне уже пора. Я передам мистеру Шеклу, что ты скоро подойдешь.', 12, 0, 100, 1, 0, 0, 95170, 317, 317, 0, 'Сеска Морской Клык to Player');
INSERT INTO `creature_text` VALUES (93471, 0, 0, '$ct приближается! Рыцари, салютуйте!', 12, 0, 100, 0, 0, 0, 98505, 0, 0, 0, 'Рыцарь Черного Клинка to Player');
INSERT INTO `creature_text` VALUES (93471, 1, 0, 'Командир!', 12, 0, 100, 0, 0, 0, 95257, 0, 0, 0, 'Рыцарь Черного Клинка to Рыцарь Черного Клинка');
INSERT INTO `creature_text` VALUES (93475, 3, 0, 'Я здесь. Только остерегайся великана!', 12, 0, 100, 5, 0, 0, 95199, 14251, 14251, 0, 'Клив Громохолм to Player');
INSERT INTO `creature_text` VALUES (93482, 0, 0, 'Привет! Ты... ЭТО ТЫ! Я перед тобой в долгу. Но, пожалуй, мне пора уходить.', 12, 0, 100, 25, 0, 0, 95171, 0, 0, 0, 'Клив Громохолм to Player');
INSERT INTO `creature_text` VALUES (93486, 3, 0, 'И вашему кораблю конец!', 14, 0, 100, 0, 0, 51770, 95547, 227643, 227643, 0, 'Жуткий капитан Тэттерсейл');
INSERT INTO `creature_text` VALUES (93486, 3, 0, 'Проклятье! Мы тут как на ладони!', 12, 0, 100, 5, 0, 51740, 97130, 4675, 4675, 0, 'Жуткий капитан Тэттерсейл');
INSERT INTO `creature_text` VALUES (93486, 4, 0, 'Ты умрешь вместе со мной!', 14, 0, 100, 5, 0, 0, 107510, 35086, 35086, 0, 'Жуткий капитан Тэттерсейл');
INSERT INTO `creature_text` VALUES (93487, 3, 0, 'Давай, открой клетку. Добряк Рош не будет тебя крушить.', 12, 0, 100, 15, 0, 0, 95200, 226795, 636958, 0, 'Добряк Рош to Player');
INSERT INTO `creature_text` VALUES (93488, 0, 0, 'Ты нравишься Добряку Рошу, $gмаленький:маленькая:r; |3-6($r). А теперь Добряку Рошу пора уходить.', 12, 0, 100, 11, 0, 0, 95179, 0, 0, 0, 'Добряк Рош to Player');
INSERT INTO `creature_text` VALUES (93491, 3, 0, '$gОдин из учеников:Одна из учениц; Амал\'тазада? Пф! Ну ладно. Забирай свое золото.', 12, 0, 100, 603, 0, 0, 97345, 60767, 60767, 0, 'Лорд Торваль to Player');
INSERT INTO `creature_text` VALUES (93492, 3, 0, 'Эй! ЭЙ, $gгосподин:госпожа;! Прошу вас, вытащите меня отсюда.', 12, 0, 100, 3, 0, 164, 95198, 53550, 53550, 0, 'Грейс Зеленая to Player');
INSERT INTO `creature_text` VALUES (93495, 0, 0, 'I’ve grumbled here, it seems, for ages. Thank you, |3-6($c). Now I have to go!', 12, 0, 100, 4, 0, 0, 95184, 0, 0, 0, 'Грейс Зеленая to Player');
INSERT INTO `creature_text` VALUES (93508, 0, 0, 'Будем надеяться, что маги не попали в услужение Легиону. Нам нужна их помощь.', 12, 0, 100, 1, 0, 0, 110911, 0, 0, 0, 'Друид Лорлатила to Player');
INSERT INTO `creature_text` VALUES (93508, 3, 0, 'Да это же... Фело\'мелорн? Огненный столб!', 12, 0, 100, 6, 0, 0, 110909, 11161, 11161, 0, 'Друид Лорлатила to Player');
INSERT INTO `creature_text` VALUES (93509, 0, 0, 'Смотрите, это $n! Я же говорила, что выберут именно $Gего:ее;! Гони монету!', 12, 0, 100, 0, 0, 0, 97343, 0, 0, 0, 'Леди Алистра to Player');
INSERT INTO `creature_text` VALUES (93517, 3, 0, 'Мы на территории врага. Меняем план патрулирования: в воздухе все время должна находиться хотя бы одна группа. Нам не нужны сюрпризы вроде того, что случился на Расколотом берегу.', 12, 0, 100, 0, 0, 0, 95248, 3767, 3767, 0, 'Властелин ужаса Таланор to Рыцарь Черного Клинка');
INSERT INTO `creature_text` VALUES (93517, 3, 0, 'Отправляйтесь к леди Алистре и получите свои приказы. Да продлятся ваши страдания, рыцари.', 12, 0, 100, 0, 0, 0, 95255, 3948, 3948, 0, 'Властелин ужаса Таланор to Рыцарь Черного Клинка');
INSERT INTO `creature_text` VALUES (93520, 0, 0, 'I will start repairing it with improvised means.', 12, 0, 100, 1, 0, 0, 105602, 0, 0, 0, 'Диди Гаечный Ключ to Player');
INSERT INTO `creature_text` VALUES (93520, 1, 0, 'Эффектно, но неэффективно. Нужен другой подход. Возможно, получится использовать какую-нибудь химическую реакцию.', 12, 0, 100, 1, 0, 0, 104805, 0, 0, 0, 'Диди Гаечный Ключ to Player');
INSERT INTO `creature_text` VALUES (93520, 2, 0, 'Сходи в лавку алхимика и спроси Декуса, нет ли у него какого-нибудь мощного зелья, которое может подойти для наших целей.', 12, 0, 100, 1, 0, 0, 104806, 0, 0, 0, 'Диди Гаечный Ключ to Player');
INSERT INTO `creature_text` VALUES (93520, 3, 0, 'Так, теперь я вылью это в раствор. Осторожнее – реакция может быть бурной.', 12, 0, 100, 0, 0, 0, 104807, 0, 0, 0, 'Диди Гаечный Ключ to Player');
INSERT INTO `creature_text` VALUES (93520, 4, 0, 'Ты в порядке? *Кхе-кхе* Мда, похоже, тут сработает только заряженный Скверной сердечник.', 12, 0, 100, 1, 0, 0, 104808, 0, 0, 0, 'Диди Гаечный Ключ to Player');
INSERT INTO `creature_text` VALUES (93520, 5, 0, 'Конечно, шансы невелики, но вдруг он найдется у кого-нибудь здесь в Даларане? Попробуй поспрашивать Костолюта в Приюте фокусника – может, ему попадался такой.', 12, 0, 100, 1, 0, 0, 104809, 0, 0, 0, 'Диди Гаечный Ключ to Player');
INSERT INTO `creature_text` VALUES (93520, 6, 0, 'Удалось?! Невероятно! Ты просто лучше всех, $n! Ну что, пожелай мне удачи – буду устанавливать.', 12, 0, 100, 0, 0, 0, 104810, 0, 0, 0, 'Диди Гаечный Ключ to Player');
INSERT INTO `creature_text` VALUES (93520, 7, 0, 'Получилось!', 12, 0, 100, 4, 0, 0, 122294, 0, 0, 0, 'Диди Гаечный Ключ to Player');
INSERT INTO `creature_text` VALUES (93520, 8, 0, 'Of course. There they are, lying on the table. I don\'t have any complaints about them yet!', 12, 0, 100, 1, 0, 0, 102675, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (93520, 8, 0, 'Конечно. Вон они, на столе лежат. Пока у меня к ним нет никаких претензий!', 12, 0, 100, 1, 0, 0, 102675, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (93521, 0, 0, 'Well Met!', 12, 0, 100, 0, 0, 0, 53806, 0, 0, 0, 'Ранид Златоплав to Player');
INSERT INTO `creature_text` VALUES (93523, 1, 0, 'Use my tanning rack and workbench to craft everything. You\'ll find a solution for tanning on my bench as well.', 12, 0, 0, 0, 0, 0, 101591, 0, 0, 0, 'Намха Лунная Вода <Учительница кожевничества>');
INSERT INTO `creature_text` VALUES (93524, 1, 0, 'I just got a fresh shipment of Runic Catgut, $n. If your supply is low, you can buy some from me.', 12, 0, 0, 0, 0, 0, 98234, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (93524, 2, 0, 'Oh. $n. I have some Spritethorns for sale if you need any for your bag recipe.', 12, 0, 0, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (93524, 3, 0, 'A summon? Sure! Where did you say he was?', 12, 0, 0, 0, 0, 0, 95692, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (93524, 4, 0, 'Hold on a sec... there! I\'ve got him!', 12, 0, 0, 0, 0, 0, 95693, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (93526, 0, 0, 'Приветствую.', 12, 0, 100, 3, 0, 0, 145038, 0, 0, 0, 'Тиффани Картье to Player');
INSERT INTO `creature_text` VALUES (93526, 0, 1, 'Well Met!', 12, 0, 100, 3, 0, 0, 53806, 0, 0, 0, 'Тиффани Картье to Player');
INSERT INTO `creature_text` VALUES (93526, 3, 0, 'Проще простого...', 12, 0, 0, 0, 0, 0, 95704, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (93526, 4, 0, 'Готово! И передай Танитрии, что за ней теперь еще одна шелковая подушка.', 12, 0, 0, 0, 0, 0, 95703, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (93526, 5, 5, 'I suppose I can part with one. But tell Didi I\'m sending her a bill for it. We\'re not running a charity here.', 12, 0, 0, 0, 0, 0, 104692, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (93526, 6, 0, 'I think we should start with Catriona McRae. She was my last client.', 12, 0, 100, 1, 0, 0, 102663, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (93526, 6, 0, 'Думаю, начать стоит с Катрионы Макрей. Она была моей последней клиенткой.', 12, 0, 100, 1, 0, 0, 102663, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (93526, 8, 0, 'I don\'t currently have any, no. But I hear they have all sorts of items in the Underbelly.', 12, 0, 100, 1, 0, 0, 103344, 0, 0, 0, 'Тиффани Картье to Player');
INSERT INTO `creature_text` VALUES (93526, 9, 0, ' I myself would never dream of going down there, but I assume you\'d have no problem given your primary career |3-1($c)', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Tiffany Cartier to Player');
INSERT INTO `creature_text` VALUES (93526, 10, 0, '$n,  I thought I asked you to tell your friend to STOP sending those letters of his?!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Тиффани Картье to Player');
INSERT INTO `creature_text` VALUES (93526, 11, 0, 'WHAT THE HELL IS THAT THING?!', 14, 0, 100, 22, 0, 0, 103964, 0, 0, 0, 'Tiffany Cartier to Player');
