INSERT INTO `creature_text` VALUES (93527, 0, 0, 'Ladies, ladies, calm down. I think this is the jeweler who works with $n.', 12, 0, 100, 1, 0, 0, 103965, 0, 0, 0, 'Тимоти Джонс to Player');
INSERT INTO `creature_text` VALUES (93527, 0, 0, 'Дамы, дамы, успокойтесь. Кажется, это и есть ювелир, с которым работает $n.', 12, 0, 100, 1, 0, 0, 103965, 0, 0, 0, 'Тимоти Джонс to Player');
INSERT INTO `creature_text` VALUES (93529, 0, 0, 'Увы, с пророком вы разминулись.', 12, 0, 100, 0, 0, 0, 125624, 0, 0, 0, 'Олисарра Добрая to player');
