INSERT INTO `creature_text` VALUES (93612, 1, 0, 'Ты разделишь нашу судьбу!', 12, 0, 100, 0, 0, 52558, 98260, 0, 0, 0, 'Страж смерти лагеря Дредвейка to Player');
INSERT INTO `creature_text` VALUES (93618, 0, 0, 'Просто так тебе не захватить этот мир, демон. Его обитатели не сдадутся без боя. Наша воля несокрушима! За Дренор!', 14, 0, 100, 0, 0, 50803, 96889, 0, 0, 0, 'Громмаш Адский Крик to Громмаш Адский Крик');
INSERT INTO `creature_text` VALUES (93618, 2, 0, 'Лок\'тар огар! Мы тесним их! К порталу! С Гул\'даном я разберусь сам!', 14, 0, 100, 0, 0, 50804, 96890, 0, 0, 0, 'Громмаш Адский Крик to Громмаш Адский Крик');
INSERT INTO `creature_text` VALUES (93620, 0, 0, 'I\'m sorry $n, but I cannot simply give you one of my scales. In this time of need, we must keep all of our strength.', 12, 0, 0, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (93622, 2, 0, 'Я возвращаюсь в Круговерть Пустоты...', 12, 0, 100, 0, 0, 52748, 95303, 0, 0, 0, 'Мортиферос to Player');
INSERT INTO `creature_text` VALUES (93622, 3, 0, 'Вам и вашему миру конец!', 14, 0, 100, 15, 0, 52749, 95298, 5012, 5012, 0, 'Мортиферос');
INSERT INTO `creature_text` VALUES (93622, 3, 0, 'Вас не спасут никакие артефакты. Они бессильны против мощи Пылающего Легиона.', 14, 0, 100, 1, 0, 52750, 95300, 4122940, 4122940, 0, 'Мортиферос');
INSERT INTO `creature_text` VALUES (93622, 3, 0, 'Ваши боги-титаны покинули вас. Сложите оружие. Все кончено.', 14, 0, 100, 25, 0, 52754, 95299, 41449, 41449, 0, 'Мортиферос');
INSERT INTO `creature_text` VALUES (93622, 3, 0, 'Вперед! Убейте иллидари! Захватите Азсуну!', 14, 0, 100, 25, 0, 52751, 95294, 202604, 202604, 0, 'Мортиферос');
INSERT INTO `creature_text` VALUES (93622, 3, 0, 'Вы опоздали, \"герои\". Этот мир наш.', 14, 0, 100, 25, 0, 51890, 95296, 577664, 577664, 0, 'Мортиферос');
INSERT INTO `creature_text` VALUES (93622, 3, 0, 'Выжгите землю. Вскипятите моря. Убейте всех!', 14, 0, 100, 1, 0, 52747, 95301, 376585, 376585, 0, 'Мортиферос');
INSERT INTO `creature_text` VALUES (93622, 3, 0, 'Мы – Легион! Нас не победить!', 14, 0, 100, 15, 0, 51889, 95295, 22643, 22643, 0, 'Мортиферос');
INSERT INTO `creature_text` VALUES (93622, 3, 0, 'На колени! Молите о пощаде!', 14, 0, 100, 25, 0, 52752, 112555, 339857, 339857, 0, 'Мортиферос');
INSERT INTO `creature_text` VALUES (93624, 3, 0, 'Тсс! Эй, $n, сюда!', 12, 0, 100, 603, 0, 69881, 115653, 6710113, 6710113, 0, 'Жуткая наездница Куллен to Player');
INSERT INTO `creature_text` VALUES (93628, 0, 0, 'Хорошо. Спрашивай.', 12, 0, 100, 1, 0, 81762, 125720, 0, 0, 0, 'Эйир to Player');
INSERT INTO `creature_text` VALUES (93628, 1, 0, 'Ох, дитя... не всякая истина – благо. Ты правда хочешь знать?', 12, 0, 100, 1, 0, 81763, 125722, 0, 0, 0, 'Эйир to Player');
INSERT INTO `creature_text` VALUES (93628, 2, 0, 'Будь по-твоему. Семью Сковальда приказал истребить сам Один.', 12, 0, 100, 1, 0, 81764, 125724, 0, 0, 0, 'Эйир to Player');
INSERT INTO `creature_text` VALUES (93628, 3, 0, 'Один боялся, что наследники Короля-бога предадут Волнскорн в руки Легиона.', 12, 0, 100, 1, 0, 81765, 125726, 0, 0, 0, 'Эйир to Player');
INSERT INTO `creature_text` VALUES (93628, 4, 0, 'Потому он повелел истребить их всех до единого.', 12, 0, 100, 1, 0, 81766, 125727, 0, 0, 0, 'Эйир to Player');
INSERT INTO `creature_text` VALUES (93628, 5, 0, 'Сигрин! Нет! Не позволяй Тьме поглотить тебя!', 14, 0, 100, 1, 0, 81767, 125729, 0, 0, 0, 'Эйир to Player');
INSERT INTO `creature_text` VALUES (93628, 6, 0, 'Сигрин... что ты наделала?', 12, 0, 100, 1, 0, 81768, 125732, 0, 0, 0, 'Эйир to Player');
INSERT INTO `creature_text` VALUES (93628, 7, 0, 'Итак, наследница Сковальда вернулась к валькирам. Знаешь, что это значит, дитя?', 12, 0, 100, 1, 0, 81757, 125675, 0, 0, 0, 'Эйир to Player');
INSERT INTO `creature_text` VALUES (93628, 8, 0, 'Так и есть. Я давно предвидела, что валькиры сплотят клан Волнскорн против грозного врага, и возглавит его дочь девы щита и Короля-бога.', 12, 0, 100, 0, 0, 81758, 125677, 0, 0, 0, 'Эйир to Player');
INSERT INTO `creature_text` VALUES (93628, 9, 0, 'Ты – дочь Сковальда и его наследница. Все, кто оспаривает твое право на трон, должны склониться перед тобой.', 12, 0, 100, 0, 0, 81759, 125678, 0, 0, 0, 'Эйир to Player');
INSERT INTO `creature_text` VALUES (93628, 10, 0, 'Если дрогнешь, твой клан расколется, и ничто не помешает Легиону взять Штормхейм.', 12, 0, 100, 0, 0, 81760, 125679, 0, 0, 0, 'Эйир to Player');
INSERT INTO `creature_text` VALUES (93628, 11, 0, 'Знаю, это тяжкое бремя, но ты – наша единственная надежда.', 12, 0, 100, 0, 0, 81761, 125680, 0, 0, 0, 'Эйир to Player');
INSERT INTO `creature_text` VALUES (93640, 0, 0, 'Thank you, traveler. All is not yet lost.', 12, 0, 100, 449, 0, 52279, 95323, 0, 0, 0, 'Эрвинд Туманная Топь to Player');
INSERT INTO `creature_text` VALUES (93644, 0, 0, 'Elothir be with you, friend of the grove.', 12, 0, 100, 449, 0, 52280, 95324, 0, 0, 0, 'Дель\'танар Мудрость Лесов to Player');
INSERT INTO `creature_text` VALUES (93654, 0, 0, 'Твой драгоценный Быстролет мертв! Тебе удалось выжить при падении, ночная эльфийка, но больше судьба не будет к тебе столь благосклонна.', 12, 0, 100, 0, 0, 0, 95419, 0, 0, 0, 'Skul\'vrax to Player');
INSERT INTO `creature_text` VALUES (93677, 3, 0, 'Победа! Вы спасли медвежонка.$b$b%s трогает вас лапой, пытаясь привлечь к себе внимание.', 41, 0, 100, 0, 0, 0, 98193, 113127, 113127, 0, 'Дрожащий пеплобрюхий медвежонок to Джиникки Прокалыватель');
INSERT INTO `creature_text` VALUES (93680, 3, 0, 'Стражи обнюхивают вас и негромко рычат.', 16, 0, 100, 0, 0, 0, 122528, 4022, 4022, 0, 'Друид-медведь to Player');
INSERT INTO `creature_text` VALUES (93684, 2, 0, 'Всего-то хотелось пошалить. Шалость... не удалась...', 12, 0, 100, 0, 0, 0, 95393, 0, 0, 0, 'Зловредный лесной дух to Player');
INSERT INTO `creature_text` VALUES (93686, 2, 0, 'Мы не отдадим вам свои леса!', 12, 0, 100, 0, 0, 0, 95391, 0, 0, 0, 'Джиникки Прокалыватель to Player');
INSERT INTO `creature_text` VALUES (93693, 0, 0, 'A fine task for the Coilskar. I am in agreement.', 12, 0, 100, 396, 0, 55295, 98553, 0, 0, 0, 'Леди С\'тэно to Player');
INSERT INTO `creature_text` VALUES (93693, 1, 0, 'Давайте покончим с этим поскорее. Вулкан – не место для наг.', 12, 0, 100, 0, 0, 0, 98554, 0, 0, 0, 'Леди С\'тэно to Player');
INSERT INTO `creature_text` VALUES (93704, 2, 0, 'Больно!', 12, 0, 100, 0, 0, 69129, 128126, 0, 0, 0, 'Охотник за головами из племени Черного Копья to Командир повелителей Скверны Азгалор');
INSERT INTO `creature_text` VALUES (93708, 2, 0, 'Мать-Земля... укажи мне путь...', 12, 0, 100, 0, 0, 70933, 99382, 0, 0, 0, 'Храбрец Громового Утеса to Мо\'арг - вестник боли');
INSERT INTO `creature_text` VALUES (93716, 1, 0, 'Смерть врагам Легиона!', 12, 0, 100, 0, 0, 55146, 96647, 0, 0, 0, 'Губитель Рока to Player');
INSERT INTO `creature_text` VALUES (93716, 1, 0, 'Тебе здесь не место!', 12, 0, 100, 0, 0, 55148, 134549, 0, 0, 0, 'Губитель Рока to Player');
INSERT INTO `creature_text` VALUES (93758, 1, 0, 'Никто ни за что не догадается поискать за посудой.', 12, 0, 100, 0, 0, 0, 95473, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (93759, 0, 0, 'I sense greater power within you, $n. Have you stolen a demon\'s essence?', 12, 0, 100, 0, 0, 55229, 96428, 0, 0, 0, 'Джейс Темный Ткач to Player');
INSERT INTO `creature_text` VALUES (93759, 1, 0, 'Use the crucible to complete the ritual.', 12, 0, 100, 0, 0, 55242, 96680, 0, 0, 0, 'Джейс Темный Ткач to Player');
INSERT INTO `creature_text` VALUES (93773, 0, 0, 'Did Eitrigg send you? I must thank him for his concern, but it is unnecessary.', 12, 0, 100, 0, 0, 0, 114651, 0, 0, 0, 'Верховный правитель Саурфанг to Player');
INSERT INTO `creature_text` VALUES (93773, 1, 0, 'Death is the likely end there, you know this. I can see in your eyes that I cannot talk you out of this, perhaps you have honor to regain there as well.', 12, 0, 100, 0, 0, 0, 114668, 0, 0, 0, 'Верховный правитель Саурфанг to Player');
INSERT INTO `creature_text` VALUES (93775, 0, 0, 'My thanks, champion. I will seek out Thrall and see if he can help.', 12, 0, 100, 0, 0, 0, 114649, 0, 0, 0, 'Эйтригг to Player');
INSERT INTO `creature_text` VALUES (93802, 0, 0, 'None can resist my kiss.', 14, 0, 100, 0, 0, 55068, 103514, 0, 0, 0, 'Королева стаи Тиранна to 0');
INSERT INTO `creature_text` VALUES (93802, 1, 0, 'Come forth, my children. Feast upon our enemies.', 14, 0, 100, 0, 0, 55069, 103517, 0, 0, 0, 'Королева стаи Тиранна to 0');
INSERT INTO `creature_text` VALUES (93802, 2, 0, '|TInterface\\Icons\\inv_misc_monsterspidercarapace_01:20|t%s использует способность |cFFFF0000|Hspell:197414|h[\"Уход в тень\"]|h|r!', 41, 0, 100, 0, 0, 0, 102140, 0, 0, 0, 'Королева стаи Тиранна to 0');
INSERT INTO `creature_text` VALUES (93802, 3, 0, 'My Lord Sargeras, no!!!', 14, 0, 100, 0, 0, 55055, 99012, 0, 0, 0, 'Королева стаи Тиранна to Player');
INSERT INTO `creature_text` VALUES (93805, 0, 0, 'Ah, a champion arrives in our most dire hour. Welcome.', 12, 0, 100, 0, 0, 0, 96688, 0, 0, 0, 'Духостранник Смоляной Рог to Player');
INSERT INTO `creature_text` VALUES (93822, 3, 0, 'Приветствую, командир! Нужно еще столько всего сделать, прежде чем на пристани закипит работа!', 12, 0, 100, 22, 0, 0, 96199, 91433, 91433, 0, 'Меррек Вондер to Player');
INSERT INTO `creature_text` VALUES (93822, 3, 0, 'Сюда, командир! Пора приступить к формированию флота!', 12, 0, 100, 22, 0, 0, 96203, 203459, 203459, 0, 'Меррек Вондер to Player');
INSERT INTO `creature_text` VALUES (93823, 0, 0, 'Welcome to the Devils of Valor, where brave men live forever!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Danica the Rebirthright to Player');
INSERT INTO `creature_text` VALUES (93823, 0, 0, 'Добро пожаловать в Чертоги Доблести, где смельчаки живут вечно!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Даника Возродительница to Player');
INSERT INTO `creature_text` VALUES (93823, 1, 0, 'Behind you is the Eye of Odin, which sees all of Azeroth! And that way is the blacksmith, where Helgar, the greatest blacksmith, smokes the most powerful weapon for the Valariars.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Danica the Rebirthright to Player');
INSERT INTO `creature_text` VALUES (93823, 1, 0, 'За тобой находится Глаз Одина, который видит весь Азерот! А в той стороне – кузня, где Хелгар, величайший кузнец, кует самое мощное оружие для валарьяров.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Даника Возродительница to Player');
INSERT INTO `creature_text` VALUES (93823, 2, 0, 'Across the way lies the Arena of Glory, where warriors vie for supremacy in epic combat. Before us lies the Great Mead Hall where drink and stories flow freely!', 12, 0, 100, 0, 0, 0, 105450, 0, 0, 0, 'Danica the Rebirthright to Player');
INSERT INTO `creature_text` VALUES (93823, 3, 0, 'Theres one in front. I will go and announce your arrival. Do not forget respect.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Danica the Rebirthright to Player');
INSERT INTO `creature_text` VALUES (93823, 3, 0, 'Один ждет впереди. Я пойду и объявлю о твоем прибытии. Не забывай об уважении.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Даника Возродительница to Player');
INSERT INTO `creature_text` VALUES (93826, 0, 0, 'I am Mayla, daughter of Ulan, and I... I stand as Chieftain of the Highmountain Tribes.', 12, 0, 100, 0, 0, 0, 109202, 0, 0, 0, 'Майла Крутогор to Player');
INSERT INTO `creature_text` VALUES (93826, 1, 0, 'Кристаллические твари наступают! Держите оборону!', 14, 0, 100, 0, 0, 0, 105178, 0, 0, 0, 'Майла Крутогор to Заклинатель земли из племени Серого Камня');
INSERT INTO `creature_text` VALUES (93833, 3, 0, 'В теснине Бурлящей Воды нуждаются в нашей помощи.', 12, 0, 100, 0, 0, 54760, 102688, 1311, 1311, 0, 'Джейл Речная Грива to Player');
INSERT INTO `creature_text` VALUES (93836, 0, 0, 'He went easy on you, outsider. Come, speak.', 14, 0, 100, 0, 0, 0, 99570, 0, 0, 0, 'Торок Кровавый Тотем to Player');
INSERT INTO `creature_text` VALUES (93836, 1, 0, 'Does such weakness pass for strength where you come from, $n?', 12, 0, 100, 0, 0, 0, 101734, 0, 0, 0, 'Торок Кровавый Тотем to Player');
INSERT INTO `creature_text` VALUES (93841, 3, 0, 'Небесный Рог! Небо ждет! Покажем этим гарпиям и рухам, как надо сражаться!', 14, 0, 100, 15, 0, 54294, 100323, 750892, 750892, 0, 'Ласан Небесный Рог to Player');
INSERT INTO `creature_text` VALUES (93841, 3, 0, 'Приветствую тебя, владелец Хищного Когтя.', 12, 0, 100, 66, 0, 0, 121326, 338037, 338037, 0, 'Ласан Небесный Рог to Player');
INSERT INTO `creature_text` VALUES (93846, 0, 0, 'When you face the worms, call to us, and we shall be there.', 12, 0, 100, 0, 0, 54897, 102252, 0, 0, 0, 'Майла Крутогор to Player');
INSERT INTO `creature_text` VALUES (93846, 1, 0, 'The Underking is not getting away. Not this time.', 12, 0, 100, 0, 0, 62098, 112815, 0, 0, 0, 'Майла Крутогор to Player');
INSERT INTO `creature_text` VALUES (93858, 0, 0, 'Жду приказаний!', 14, 0, 100, 0, 0, 50499, 95603, 0, 0, 0, 'Огромный берсерк to Осадный мастер Мар\'так');
INSERT INTO `creature_text` VALUES (93858, 1, 0, 'В атаку!', 14, 0, 100, 0, 0, 50497, 139449, 0, 0, 0, 'Огромный берсерк to Осадный мастер Мар\'так');
INSERT INTO `creature_text` VALUES (93860, 1, 0, 'I will tear you to pieces!', 12, 0, 100, 0, 0, 51687, 98173, 0, 0, 0, 'Секретный агент с заставы Седогрива to Player');
INSERT INTO `creature_text` VALUES (93860, 1, 0, 'За Альянс!', 12, 0, 100, 0, 0, 51685, 146212, 0, 0, 0, 'Секретный агент с заставы Седогрива to Player');
INSERT INTO `creature_text` VALUES (93860, 1, 0, 'За Гилнеас!', 12, 0, 100, 0, 0, 51684, 106378, 0, 0, 0, 'Секретный агент с заставы Седогрива to Player');
INSERT INTO `creature_text` VALUES (93860, 1, 0, 'Орда заплатит за предательство!', 12, 0, 100, 0, 0, 51686, 98174, 0, 0, 0, 'Секретный агент с заставы Седогрива to Player');
INSERT INTO `creature_text` VALUES (93890, 0, 0, 'Прошу тебя, отыщи моего племянника, пока он не обезумел, как остальные!', 12, 0, 100, 0, 0, 0, 95512, 0, 0, 0, 'Старейшина Сух to Player');
INSERT INTO `creature_text` VALUES (93892, 3, 0, 'Я возвращаюсь к дядюшке. Спасибо тебе, $gдобрый незнакомец:добрая незнакомка;!', 12, 0, 100, 53, 0, 0, 95517, 1528, 1528, 0, 'Редкомех to Player');
INSERT INTO `creature_text` VALUES (93913, 0, 0, '|TInterface\\Icons\\spell_fire_ragnaros_molteninferno.blp:20|t$n |cFFFF0000|Hspell:185824|h[взрывается]|h|r в момент гибели!', 16, 0, 100, 0, 0, 0, 96142, 0, 0, 0, 'Выжигатель Скверны to Выжигатель Скверны');
INSERT INTO `creature_text` VALUES (93926, 0, 0, 'Глупцы! Здесь вас ждет лишь смерть! Черные Врата готовы!', 14, 0, 100, 0, 0, 0, 95882, 0, 0, 0, 'Гул\'дан to Player');
INSERT INTO `creature_text` VALUES (93926, 1, 0, 'Мощь легиона сокрушит вас!', 14, 0, 100, 1, 0, 50945, 95883, 0, 0, 0, 'Гул\'дан to Player');
INSERT INTO `creature_text` VALUES (93926, 2, 0, 'Да, повелитель.', 14, 0, 100, 1, 0, 50946, 95884, 0, 0, 0, 'Гул\'дан to Player');
INSERT INTO `creature_text` VALUES (93931, 0, 0, 'Я готова!', 14, 0, 100, 0, 0, 50830, 95610, 0, 0, 0, 'Связанная кровью чародейка Скверны to Осадный мастер Мар\'так');
INSERT INTO `creature_text` VALUES (93931, 1, 0, 'Выдвигаемся!', 14, 0, 100, 0, 0, 50825, 95599, 0, 0, 0, 'Связанная кровью чародейка Скверны to Осадный мастер Мар\'так');
INSERT INTO `creature_text` VALUES (93940, 3, 0, 'I hear this twenty-seventh time in the last two hours. How long to?', 12, 0, 100, 0, 0, 0, 95575, 257403, 257403, 0, 'Дуглас Керрингтон to Рис Керрингтон');
INSERT INTO `creature_text` VALUES (93940, 3, 0, 'Если каждый из нас будет делать то, что должен, мы продержимся. Займись-ка лучше работой.', 12, 0, 100, 0, 0, 0, 95579, 4569, 4569, 0, 'Дуглас Керрингтон to Рис Керрингтон');
INSERT INTO `creature_text` VALUES (93948, 0, 0, 'Тебя послал этот предатель-принц? Коли так, не жди от меня пощады!', 12, 0, 100, 0, 0, 0, 95627, 0, 0, 0, 'Бельгун Жаробород to Player');
INSERT INTO `creature_text` VALUES (93948, 1, 0, 'Вы со своим мерзавцем-принцем заплатите за все, что он сделал с Мурадином!', 12, 0, 100, 0, 0, 0, 96952, 0, 0, 0, 'Бельгун Жаробород to 0');
INSERT INTO `creature_text` VALUES (93948, 2, 0, 'Что... Что я наделал?..', 12, 0, 100, 0, 0, 0, 95628, 0, 0, 0, 'Бельгун Жаробород to Player');
INSERT INTO `creature_text` VALUES (93949, 0, 0, 'Может, короля и нет больше, но я никогда не оставлю Лордерон!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Халак Смертоносный to Player');
INSERT INTO `creature_text` VALUES (93949, 1, 0, 'Свет... о нет...', 12, 0, 100, 0, 0, 0, 96953, 0, 0, 0, 'Халак Смертоносный to Player');
INSERT INTO `creature_text` VALUES (93950, 0, 0, 'Нечестивое чудовище! Праведная ярость Света разорвет тебя на части!', 12, 0, 100, 0, 0, 0, 96954, 0, 0, 0, 'Магрот Защитник to Player');
INSERT INTO `creature_text` VALUES (93950, 1, 0, 'Покой... наконец-то...', 12, 0, 100, 0, 0, 0, 96955, 0, 0, 0, 'Магрот Защитник to Player');
INSERT INTO `creature_text` VALUES (93951, 0, 0, 'Серебряная Длань очистит мир от вашей скверны!', 12, 0, 100, 0, 0, 0, 96956, 0, 0, 0, 'Гавинрад Ужасный to Player');
INSERT INTO `creature_text` VALUES (93951, 1, 0, 'Боль... утихла...', 12, 0, 100, 0, 0, 0, 96957, 0, 0, 0, 'Гавинрад Ужасный to Player');
INSERT INTO `creature_text` VALUES (93955, 0, 0, '|cFFFF0000%s|r begins to cast a spell |cFFFF0000|Hspell:186045|h[\"Солнечный шакрам\"]|h|r, нацеленное на вас!', 42, 0, 100, 0, 0, 0, 95630, 0, 0, 0, 'Помешанный фанатик to Player');
INSERT INTO `creature_text` VALUES (93959, 3, 0, 'Удалось связаться с флотом?', 12, 0, 100, 6, 0, 52940, 95893, 3833, 3833, 0, 'Генн Седогрив');
