INSERT INTO `creature_text` VALUES (94054, 1, 0, 'Уничтожьте фолианты хаоса, чтобы развеять щит Корданы.', 41, 0, 100, 0, 0, 0, 95799, 0, 0, 187007, 'Кордана Оскверненная Песнь to Player');
INSERT INTO `creature_text` VALUES (94054, 3, 0, 'Дай мне свое кольцо. Оно мне нужно. Отдай!', 12, 0, 100, 5, 0, 50320, 95725, 38469, 38469, 0, 'Кордана Оскверненная Песнь to Player');
INSERT INTO `creature_text` VALUES (94054, 3, 0, 'Книги Гул\'дана... Как думаешь, Кадгар пытался их использовать?', 12, 0, 100, 6, 0, 50318, 95723, 13797, 13797, 0, 'Кордана Оскверненная Песнь to Player');
INSERT INTO `creature_text` VALUES (94054, 3, 0, 'Магическая сила… знания… написанные на языке хаоса… Не волнуйся, герой, я уничтожу их.  ', 12, 0, 100, 0, 0, 50319, 95724, 24578, 24578, 0, 'Кордана Оскверненная Песнь to Player');
INSERT INTO `creature_text` VALUES (94054, 3, 0, 'Тогда я заберу его силой! Во славу Гул\'дана!', 12, 0, 100, 5, 0, 50321, 95727, 56438, 56438, 0, 'Кордана Оскверненная Песнь to Player');
INSERT INTO `creature_text` VALUES (94054, 4, 0, 'Кадгар как ребенок. Важничает, мучает пленников, играет жизнями, магией... Которую он якобы понимает.', 12, 0, 100, 0, 0, 50323, 95729, 26875, 26875, 0, 'Кордана Оскверненная Песнь');
INSERT INTO `creature_text` VALUES (94054, 4, 0, 'Сфера владычества открыла мне ИСТИНУ.', 12, 0, 100, 0, 0, 50322, 95728, 14234, 14234, 186984, 'Кордана Оскверненная Песнь to 94156');
INSERT INTO `creature_text` VALUES (94054, 4, 0, 'Что ж, живи пока. Гул\'дана не остановить – за ним Легион. И запомни. Твои вожди предадут тебя. АЗЕРОТ СГОРИТ.', 12, 0, 100, 5, 0, 50325, 95731, 38219, 38219, 0, 'Кордана Оскверненная Песнь');
INSERT INTO `creature_text` VALUES (94097, 1, 0, 'Свеча! Мое!', 12, 0, 100, 0, 0, 54340, 102745, 0, 0, 0, 'Крысолов из племени Синего Воска to Player');
INSERT INTO `creature_text` VALUES (94100, 3, 0, 'Что?.. Дарир был демоном? Как мы не догадались?', 12, 0, 100, 6, 0, 0, 114815, 6290, 6290, 0, 'Лукас Зигмунд to Player');
INSERT INTO `creature_text` VALUES (94101, 3, 0, 'Ну что, делаем ставки на то, сколько продержится Адди?', 12, 0, 100, 1, 0, 0, 101098, 201490, 201490, 0, 'Лаэни Серебряная Стрела to Player');
INSERT INTO `creature_text` VALUES (94110, 3, 0, 'Надежды нет, но я буду сражаться до конца!', 12, 0, 100, 0, 0, 52272, 96434, 13093, 13093, 0, 'Друид Луны to Друид Луны');
INSERT INTO `creature_text` VALUES (94110, 3, 0, 'Словно сам лес ополчился на нас...', 12, 0, 100, 0, 0, 0, 96431, 240907, 240907, 0, 'Друид Луны to Друид Луны');
INSERT INTO `creature_text` VALUES (94112, 3, 0, 'Xavius is the master of this forest now, $c. To enter is to succumb to the Nightmare.', 12, 0, 100, 1, 0, 52248, 96669, 2141, 2141, 0, 'Лиратос Темная Роща to Player');
INSERT INTO `creature_text` VALUES (94112, 3, 0, 'Теперь Ксавий – хозяин леса, $c. Войдешь в него и погрузишься в Кошмар.', 12, 0, 100, 1, 0, 52248, 96669, 2522, 2522, 0, 'Лиратос Темная Роща to Player');
INSERT INTO `creature_text` VALUES (94113, 0, 0, 'Докажи мне, что ты $gдостоин:достойна; этой силы! Освободи моих братьев из их призрачного заточения и возвращайся ко мне, когда они оставят на тебе свой знак.', 14, 0, 100, 0, 0, 0, 95787, 0, 0, 0, 'Рукмаз');
INSERT INTO `creature_text` VALUES (94117, 0, 0, 'Гнилоклюв должен быть к северо-западу отсюда, за пристанью. Принеси мне его голову!', 12, 0, 100, 0, 0, 0, 96628, 0, 0, 0, 'Цецилия Редклифф to Player');
INSERT INTO `creature_text` VALUES (94138, 0, 0, 'Vanessa...?', 12, 0, 100, 0, 0, 0, 109230, 0, 0, 0, 'Princess Tess Greymane to Player');
