INSERT INTO `creature_text` VALUES (94138, 2, 0, 'Ты не пожалеешь об этом. Я убью любого демона или прислужника Легиона, что встанет у меня на пути!', 12, 0, 100, 66, 0, 81239, 127696, 0, 0, 0, 'Лилиан Восс to Player');
