INSERT INTO `creature_text` VALUES (94142, 0, 0, 'Помогите мне, герои! Это еще не конец! Мы должны выжить!', 14, 0, 100, 0, 0, 50665, 95708, 0, 0, 0, 'Верховный маг Кадгар to Верховный маг Кадгар');
INSERT INTO `creature_text` VALUES (94142, 1, 0, 'Никогда! Этот мир не падет перед Легионом!', 14, 0, 100, 0, 0, 50666, 95709, 0, 0, 0, 'Верховный маг Кадгар to Верховный маг Кадгар');
INSERT INTO `creature_text` VALUES (94147, 0, 0, 'Бррибитит!', 12, 0, 100, 0, 0, 54344, 102755, 0, 0, 0, 'Грязнорыл-коротышка to Player');
