INSERT INTO `creature_text` VALUES (94153, 0, 0, 'Move quickly, sisters. We must finish the spell so that no outsider or tauren can break it\'s hold!', 12, 0, 100, 0, 0, 0, 107314, 0, 0, 0, 'Мерзкокрылая крикунья to 0');
INSERT INTO `creature_text` VALUES (94153, 1, 0, 'Глупцы из племени Речной Гривы не ожидают, что скоро весь лес яростно ополчится против них.', 12, 0, 100, 0, 0, 0, 107316, 0, 0, 0, 'Мерзкокрылая крикунья to 0');
INSERT INTO `creature_text` VALUES (94159, 0, 0, 'Прекрасный выбор. А теперь я посвящу тебя в подробности нашего плана.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Адмирал флота Тетис to Player');
