INSERT INTO `creature_text` VALUES (95319, 1, 0, 'Эти друиды были слишком слабы... Надеюсь, с тобой не будет так же скучно!', 12, 0, 100, 0, 0, 52113, 97947, 0, 0, 0, 'Ксандрис Бесчестный to Player');
