INSERT INTO `creature_text` VALUES (95464, 0, 0, 'Последний свет оставил верховного лорда Тириона Фордринга.', 16, 0, 100, 0, 0, 0, 96198, 0, 0, 0, '95464 to Player');
INSERT INTO `creature_text` VALUES (95617, 0, 0, 'Спасибо! Значит, мое время еще не пришло!', 12, 0, 100, 377, 0, 0, 99630, 0, 0, 0, 'Друид Лунного Когтя to Player');
INSERT INTO `creature_text` VALUES (95617, 1, 0, 'Благодарю за спасение!', 12, 0, 100, 377, 0, 0, 99634, 0, 0, 0, 'Друид Лунного Когтя to Player');
INSERT INTO `creature_text` VALUES (95617, 3, 0, 'Спасибо тебе! Да хранит тебя свет Элуны!', 12, 0, 100, 377, 0, 0, 99632, 0, 0, 0, 'Друид Лунного Когтя to Player');
INSERT INTO `creature_text` VALUES (95659, 0, 0, 'Мы прорвались в цитадель! Гул\'дан внутри. Я обустрою плацдарм. Развивайте наступление!', 14, 0, 100, 0, 0, 50667, 95710, 0, 0, 0, 'Верховный маг Кадгар  to Player');
INSERT INTO `creature_text` VALUES (95659, 1, 0, 'Порча Гул\'дана сочится из стен этой цитадели! Второй портал рядом. Сдерживайте демонов, пока я ломаю печати и привязываю портал к его логову.', 14, 0, 100, 0, 0, 50670, 95714, 0, 0, 0, 'Верховный маг Кадгар  to Player');
INSERT INTO `creature_text` VALUES (95659, 2, 0, 'I broke the first seal, soon the portal will lead us to Gul\'dan.', 14, 0, 100, 0, 0, 50671, 95715, 0, 0, 0, 'Верховный маг Кадгар  to Ритуальный голем');
INSERT INTO `creature_text` VALUES (95659, 3, 0, 'Никогда бы не поверил, что Гул\'дан зайдет так далеко… Продолжаем наступление. В этих руинах есть портал вглубь цитадели.', 14, 0, 100, 0, 0, 50668, 95712, 0, 0, 0, 'Верховный маг Кадгар  to Player');
INSERT INTO `creature_text` VALUES (95659, 4, 0, 'Портал готов, герои. Крепитесь. Мне неведомо, каких чудовищ Гул\'дан призвал по ту его сторону.', 14, 0, 100, 0, 0, 50669, 95713, 0, 0, 0, 'Верховный маг Кадгар  to Player');
INSERT INTO `creature_text` VALUES (95659, 5, 0, 'Гул\'дан сбежал к Темному Порталу! Быстрее! Ему там некуда деться, покончим с ним!', 14, 0, 100, 0, 0, 50676, 95721, 0, 0, 0, 'Верховный маг Кадгар  to Player');
INSERT INTO `creature_text` VALUES (95659, 6, 0, 'Портал открыт! Он приведет нас прямо к Гул\'дану. Он заплатит за свои преступления.', 14, 0, 100, 0, 0, 50675, 95720, 0, 0, 0, 'Верховный маг Кадгар  to Player');
INSERT INTO `creature_text` VALUES (95672, 1, 0, 'Возрадуйся мощи, что дарует Свет!', 14, 0, 100, 0, 0, 54687, 102981, 0, 0, 191924, 'Олмир Просвещенный to Хирья');
INSERT INTO `creature_text` VALUES (95672, 1, 0, 'Свет – твой союзник, Хирья!', 14, 0, 100, 0, 0, 54687, 102980, 0, 0, 191924, 'Олмир Просвещенный to Хирья');
INSERT INTO `creature_text` VALUES (95672, 3, 0, 'The Light embraces you, Hyrja!', 14, 0, 100, 0, 0, 54687, 102980, 51251, 51251, 191924, 'Olmyr the Enlightened to Hyrja');
