INSERT INTO `creature_text` VALUES (95675, 4, 0, 'Все мои планы... рухнули...', 14, 0, 100, 0, 0, 0, 102927, 0, 0, 0, 'Король-Бог Сковальд to Player');
INSERT INTO `creature_text` VALUES (95676, 0, 0, 'Испытания показали, что ваши сила, сноровка и мудрость достойны величайших королей врайкулов. Что ж... вам предстоит сойтись с ними в бою. Одержите победу – и получите награду!', 14, 0, 100, 0, 0, 0, 100271, 0, 0, 0, 'Один to Player');
INSERT INTO `creature_text` VALUES (95676, 1, 0, 'Победителям – достойная награда... Эгида Агграмара ваша.', 14, 0, 100, 0, 0, 0, 100240, 0, 0, 0, 'Один to Король Ранульф');
INSERT INTO `creature_text` VALUES (95676, 2, 0, 'Удивительно! Я не верил, что кто-то может сравниться с валарьярами... Но вы доказали, что это возможно.', 14, 0, 100, 0, 0, 0, 102879, 0, 0, 0, 'Один to Один');
INSERT INTO `creature_text` VALUES (95676, 3, 0, 'Вашим последним испытанием... стану я. Победите – и я высеку ваши имена на стенах этих чертогов. И дарую вам достойную награду.', 14, 0, 100, 0, 0, 0, 102881, 0, 0, 0, 'Один to Один');
INSERT INTO `creature_text` VALUES (95676, 4, 0, 'Веками я не встречал достойных противников. Да начнется битва!', 14, 0, 100, 0, 0, 0, 102913, 0, 0, 0, 'Один to Player');
INSERT INTO `creature_text` VALUES (95676, 5, 0, 'Знайте, герои: я не стану себя сдерживать.', 14, 0, 100, 0, 0, 0, 102885, 0, 0, 0, 'Один to 0');
INSERT INTO `creature_text` VALUES (95676, 6, 0, '|TInterface\\Icons\\ability_priest_flashoflight:20|t Один begins to cast a spell |cFFFF0404|Hspell:198077|h[\"Расколотые копья\"]|h|r!', 41, 0, 100, 0, 0, 0, 106485, 0, 0, 0, 'Один to 0');
INSERT INTO `creature_text` VALUES (95676, 7, 0, 'У каждого есть слабое место. Я найду ваши!', 14, 0, 100, 0, 0, 0, 102893, 0, 0, 0, 'Один to 0');
INSERT INTO `creature_text` VALUES (95676, 7, 1, 'Гибкий ум так же опасен, как и острый меч.', 14, 0, 100, 0, 0, 0, 102891, 0, 0, 0, 'Один to 0');
INSERT INTO `creature_text` VALUES (95676, 8, 0, '|TINTERFACE\\ICONS\\trade_archaeology_vrykul_runestick:20|t Один помечает всех противников |cFFFF0404|Hspell:197961|h[руническим клеймом]|h|r!', 42, 0, 100, 0, 0, 0, 103781, 0, 0, 0, 'Один to Player');
INSERT INTO `creature_text` VALUES (95676, 9, 0, 'Довольно! Победа за вами! Вы удивительные создания. И вас ждет достойная награда.', 12, 0, 100, 0, 0, 0, 102907, 0, 0, 0, 'Один to 0');
INSERT INTO `creature_text` VALUES (95693, 0, 0, 'Crawliac descends upon you all!', 12, 0, 100, 0, 0, 0, 97325, 0, 0, 0, 'Проклинательница Акара to Player');
INSERT INTO `creature_text` VALUES (95693, 1, 0, 'Как ты победишь меня, если я властвую над природой?!', 12, 0, 100, 0, 0, 0, 102004, 0, 0, 0, 'Проклинательница Акара to Player');
