INSERT INTO `creature_text` VALUES (95726, 0, 1, 'Идол наш, |3-6($r). Я ни за что не скажу тебе, где он!', 12, 0, 100, 0, 0, 68343, 110968, 0, 0, 0, 'Мракобес-ревнитель to Player');
