INSERT INTO `creature_text` VALUES (96083, 3, 0, 'Пожалуйста, найди Музи. Я отдохну и тоже продолжу поиски. Если найдешь, отведи его к матери – она пошла выше по течению.', 12, 0, 100, 5, 0, 0, 101175, 237395, 237395, 0, 'Рордан Мудрая Вода to Player');
