INSERT INTO `creature_text` VALUES (96164, 0, 0, 'The Path of Huln follows in the footsteps of Huln\'s last and greatest hunt.', 12, 0, 100, 0, 0, 0, 98229, 0, 0, 0, 'Духостранник Смоляной Рог to Player');
INSERT INTO `creature_text` VALUES (96164, 1, 0, 'Be warned, the path ahead is quite dangerous. I can only provide you guidance and healing. Nothing more.', 12, 0, 100, 0, 0, 0, 98535, 0, 0, 0, 'Духостранник Смоляной Рог to Player');
INSERT INTO `creature_text` VALUES (96164, 2, 0, 'For Huln, the War of the Ancients did not end at the Well of Eternity. With the Legion defeated, he focused on the monster that got away.', 12, 0, 100, 0, 0, 0, 98534, 0, 0, 0, 'Духостранник Смоляной Рог to Player');
INSERT INTO `creature_text` VALUES (96164, 3, 0, 'Welcome, my friends, to the Vault of Neltharion. Or as the world came to know him: Deathwing.', 12, 0, 100, 0, 0, 0, 98289, 0, 0, 0, 'Духостранник Смоляной Рог to Player');
INSERT INTO `creature_text` VALUES (96175, 0, 0, 'Как хранитель этого места, я испытываю мощь каждого, кто желает продолжить путь.', 14, 0, 100, 0, 0, 0, 97637, 0, 0, 0, 'Йотнар to Player');
INSERT INTO `creature_text` VALUES (96175, 1, 0, 'Приготовься!', 14, 0, 100, 0, 0, 52385, 97638, 0, 0, 0, 'Йотнар to Player');
INSERT INTO `creature_text` VALUES (96175, 2, 0, 'Испытание мощи пройдено. Иди ко входу в хранилище. Я дам тебе благословение стражей.', 12, 0, 100, 0, 0, 52388, 97718, 63498, 63498, 0, 'Йотнар');
INSERT INTO `creature_text` VALUES (96175, 3, 0, 'Надеюсь, тебе по силам пройти первое испытание.', 14, 0, 100, 0, 0, 52383, 97636, 146, 146, 0, 'Йотнар to Player');
