INSERT INTO `creature_text` VALUES (96183, 3, 0, 'Look there, upon that terrible sight. How many disasters must we face before we can at last find peace?', 12, 0, 100, 0, 0, 63543, 114697, 6384, 6384, 0, 'Danath Trollbane to Player');
