INSERT INTO `creature_text` VALUES (96198, 3, 0, 'Эй! Кто там меня разыскивал?', 12, 0, 100, 1, 0, 0, 104783, 0, 0, 0, 'Катриона Макрей to Player');
INSERT INTO `creature_text` VALUES (96198, 4, 0, 'Интересно, куда они все подевались... Ну ладно. Может, еще вернутся.', 12, 0, 100, 1, 0, 0, 104784, 0, 0, 0, 'Катриона Макрей to Player');
