INSERT INTO `creature_text` VALUES (96318, 3, 0, 'Смотрите! Он уцелел. Какие милые черные рожки!', 12, 0, 100, 1, 0, 62091, 97796, 66586, 66586, 0, 'Халн Крутогор to 96242');
