INSERT INTO `creature_text` VALUES (96347, 1, 0, 'Well, I chased him around until he scrambled up a pipe that leads up top in front of Violet Hold. You\'ll probably wanna go looking around up there somewhere.', 12, 0, 100, 0, 0, 0, 97880, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (96368, 0, 0, 'Deucus is an old fla--er, friend. I\'d be happy to calibrate the burette for you.', 12, 0, 0, 0, 0, 0, 98352, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (96368, 1, 1, 'There, much better. Please give my best to Deucus.', 12, 0, 0, 0, 0, 0, 98360, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (96387, 3, 0, 'The eggs were spoiled. They must be destroyed.', 12, 0, 100, 396, 0, 62099, 113353, 1317, 1317, 0, 'Игрул Губитель Драконов to Player');
INSERT INTO `creature_text` VALUES (96387, 3, 0, 'Стражи просыпаются!', 12, 0, 100, 45, 0, 62101, 113356, 15132, 15132, 0, 'Игрул Губитель Драконов to Player');
