INSERT INTO `creature_text` VALUES (96420, 0, 0, 'They outnumber us by an order of magnitude. Can we defeat them?', 12, 0, 100, 6, 0, 55092, 99002, 0, 0, 0, 'Циана Ночная Глефа to Player');
INSERT INTO `creature_text` VALUES (96420, 1, 0, 'More... I want more power!', 12, 0, 100, 0, 0, 55094, 100740, 0, 0, 0, 'Циана Ночная Глефа to Player');
INSERT INTO `creature_text` VALUES (96423, 3, 0, 'Гора будет принадлежать племени Тотема Скверны!', 12, 0, 100, 0, 0, 0, 102408, 78189, 78189, 0, 'Воспеватель крови из племени Тотема Скверны to Player');
INSERT INTO `creature_text` VALUES (96423, 4, 0, 'I\'ll rip your hands off you!', 12, 0, 100, 0, 0, 0, 102403, 3221, 3221, 0, 'Воспеватель крови из племени Тотема Скверны to Player');
INSERT INTO `creature_text` VALUES (96436, 0, 0, '$n, will you use your spectral sight on the cave entrance? Something\'s not right.', 12, 0, 100, 397, 0, 55233, 98201, 0, 0, 0, 'Джейс Темный Ткач to Player');
INSERT INTO `creature_text` VALUES (96436, 1, 0, 'They won\'t escape us. Illidari, attack!', 14, 0, 100, 397, 0, 55234, 98271, 0, 0, 0, 'Джейс Темный Ткач to Player');
INSERT INTO `creature_text` VALUES (96441, 0, 0, 'Damn your spectral sight!', 14, 0, 100, 0, 0, 55172, 98023, 0, 0, 0, 'Повелитель Скверны Кхаза to Player');
INSERT INTO `creature_text` VALUES (96441, 1, 0, 'My axe never misses its mark.', 14, 0, 100, 0, 0, 55174, 103483, 0, 0, 0, 'Повелитель Скверны Кхаза to 100061');
INSERT INTO `creature_text` VALUES (96441, 2, 0, 'I would have sundered your forces...', 12, 0, 100, 0, 0, 55173, 98274, 0, 0, 0, 'Повелитель Скверны Кхаза to Player');
INSERT INTO `creature_text` VALUES (96453, 0, 0, 'Shard, come to me and strike our enemies down!', 12, 0, 100, 0, 0, 0, 114577, 0, 0, 0, 'Наваррогг to Player');
INSERT INTO `creature_text` VALUES (96453, 1, 0, 'Torok will not trouble the Stonedark again. Let\'s head to the homeland of my people.', 12, 0, 100, 0, 0, 0, 98233, 0, 0, 0, 'Наваррогг to Player');
INSERT INTO `creature_text` VALUES (96465, 0, 0, 'The Drekirjar have upset our ancient pact.', 12, 0, 100, 0, 0, 0, 98427, 0, 0, 0, 'Vetir to Player');
INSERT INTO `creature_text` VALUES (96465, 1, 0, 'It is time they face the consequences.', 12, 0, 100, 0, 0, 0, 98428, 0, 0, 0, 'Vetir to Player');
INSERT INTO `creature_text` VALUES (96468, 0, 0, 'A glorious landing! Are you ready to find Magnar?', 12, 0, 100, 0, 0, 60384, 98317, 0, 0, 0, 'Krutnir to Player');
INSERT INTO `creature_text` VALUES (96468, 1, 0, 'His tomb is not far.', 12, 0, 100, 0, 0, 60385, 98318, 0, 0, 0, 'Krutnir to Player');
INSERT INTO `creature_text` VALUES (96468, 2, 0, 'The dead are rising! Something\'s not right...', 12, 0, 100, 0, 0, 60386, 98386, 0, 0, 0, 'Krutnir to Player');
INSERT INTO `creature_text` VALUES (96468, 3, 0, 'These warriors are restless! They must sense some kind of disturbance...', 12, 0, 100, 0, 0, 60387, 98388, 0, 0, 0, 'Krutnir to Rattling Dead');
INSERT INTO `creature_text` VALUES (96468, 4, 0, 'Onward! We are almost to Magnar\'s tomb.', 12, 0, 100, 0, 0, 60388, 98389, 0, 0, 0, 'Krutnir to Rattling Dead');
INSERT INTO `creature_text` VALUES (96468, 5, 0, 'A good fight! Follow me. I will open the tomb.', 12, 0, 100, 0, 0, 60393, 98321, 0, 0, 0, 'Krutnir to Пиллик');
INSERT INTO `creature_text` VALUES (96468, 6, 0, 'The guardians do not obey me! Seems we will have to do this the hard way.', 12, 0, 100, 0, 0, 60395, 98322, 0, 0, 0, 'Krutnir to 94714');
INSERT INTO `creature_text` VALUES (96468, 7, 0, 'These spirit winds are relentless! We cannot walk through.', 12, 0, 100, 0, 0, 60396, 98685, 0, 0, 0, 'Krutnir to 94714');
INSERT INTO `creature_text` VALUES (96468, 8, 0, '%s ponders the situation at hand.', 16, 0, 100, 0, 0, 0, 98686, 0, 0, 0, 'Krutnir to 94714');
INSERT INTO `creature_text` VALUES (96468, 9, 0, 'We should be able to leap over this.', 12, 0, 100, 0, 0, 0, 98323, 0, 0, 0, 'Krutnir to 94714');
INSERT INTO `creature_text` VALUES (96468, 10, 0, 'His guards cannot tell friend from foe.', 12, 0, 100, 0, 0, 60398, 98688, 0, 0, 0, 'Krutnir to Phantom Weaver of the Wind');
INSERT INTO `creature_text` VALUES (96468, 11, 0, 'Watch out!', 12, 0, 100, 0, 0, 60399, 125300, 0, 0, 0, 'Krutnir to Player');
INSERT INTO `creature_text` VALUES (96468, 12, 0, 'That door is sealed with lightning. We must find a way through!', 12, 0, 100, 0, 0, 60400, 98690, 0, 0, 0, 'Krutnir to Player');
INSERT INTO `creature_text` VALUES (96468, 13, 0, 'Like I always say, if you can\'t reach it, throw something at it. Never fails!', 12, 0, 100, 0, 0, 60401, 98691, 0, 0, 0, 'Krutnir to Player');
INSERT INTO `creature_text` VALUES (96468, 14, 0, 'Here we are... Magnar\'s chamber!', 12, 0, 100, 0, 0, 60402, 98692, 0, 0, 0, 'Krutnir to 96455');
INSERT INTO `creature_text` VALUES (96468, 15, 0, 'Champion, we must save him! I will find the words to spur him on to the Halls of Valor, but he may resist the truth and turn his guards against me.', 12, 0, 100, 0, 0, 60403, 98324, 0, 0, 0, 'Krutnir to 96455');
INSERT INTO `creature_text` VALUES (96468, 16, 0, 'Say the word and we shall begin.', 12, 0, 100, 0, 0, 60404, 98325, 0, 0, 0, 'Krutnir to 96455');
INSERT INTO `creature_text` VALUES (96468, 17, 0, 'Командуй, когда начинать.', 12, 0, 100, 396, 0, 60404, 98325, 0, 0, 0, 'Krutnir to 96455');
INSERT INTO `creature_text` VALUES (96468, 18, 0, 'Осквернители! Мы должны уничтожить этих тварей!', 12, 0, 100, 22, 0, 60423, 98320, 0, 0, 0, 'Krutnir to Громыхающий мертвец');
INSERT INTO `creature_text` VALUES (96468, 19, 0, '%s настороженно смотрит на Рейлара Буревестника.', 16, 0, 100, 396, 0, 60395, 98662, 0, 0, 0, 'Krutnir to 94714');
INSERT INTO `creature_text` VALUES (96469, 0, 0, 'Теперь ступай. Krutnir ожидает тебя на Небесной Площадке. Он поможет тебе в этом деле.', 12, 0, 100, 0, 0, 0, 106221, 0, 0, 0, 'Odin to Player');
INSERT INTO `creature_text` VALUES (96469, 0, 0, 'Теперь ступай. Хрутнир ожидает тебя на Небесной Площадке. Он поможет тебе в этом деле.', 12, 0, 100, 0, 0, 0, 106221, 0, 0, 0, 'Один to Player');
INSERT INTO `creature_text` VALUES (96469, 1, 0, 'Торим... сын! Держись! Я пошлю героев тебе на помощь.', 14, 0, 100, 1, 0, 68400, 119684, 0, 0, 0, 'Один to Player');
INSERT INTO `creature_text` VALUES (96469, 2, 0, 'Слушайте все! Сегодня благодаря новому лидеру валарьяры одержали первую решительную победу над Легионом в одном из его миров!', 14, 0, 100, 1, 0, 69283, 119685, 0, 0, 0, 'Один to Player');
INSERT INTO `creature_text` VALUES (96469, 3, 0, 'Слава валарьярам! Слава нашему лидеру!', 14, 0, 100, 25, 0, 69284, 119687, 0, 0, 0, 'Один to Player');
INSERT INTO `creature_text` VALUES (96470, 0, 0, 'Deucus Valdera? A blustery fellow, but honorable. I will be pleased to assist you with calibration.', 12, 0, 0, 0, 0, 0, 98365, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (96470, 1, 1, 'I\'ve done my best to calibrate the burette. Give Deucus my regards.', 12, 0, 0, 0, 0, 0, 98367, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (96473, 0, 0, 'Да исполнится воля Легиона!', 12, 0, 100, 0, 0, 56991, 105111, 0, 0, 0, 'Эредарский колдун to Player');
INSERT INTO `creature_text` VALUES (96474, 0, 0, 'Deucus? That undead has some nerve! Called me a cheater once... but I suppose I can forgive him. Lemme see...', 12, 0, 0, 0, 0, 0, 98369, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (96474, 1, 1, 'Here ya go! Tell Deucus I treated ya fair\'n square!', 12, 0, 0, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (96479, 0, 0, 'Приветствую вас! Пожалуйста, осмотрите мои товары.', 12, 0, 100, 0, 0, 0, 43341, 0, 0, 0, 'Бреанни to Player');
INSERT INTO `creature_text` VALUES (96479, 0, 1, 'Приветствую.', 12, 0, 100, 0, 0, 0, 145038, 0, 0, 0, 'Бреанни to Player');
INSERT INTO `creature_text` VALUES (96483, 3, 0, 'О, какая прелесть! Коробка новых игрушек!', 12, 0, 100, 4, 0, 35296, 113081, 13114, 13114, 0, 'Джепетто Таратор to Player');
INSERT INTO `creature_text` VALUES (96485, 0, 0, 'Deucus? I thought he was dead! Of course I would be happy to help my old friend.', 12, 0, 0, 0, 0, 0, 98374, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (96485, 1, 1, 'Perfect calibration! Tell Deucus I shall make a trip to Dalaran soon to say hello.', 12, 0, 0, 0, 0, 0, 98375, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (96488, 3, 0, 'Ворон подлетает к вам и зависает в воздухе, чего-то ожидая. Он принес послание!', 41, 0, 100, 0, 0, 0, 97467, 91, 91, 0, 'Huguynn to Player');
INSERT INTO `creature_text` VALUES (96490, 0, 0, 'Deucus always sends me the most challenging puzzles to solve! Let\'s see that burette!', 12, 0, 0, 0, 0, 0, 98377, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (96490, 1, 1, 'Perfection! You can now make potions with precise exactitude!', 12, 0, 0, 0, 0, 0, 98379, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (96494, 0, 0, 'For the Legion!', 12, 0, 100, 0, 0, 55192, 96663, 0, 0, 0, 'Страж Скверны - мясник to Player');
INSERT INTO `creature_text` VALUES (96499, 4, 0, 'Повелитель Скверны уже близко!', 12, 0, 100, 0, 0, 55237, 98272, 11232, 11232, 0, 'Джейс Темный Ткач to Player');
INSERT INTO `creature_text` VALUES (96503, 0, 0, 'My thanks. Come, my brothers... back to the battle!', 12, 0, 100, 0, 0, 55076, 99093, 0, 0, 0, 'Морская взывательница из клана Змеиных Колец to Генерал Волрот');
INSERT INTO `creature_text` VALUES (96512, 0, 0, 'Defilers... I can smell the Nightmare in your blood. Be gone from these woods or suffer nature\'s wrath!', 14, 0, 100, 0, 0, 0, 102280, 0, 0, 0, 'Arch-Druid Glaidalis to Player');
INSERT INTO `creature_text` VALUES (96512, 1, 0, 'Defilers... so it is you who have befouled these woods with the Nightmare.', 14, 0, 100, 0, 0, 0, 102281, 0, 0, 0, 'Arch-Druid Glaidalis to 0');
INSERT INTO `creature_text` VALUES (96512, 2, 0, 'Andu-falah-dor!', 14, 0, 100, 0, 0, 0, 102289, 0, 0, 0, 'Arch-Druid Glaidalis to 0');
INSERT INTO `creature_text` VALUES (96512, 2, 1, 'Be purified in the light of the stars!', 14, 0, 100, 0, 0, 0, 102290, 0, 0, 0, 'Arch-Druid Glaidalis to 0');
INSERT INTO `creature_text` VALUES (96512, 3, 0, 'I am the vessel of nature\'s fury!', 14, 0, 100, 0, 0, 0, 102287, 0, 0, 0, 'Arch-Druid Glaidalis to 0');
INSERT INTO `creature_text` VALUES (96512, 4, 0, 'By tooth and claw be purged!', 14, 0, 100, 0, 0, 0, 102286, 0, 0, 0, 'Arch-Druid Glaidalis to 0');
INSERT INTO `creature_text` VALUES (96512, 5, 0, 'I will bleed the Nightmare from you!', 14, 0, 100, 0, 0, 0, 102285, 0, 0, 0, 'Arch-Druid Glaidalis to 0');
INSERT INTO `creature_text` VALUES (96512, 6, 0, 'The Nightmare... clouds my thoughts no more...', 14, 0, 100, 0, 0, 0, 102300, 0, 0, 0, 'Arch-Druid Glaidalis to Player');
INSERT INTO `creature_text` VALUES (96527, 0, 0, 'Да, мы должны нанести ответный удар. Но как?', 12, 0, 100, 6, 0, 60554, 99096, 0, 0, 0, 'Тралл to Player');
INSERT INTO `creature_text` VALUES (96527, 1, 0, 'Они явятся по наши души.', 12, 0, 100, 1, 0, 60559, 99100, 0, 0, 0, 'Тралл to Player');
INSERT INTO `creature_text` VALUES (96527, 2, 0, 'Берегитесь!', 12, 0, 100, 0, 0, 60555, 138096, 0, 0, 0, 'Тралл to Player');
INSERT INTO `creature_text` VALUES (96527, 3, 0, 'Твой господин еще нас не одолел.', 12, 0, 100, 0, 0, 60556, 99120, 0, 0, 0, 'Тралл to Фальтий');
INSERT INTO `creature_text` VALUES (96527, 4, 0, 'I\'ve got this one, shaman. Help the others!', 12, 0, 100, 0, 0, 60552, 99783, 0, 0, 0, 'Тралл to Player');
INSERT INTO `creature_text` VALUES (96527, 5, 0, 'Молот Рока', 12, 0, 100, 1, 0, 60521, 99141, 0, 0, 0, 'Тралл to Player');
INSERT INTO `creature_text` VALUES (96527, 6, 0, 'Rehgar has a plan. Speak with him. He\'s good to have at your back in a fight.', 12, 0, 100, 1, 0, 0, 107180, 0, 0, 0, 'Тралл to Player');
INSERT INTO `creature_text` VALUES (96527, 7, 0, 'Молот Рока где-то здесь. Я чувствую его.', 12, 0, 100, 1, 0, 0, 106969, 0, 0, 0, 'Тралл to 950');
INSERT INTO `creature_text` VALUES (96527, 8, 0, 'Надо осмотреть те залы... Я буду рядом...', 12, 0, 100, 1, 0, 0, 106970, 0, 0, 0, 'Тралл to 950');
INSERT INTO `creature_text` VALUES (96527, 9, 0, 'Еще одна причина, почему мы должны первыми добраться до Молота Рока.', 12, 0, 100, 1, 0, 0, 106975, 0, 0, 0, 'Тралл to Dave\'s Industrial Light and Magic Bunny (Small)(Sessile)');
INSERT INTO `creature_text` VALUES (96527, 10, 0, 'Doomhammer... it felt like dead weight in my hands. The elements were silent. It has been this way since I used it to slay Garrosh.', 12, 0, 100, 1, 0, 0, 106994, 0, 0, 0, 'Тралл to Dave\'s Industrial Light and Magic Bunny (Small)(Sessile)');
INSERT INTO `creature_text` VALUES (96527, 11, 0, 'Надо двигаться дальше. Гет\'зун где-то рядом...', 12, 0, 100, 1, 0, 0, 107002, 0, 0, 0, 'Тралл to Player');
INSERT INTO `creature_text` VALUES (96527, 12, 0, 'The Doomhammer! Get the Doomhammer! Prove your power as a shaman...', 12, 0, 100, 1, 0, 0, 107010, 0, 0, 0, 'Тралл to Player');
INSERT INTO `creature_text` VALUES (96527, 13, 0, 'Весь Азерот восстанет против тебя, демон.', 12, 0, 100, 1, 0, 0, 107025, 0, 0, 0, 'Тралл to Гет\'зун');
INSERT INTO `creature_text` VALUES (96527, 14, 0, 'Сама земля под твоими ногами и небо над твоей головой обрушатся на тебя и остановят твои злодеяния!', 12, 0, 100, 1, 0, 0, 107027, 0, 0, 0, 'Тралл to Гет\'зун');
INSERT INTO `creature_text` VALUES (96527, 15, 0, 'Молот Рока выбрал тебя, шаман. Он уже дарует тебе больше силы, чем когда-то из него черпал я.', 12, 0, 100, 1, 0, 0, 107043, 0, 0, 0, 'Тралл to 950');
INSERT INTO `creature_text` VALUES (96527, 16, 0, 'I must go my own way. I have a great deal of thinking to do. Aggra will be at the Maelstrom shortly. Listen to her counsel... she has taught me so much.', 12, 0, 100, 6, 0, 0, 107044, 0, 0, 0, 'Тралл to 950');
INSERT INTO `creature_text` VALUES (96527, 17, 0, 'Возвращайся к Служителям Земли. Объедини их. Обрати их мощь на борьбу с Пылающим Легионом. Пробил твой час.', 12, 0, 100, 1, 0, 0, 107045, 0, 0, 0, 'Тралл to 950');
INSERT INTO `creature_text` VALUES (96528, 0, 0, 'Нам нужно оружие, которым можно дать Легиону достойный отпор.', 12, 0, 100, 0, 0, 0, 99097, 0, 0, 0, 'Предсказатель Нобундо to Player');
INSERT INTO `creature_text` VALUES (96528, 1, 0, 'Здесь стоит древняя защита. Надо ею воспользоваться.', 12, 0, 100, 0, 0, 0, 99103, 0, 0, 0, 'Предсказатель Нобундо to Player');
INSERT INTO `creature_text` VALUES (96528, 2, 0, 'It is ready! Everyone, focus on the pillar. Quickly!', 12, 0, 100, 0, 0, 0, 99134, 0, 0, 0, 'Предсказатель Нобундо to 1038');
INSERT INTO `creature_text` VALUES (96528, 3, 0, 'Мы стоим на пороге забвения, но мы не одиноки. Выйди вперед, шаман.', 12, 0, 100, 0, 0, 0, 100765, 0, 0, 0, 'Предсказатель Нобундо to Player');
INSERT INTO `creature_text` VALUES (96528, 4, 0, 'Займи свое место в круге, $gпредсказатель:предсказательница;. Служители Земли собрались заново, а с ними возродилась и надежда на спасение мира.', 12, 0, 100, 0, 0, 0, 100767, 0, 0, 0, 'Предсказатель Нобундо to Player');
INSERT INTO `creature_text` VALUES (96528, 5, 0, 'You are the chosen defender of Azeroth! May your wisdom guide this circle to victory, and may the power of the weapon you wield crush those who would bring chaos to this land.', 12, 0, 100, 0, 0, 0, 100768, 0, 0, 0, 'Предсказатель Нобундо to Player');
INSERT INTO `creature_text` VALUES (96528, 6, 0, 'Step this way, Farseer. The tauren tell of hidden chambers below that may serve as a base of operations.', 12, 0, 100, 0, 0, 0, 100830, 0, 0, 0, 'Предсказатель Нобундо to Player');
INSERT INTO `creature_text` VALUES (96530, 0, 0, 'Видите? Сработало! Этот мир еще постоит за себя, если мы хорошо его к этому подготовим.', 12, 0, 100, 0, 0, 0, 99113, 0, 0, 0, 'Эрунак Говорящий с Камнем to Player');
INSERT INTO `creature_text` VALUES (96530, 1, 0, 'Come with me. The depths await!', 12, 0, 100, 0, 0, 0, 108774, 0, 0, 0, 'Эрунак Говорящий с Камнем to Player');
INSERT INTO `creature_text` VALUES (96539, 0, 0, 'Нагорье повержено. Силы клана рассеяны.', 12, 0, 100, 0, 0, 0, 99106, 0, 0, 0, 'Буревестница Милра to Player');
INSERT INTO `creature_text` VALUES (96539, 1, 0, 'Они нас выследили!', 12, 0, 100, 0, 0, 0, 99115, 0, 0, 0, 'Буревестница Милра to Player');
INSERT INTO `creature_text` VALUES (96539, 2, 0, 'The Doomhammer fell somewhere below. Hold on! This part always gives me tummy the wubblies!', 12, 0, 100, 0, 0, 0, 101364, 0, 0, 0, 'Буревестница Милра to Player');
INSERT INTO `creature_text` VALUES (96541, 0, 0, 'Эта штуковина пролежала мертвым грузом десять тысяч лет.', 12, 0, 100, 0, 0, 0, 99110, 0, 0, 0, 'Регар Ярость Земли to Player');
INSERT INTO `creature_text` VALUES (96541, 1, 0, 'Ха-ха! Ты $gнаш защитник:наша защитница;!', 12, 0, 100, 0, 0, 0, 99121, 0, 0, 0, 'Регар Ярость Земли to Player');
INSERT INTO `creature_text` VALUES (96541, 2, 0, 'Помоги остальным!', 12, 0, 100, 0, 0, 0, 99122, 0, 0, 0, 'Регар Ярость Земли to Player');
INSERT INTO `creature_text` VALUES (96574, 0, 0, 'Мне как раз хотелось подраться!', 14, 0, 100, 0, 0, 0, 104682, 0, 0, 0, 'Закаленный бурей страж to Закаленный бурей страж');
INSERT INTO `creature_text` VALUES (96590, 4, 0, 'Ща ВЛОМЛЮ!', 14, 0, 100, 0, 0, 0, 111786, 6541, 46510, 0, 'Гурбог Крушака');
INSERT INTO `creature_text` VALUES (96591, 0, 0, 'Ты пожалеешь о своих словах!', 12, 0, 100, 0, 0, 0, 100559, 0, 0, 0, 'Начинающий охотник to Player');
INSERT INTO `creature_text` VALUES (96591, 0, 1, 'Ладно, твоя взяла! Найду себе другое место для охоты.', 12, 0, 100, 0, 0, 0, 103680, 0, 0, 0, 'Начинающий охотник to Player');
INSERT INTO `creature_text` VALUES (96591, 0, 2, 'Можешь занять мое место. Я себе получше найду.', 12, 0, 100, 0, 0, 0, 100567, 0, 0, 0, 'Начинающий охотник to Player');
INSERT INTO `creature_text` VALUES (96591, 0, 3, 'Меня два раза просить не нужно.', 12, 0, 100, 0, 0, 0, 100548, 0, 0, 0, 'Начинающий охотник to Player');
INSERT INTO `creature_text` VALUES (96612, 3, 0, 'И что мы отдадим взамен?', 12, 0, 100, 6, 0, 0, 99601, 2143, 2143, 0, 'Зверолов из племени Кровавого Тотема to Player');
INSERT INTO `creature_text` VALUES (96612, 3, 0, 'Наверное, все не так уж плохо...', 12, 0, 100, 1, 0, 0, 99600, 7011, 7011, 0, 'Зверолов из племени Кровавого Тотема to Player');
INSERT INTO `creature_text` VALUES (96615, 1, 0, 'The Feltotem will own this mountain!', 12, 0, 100, 0, 0, 54828, 102408, 0, 0, 0, 'Демоническое отродье Тотема Скверны to Player');
INSERT INTO `creature_text` VALUES (96615, 1, 0, 'Союзники помогут нам!', 12, 0, 100, 0, 0, 0, 102406, 0, 0, 0, 'Демоническое отродье из племени Тотема Скверны to Player');
INSERT INTO `creature_text` VALUES (96615, 3, 0, 'I smell blood in the forest.', 12, 0, 100, 0, 0, 54822, 102402, 308664, 308664, 0, 'Демоническое отродье Тотема Скверны to Player');
INSERT INTO `creature_text` VALUES (96640, 0, 0, 'Ну, все! Мой кулак, твоя морда!', 14, 0, 100, 0, 0, 0, 104680, 0, 0, 0, 'Валарьяр-лучница to Валарьяр-лучница');
INSERT INTO `creature_text` VALUES (96644, 0, 0, 'Следуй за мной, $n. \"Небесный огонь\" готов к отправлению.', 12, 0, 100, 1, 0, 57559, 99483, 0, 0, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` VALUES (96644, 1, 0, 'В Штормхейме покоится Эгида Агграмара, один из Столпов Созидания.', 12, 0, 100, 0, 0, 53149, 98604, 0, 0, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` VALUES (96644, 2, 0, 'По легенде, ее может получить лишь тот, кто сумеет пройти особое испытание.', 12, 0, 100, 0, 0, 53150, 98605, 0, 0, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` VALUES (96644, 3, 0, 'Впечатляет, правда? Из новых орудий можно сбить виверну с трехсот метров на полном ходу. Взгляни-ка.', 12, 0, 100, 0, 0, 53151, 98607, 0, 0, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` VALUES (96644, 4, 0, 'Нам приказано доставить тебя в Штормхейм и помочь в прохождении испытания.', 12, 0, 100, 0, 0, 53152, 98606, 0, 0, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` VALUES (96644, 5, 0, 'Кроме того, нам поручена и другая задача...', 12, 0, 100, 0, 0, 53153, 98609, 0, 0, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` VALUES (96644, 6, 0, 'Седьмой легион. Самые суровые ребята в Альянсе. Они тоже выполняют личный приказ короля.', 12, 0, 100, 0, 0, 53154, 98610, 0, 0, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` VALUES (96644, 7, 0, 'Три дня назад флот Отрекшихся вышел из Дуротара к Расколотым островам. Мы считаем, что его возглавляет сама Сильвана.', 12, 0, 100, 0, 0, 53155, 98611, 0, 0, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` VALUES (96644, 8, 0, 'Мы должны проследить за этим флотом, но при этом постараться избежать прямых стычек.', 12, 0, 100, 0, 0, 53156, 98612, 0, 0, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` VALUES (96644, 9, 0, 'Хотя, по-моему, они неизбежны.', 12, 0, 100, 0, 0, 53157, 98613, 0, 0, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` VALUES (96644, 10, 0, 'Приготовления почти завершены. Устраивайтесь поудобнее.', 12, 0, 100, 0, 0, 53160, 98616, 0, 0, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` VALUES (96652, 0, 0, '$n, эта сила... переполняет меня.', 12, 0, 100, 0, 0, 55313, 100753, 0, 0, 0, 'Маннетрель Темная Звезда to Player');
INSERT INTO `creature_text` VALUES (96652, 1, 0, 'Aaaaggggghhhhh!', 14, 0, 100, 0, 0, 55312, 100754, 0, 0, 0, 'Маннетрель Темная Звезда to Player');
INSERT INTO `creature_text` VALUES (96653, 0, 0, 'Good luck to all of you. I will get the rest of us up there, $n.', 12, 0, 100, 66, 0, 55228, 99837, 0, 0, 0, 'Изаль Белая Луна to Player');
INSERT INTO `creature_text` VALUES (96654, 0, 0, '$n, you made it through!', 14, 0, 100, 22, 0, 55054, 99836, 0, 0, 0, 'Белат Клинок Рассвета to Player');
INSERT INTO `creature_text` VALUES (96655, 0, 0, 'Last I saw, Kor\'vas cut a swath of destruction on her way across the bridge.', 12, 0, 100, 1, 0, 55010, 98848, 0, 0, 0, 'Аллари Пожирательница Душ to Player');
INSERT INTO `creature_text` VALUES (96655, 1, 0, 'Демоническая энергия... Я чувствую, как стала сильнее.', 12, 0, 100, 0, 0, 55006, 100731, 0, 0, 0, 'Аллари Пожирательница Душ to Player');
INSERT INTO `creature_text` VALUES (96657, 0, 0, '|TInterface\\Icons\\spell_fire_ragnaros_molteninferno.blp:20|t$n |cFFFF0000|Hspell:185828|h[разжигает]|h|r выжигателя Скверны!', 16, 0, 100, 0, 0, 0, 96139, 0, 0, 0, 'Иллиана Танцующая с Клинками to Иллиана Танцующая с Клинками');
INSERT INTO `creature_text` VALUES (96658, 0, 0, 'ONCE AGAIN YOU STEAL FROM THE VRYKUL, OUTSIDER!', 12, 0, 100, 0, 0, 0, 97211, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (96658, 1, 1, 'MY TOME HOLDS WISDOM GLEANED FROM A THOUSAND LIFETIMES!', 12, 0, 100, 0, 0, 0, 97212, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (96658, 2, 2, 'ATONE FOR YOUR CRIMES! RETURN THE BOOK TO MY PEOPLE!', 12, 0, 100, 0, 0, 0, 97213, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (96658, 3, 3, 'PERSIST IN PLUNDERING ITS SECRETS AND YOUR LIFE SHALL BE FORFEIT!', 12, 0, 100, 0, 0, 0, 97214, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (96660, 0, 0, 'Our journey should be quick. Odyn is lending his might to pierce the veil of Helya\'s sorcery.', 12, 0, 100, 0, 0, 0, 98295, 0, 0, 0, 'Krutnir to Player');
INSERT INTO `creature_text` VALUES (96663, 0, 0, 'Жду – не дождусь.', 12, 0, 100, 0, 0, 53158, 98614, 0, 0, 0, 'Генн Седогрив to Player');
INSERT INTO `creature_text` VALUES (96663, 1, 0, 'I’m not used to tracking prey so I can let it go later.', 12, 0, 100, 0, 0, 53159, 98615, 0, 0, 0, 'Генн Седогрив to Player');
INSERT INTO `creature_text` VALUES (96665, 0, 0, '%s смотрит на Алтруиса Страдальца.', 16, 0, 100, 0, 0, 0, 99510, 0, 0, 0, 'Кайн Ярость Солнца to Player');
INSERT INTO `creature_text` VALUES (96665, 1, 0, 'Вы все слышали, что сказала Стражница. Только мы можем победить Легион. Мы ДОЛЖНЫ выбраться отсюда живыми.', 12, 0, 100, 0, 0, 57322, 99530, 0, 0, 0, 'Кайн Ярость Солнца to Player');
INSERT INTO `creature_text` VALUES (96665, 2, 0, 'You trust no one and believe in nothing, Altruis. You are a leader with no followers.', 12, 0, 100, 0, 0, 57390, 101999, 0, 0, 0, 'Кайн Ярость Солнца to Player');
INSERT INTO `creature_text` VALUES (96672, 3, 0, 'Не лучший бой в моей жизни. Но теперь сила демона принадлежит тебе.', 12, 0, 100, 0, 0, 57357, 101409, 46967, 46967, 0, 'Циана Ночная Глефа to Player');
INSERT INTO `creature_text` VALUES (96675, 0, 0, 'Подъемник скоро будет здесь. Остальные ждут тебя наверху.', 12, 0, 100, 0, 0, 57355, 99039, 0, 0, 0, 'Аллари Пожирательница Душ to Player');
INSERT INTO `creature_text` VALUES (96682, 0, 0, 'Ко мне, охотники на демонов. Я дарую вам невиданную силу!', 12, 0, 100, 0, 0, 57358, 101410, 0, 0, 0, 'Иммолант to 0');
INSERT INTO `creature_text` VALUES (96682, 1, 0, 'Стражи оставили бы нас здесь гнить до скончания веков. Почему ты на их стороне?', 12, 0, 100, 0, 0, 57359, 101412, 0, 0, 0, 'Иммолант to 0');
INSERT INTO `creature_text` VALUES (96682, 2, 0, '|TInterface\\Icons\\spell_fire_felfirenova:20|t %s применяет заклинание|cFFFF0000|Hspell:199828|h[\"Кольцо Хаоса\"]|h|r!', 41, 0, 100, 0, 0, 0, 103693, 0, 0, 0, 'Иммолант to 0');
INSERT INTO `creature_text` VALUES (96682, 3, 0, 'Нет... Не-е-ет! Глупцы.', 12, 0, 100, 0, 0, 57360, 101416, 0, 0, 0, 'Иммолант to Player');
INSERT INTO `creature_text` VALUES (96682, 4, 0, 'Нет... Не-е-ет! Глупцы…', 12, 0, 100, 0, 0, 0, 101416, 0, 0, 0, 'Иммолант to Player');
INSERT INTO `creature_text` VALUES (96683, 0, 0, 'Пора в путь. Флот Темной Госпожи уже почти готов выдвинуться.', 12, 0, 100, 0, 0, 0, 99482, 0, 0, 0, 'Натанос Гнилостень to Player');
INSERT INTO `creature_text` VALUES (96686, 0, 0, 'Are you familiar with the vrykul? Towering, brutish warriors from an ancient stock. Subtlety is not a value they hold in high regard.', 12, 0, 100, 0, 0, 0, 98641, 0, 0, 0, 'Леди Сильвана Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (96686, 1, 0, 'Their legends speak of the Aegis of Aggramar. To claim it, one must undergo some kind of trial. One must be... worthy.', 12, 0, 100, 0, 0, 0, 98646, 0, 0, 0, 'Леди Сильвана Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (96686, 2, 0, 'The new catapults are faster and more powerful than ever before. I have our goblin allies to thank for that.', 12, 0, 100, 0, 0, 0, 98650, 0, 0, 0, 'Леди Сильвана Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (96686, 3, 0, 'I trust you are more than capable of handling whatever trials stand between you and the Aegis.', 12, 0, 100, 0, 0, 0, 98651, 0, 0, 0, 'Леди Сильвана Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (96686, 4, 0, 'Truthfully, Stormheim holds another treasure. One that I will be pursuing by my own means.', 12, 0, 100, 0, 0, 0, 98652, 0, 0, 0, 'Леди Сильвана Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (96686, 5, 0, 'Nathanos has been training a new generation of rangers. They represent both our past, and our future.', 12, 0, 100, 0, 0, 0, 98654, 0, 0, 0, 'Леди Сильвана Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (96686, 6, 0, 'I think often about our future, hero. My people cannot reproduce. Are we not destined, then, to wither away eventually?', 12, 0, 100, 0, 0, 0, 98655, 0, 0, 0, 'Леди Сильвана Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (96686, 7, 0, 'No. The Forsaken will never stop fighting. Not while I reign.', 12, 0, 100, 0, 0, 0, 98656, 0, 0, 0, 'Леди Сильвана Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (96686, 8, 0, 'Not when there are still beings out there with the power to stop death.', 12, 0, 100, 0, 0, 0, 98657, 0, 0, 0, 'Леди Сильвана Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (96686, 9, 0, 'Good. Send a bat - tell them we are leaving shortly.', 12, 0, 100, 0, 0, 0, 98660, 0, 0, 0, 'Леди Сильвана Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (96689, 3, 0, 'Вести от капитана Дредвейка, госпожа. Он нашел храм.', 12, 0, 100, 1, 0, 53174, 98658, 941, 941, 0, 'Жуткий капитан Тэттерсейл to Player');
INSERT INTO `creature_text` VALUES (96690, 1, 0, 'Эти скалы принадлежат дрогбарам!', 12, 0, 100, 0, 0, 54871, 96416, 0, 0, 0, 'Боец из племени Камня Силы to Player');
INSERT INTO `creature_text` VALUES (96690, 1, 0, 'Я выпущу тебе кишки.', 12, 0, 100, 0, 0, 54862, 96414, 0, 0, 0, 'Боец из племени Камня Силы to Player');
INSERT INTO `creature_text` VALUES (96691, 1, 0, 'Шан ронир! Вали к тауренам!', 12, 0, 100, 0, 0, 57968, 107087, 0, 0, 0, 'Дискобол из племени Камня Силы to Player');
INSERT INTO `creature_text` VALUES (96726, 0, 0, 'Стоп! Хорош! Ты... ты не демон...', 12, 0, 100, 0, 0, 0, 98723, 0, 0, 0, 'Сарис Свист Молота to 0');
INSERT INTO `creature_text` VALUES (96726, 1, 0, 'Туман рассеялся, и теперь мои глаза снова видят ясно. Спасибо тебе, |3-6($pc). Загляни при случае в мою старую лавку, и мы с тобой поговорим.', 12, 0, 100, 0, 0, 0, 98725, 0, 0, 0, 'Сарис Свист Молота to 0');
INSERT INTO `creature_text` VALUES (96746, 3, 0, 'Глядя на эту карту, ты можешь отправлять Служителей Земли по всей территории Расколотых островов. Веди нас к победе, $gпредсказатель:предсказательница;!', 12, 0, 100, 6, 0, 0, 0, 98632, 98632, 0, 'Советник Севел to Player');
INSERT INTO `creature_text` VALUES (96746, 3, 0, 'С помощью этой карты ты можешь отправлять Служителей Земли по всей территории Расколотых островов. Веди нас к победе, $gПредсказатель:Предсказательница;!', 12, 0, 100, 6, 0, 0, 101133, 83851, 83851, 0, 'Советник Севел to Player');
INSERT INTO `creature_text` VALUES (96747, 0, 0, 'Shush, you! Dis wood be wet and me hands be shakin\'.', 12, 0, 100, 0, 0, 0, 99108, 0, 0, 0, 'Бат\'ра Страж Ветра to Player');
INSERT INTO `creature_text` VALUES (96754, 0, 0, 'The spirits grow hungry from the scent of blood.', 14, 0, 100, 0, 0, 0, 102201, 0, 0, 0, 'Харбарон to Player');
INSERT INTO `creature_text` VALUES (96754, 1, 0, 'New passengers... New souls. I will guide you to the depths of Helheim for a small price: your flesh and blood.', 14, 0, 100, 0, 0, 0, 102206, 0, 0, 0, 'Харбарон to Player');
INSERT INTO `creature_text` VALUES (96754, 2, 0, 'Arise, servitor! Claim our blood debt!', 14, 0, 100, 0, 0, 0, 102202, 0, 0, 0, 'Харбарон to 0');
INSERT INTO `creature_text` VALUES (96754, 3, 0, 'If I cannot bleed the payment from you, the spirits will!', 14, 0, 100, 0, 0, 0, 102200, 0, 0, 0, 'Харбарон to Player');
INSERT INTO `creature_text` VALUES (96754, 4, 0, '|TInterface\\Icons\\Ability_Warlock_ImprovedSoulLeech:20|t %s begins to |cFFFF0000|Hspell:194325|h[Fragment]|h|r $n.', 41, 0, 100, 0, 0, 0, 105669, 0, 0, 0, 'Харбарон to Player');
INSERT INTO `creature_text` VALUES (96754, 5, 0, 'Helya will make you pay... In the end, everyone pays...', 14, 0, 100, 0, 0, 0, 102211, 0, 0, 0, 'Харбарон to Player');
INSERT INTO `creature_text` VALUES (96756, 0, 0, 'Your souls will drift for eternity in the tides of the underworld!', 14, 0, 100, 0, 0, 0, 102193, 0, 0, 0, 'Ymiron, the Fallen King');
INSERT INTO `creature_text` VALUES (96756, 1, 0, 'Another drop in the sea of souls!', 14, 0, 100, 0, 0, 0, 102185, 0, 0, 0, 'Ymiron, the Fallen King');
INSERT INTO `creature_text` VALUES (96756, 2, 0, 'Mortals... Your wretched kind murdered MY people... destroyed MY kingdom! Long have I waited for a chance to repay what was done to me!', 14, 0, 100, 0, 0, 0, 102189, 0, 0, 0, 'Ymiron, the Fallen King');
INSERT INTO `creature_text` VALUES (96756, 3, 0, 'CRAVEN MAGGOTS!', 14, 0, 100, 0, 0, 0, 102176, 0, 0, 0, 'Ymiron, the Fallen King');
INSERT INTO `creature_text` VALUES (96756, 3, 1, 'FLEE, COWARDS!', 14, 0, 100, 0, 0, 0, 102175, 0, 0, 0, 'Ymiron, the Fallen King');
INSERT INTO `creature_text` VALUES (96756, 3, 2, 'COWER BEFORE ME!', 14, 0, 100, 0, 0, 0, 102174, 0, 0, 0, 'Ymiron, the Fallen King');
INSERT INTO `creature_text` VALUES (96756, 4, 0, 'Know the bitter chill of death as I have!', 14, 0, 100, 0, 0, 0, 102177, 0, 0, 0, 'Ymiron, the Fallen King');
INSERT INTO `creature_text` VALUES (96756, 4, 1, 'The winds of the frozen north still howl in these veins!', 14, 0, 100, 0, 0, 0, 102178, 0, 0, 0, 'Ymiron, the Fallen King');
INSERT INTO `creature_text` VALUES (96756, 5, 0, 'The shadows of the underworld bend to me!', 14, 0, 100, 0, 0, 0, 102171, 0, 0, 0, 'Ymiron, the Fallen King');
INSERT INTO `creature_text` VALUES (96756, 6, 0, 'The shadows of Helheim cut deeper than steel!', 14, 0, 100, 0, 0, 0, 102173, 0, 0, 0, 'Ymiron, the Fallen King');
INSERT INTO `creature_text` VALUES (96756, 7, 0, 'You have not won... The Naglfar comes... for you...', 14, 0, 100, 0, 0, 0, 102180, 0, 0, 0, 'Ymiron, the Fallen King');
INSERT INTO `creature_text` VALUES (96757, 0, 0, 'Помоги ей! Силы ее оставляют!', 12, 0, 100, 0, 0, 0, 99104, 0, 0, 0, 'Иэн Огнебород to Player');
INSERT INTO `creature_text` VALUES (96757, 1, 0, 'Спасибо, шаман.', 12, 0, 100, 0, 0, 0, 99105, 0, 0, 0, 'Иэн Огнебород to Player');
INSERT INTO `creature_text` VALUES (96757, 2, 0, 'Моя птица летела бы быстрее, если б ей не пришлось тащить на себе твою тушу!', 12, 0, 100, 0, 0, 0, 99117, 0, 0, 0, 'Иэн Огнебород to Player');
INSERT INTO `creature_text` VALUES (96757, 3, 0, 'Громовой Молот!', 12, 0, 100, 0, 0, 0, 99130, 0, 0, 0, 'Иэн Огнебород to Player');
INSERT INTO `creature_text` VALUES (96758, 0, 0, 'Громовой Молот!', 12, 0, 100, 15, 0, 60543, 99130, 0, 0, 0, 'Маккей Огнебород to Player');
INSERT INTO `creature_text` VALUES (96758, 3, 0, 'Говорил я, что твоя дурацкая птица летит слишком медленно!', 12, 0, 100, 1, 0, 60545, 99116, 89627, 89627, 0, 'Маккей Огнебород to Player');
INSERT INTO `creature_text` VALUES (96758, 3, 0, 'Давай! Обрушь его!', 12, 0, 100, 5, 0, 57558, 0, 277307, 277307, 0, 'Маккей Огнебород to Grapple Hook Bunny');
INSERT INTO `creature_text` VALUES (96759, 0, 0, 'You ALL will regret trespassing in my realm.', 14, 0, 100, 0, 0, 0, 102212, 0, 0, 0, 'Хелия to Player');
INSERT INTO `creature_text` VALUES (96759, 1, 0, 'You have come far enough. Time to die.', 14, 0, 100, 0, 0, 0, 102213, 0, 0, 0, 'Хелия to Player');
INSERT INTO `creature_text` VALUES (96759, 2, 0, 'Your fate rains down!', 14, 0, 100, 0, 0, 0, 102217, 0, 0, 0, 'Хелия to 0');
INSERT INTO `creature_text` VALUES (96759, 3, 0, 'You will pay for that.', 14, 0, 100, 0, 0, 0, 102221, 0, 0, 0, 'Хелия to Хелия');
INSERT INTO `creature_text` VALUES (96759, 4, 0, 'I am losing my patience.', 14, 0, 100, 0, 0, 0, 102222, 0, 0, 0, 'Хелия to Хелия');
INSERT INTO `creature_text` VALUES (96759, 5, 0, 'Embrace your fate, fools!', 14, 0, 100, 0, 0, 0, 102228, 0, 0, 0, 'Хелия to Хелия');
INSERT INTO `creature_text` VALUES (96759, 6, 0, 'No! This is not possible!', 14, 0, 100, 0, 0, 0, 102223, 0, 0, 0, 'Хелия to Хелия');
INSERT INTO `creature_text` VALUES (96759, 7, 0, 'What manner of creatures ARE you??', 14, 0, 100, 0, 0, 0, 102227, 0, 0, 0, 'Хелия to Хелия');
INSERT INTO `creature_text` VALUES (96759, 8, 0, '|TInterface\\Icons\\inv_misc_leatherscrap_17.blp:20|t %s не видит рядом с собой ни одного противника и начинает использовать заклинание|cFFFF0000|Hspell:198520|h[\"Взмах крылом\"]|h|r!', 41, 0, 100, 0, 0, 0, 103183, 0, 0, 0, 'Хелия to 0');
INSERT INTO `creature_text` VALUES (96759, 9, 0, 'Do you think you have won? You have merely survived the storm... The seas are unstoppable.', 14, 0, 100, 0, 0, 0, 102661, 0, 0, 0, 'Хелия to 0');
