INSERT INTO `creature_text` VALUES (96774, 1, 0, 'Нет!', 12, 0, 100, 0, 0, 72571, 135514, 0, 0, 0, 'Грабитель могил из племени Синего Воска to Player');
INSERT INTO `creature_text` VALUES (96774, 1, 0, 'Ракинишу!', 12, 0, 100, 0, 0, 54336, 102747, 0, 0, 0, 'Грабитель могил из племени Синего Воска to Player');
INSERT INTO `creature_text` VALUES (96774, 1, 0, 'Свечка моя-я-я-я!', 12, 0, 100, 0, 0, 54343, 102754, 0, 0, 0, 'Грабитель могил из племени Синего Воска to Player');
INSERT INTO `creature_text` VALUES (96778, 0, 0, 'Tell me if you need my help, |3-6($c).', 12, 0, 100, 3, 0, 0, 43342, 0, 0, 0, 'Эмара to Player');
INSERT INTO `creature_text` VALUES (96778, 3, 0, 'Well Met!', 12, 0, 100, 3, 0, 0, 53806, 11754, 11754, 0, 'Эмара to Player');
INSERT INTO `creature_text` VALUES (96778, 3, 0, 'Приветствую вас! Пожалуйста, осмотрите мои товары.', 12, 0, 100, 3, 0, 0, 43341, 9214678, 9214678, 0, 'Эмара to Player');
INSERT INTO `creature_text` VALUES (96778, 3, 0, 'Приветствую тебя, |3-6($c).', 12, 0, 100, 3, 0, 0, 33548, 97395, 97395, 0, 'Эмара to Player');
INSERT INTO `creature_text` VALUES (96782, 0, 0, 'Nothing to share, I\'m afraid. The wine gets people talking, but sometimes they just don\'t have anything useful to say.', 12, 0, 100, 0, 0, 0, 108120, 0, 0, 0, 'Люсьен Триас to Player');
INSERT INTO `creature_text` VALUES (96782, 1, 0, 'Say, care for some Moist Azsunian Feta while you\'re here? It has a lovely tangy flavor!', 12, 0, 100, 0, 0, 0, 108121, 0, 0, 0, 'Люсьен Триас to Player');
INSERT INTO `creature_text` VALUES (96783, 0, 0, 'Удивительно, что вы вообще дошли сюда, охотники на демонов. А теперь я погашу ваши души, словно свечи!', 12, 0, 100, 0, 0, 57362, 99652, 0, 0, 0, 'Bastillax to Player');
INSERT INTO `creature_text` VALUES (96783, 1, 0, 'Какая наглость... Думаете, у вас есть шанс выстоять против Легиона?', 12, 0, 100, 0, 0, 57385, 103145, 0, 0, 0, 'Bastillax to 0');
INSERT INTO `creature_text` VALUES (96783, 2, 0, 'Нет... Не-е-ет! Вам не видать свободы…', 12, 0, 100, 0, 0, 57383, 101957, 0, 0, 0, 'Bastillax to Player');
INSERT INTO `creature_text` VALUES (96784, 3, 0, 'А вот и $n... ты глянь, какие очки!', 12, 0, 100, 1, 0, 0, 121048, 1048567, 1048567, 0, 'Флалла Сладкоягода to Player');
INSERT INTO `creature_text` VALUES (96785, 3, 0, 'Как там дела, $n? Посетители приходят и уходят, но никто толком ничего не знает.', 12, 0, 100, 1, 0, 0, 129486, 9596, 9596, 0, 'Кристи Стоктон to Player');
INSERT INTO `creature_text` VALUES (96785, 3, 0, 'Осторожнее, $n, не споткнись! Есть новости от Совета?', 12, 0, 100, 1, 0, 0, 129489, 2799, 2799, 0, 'Кристи Стоктон to Player');
INSERT INTO `creature_text` VALUES (96785, 3, 0, 'Что там происходит? Шумят все громче и громче. Надеюсь, что скоро мы узнаем хоть что-нибудь.', 12, 0, 100, 1, 0, 0, 129488, 415772, 415772, 0, 'Кристи Стоктон to Player');
INSERT INTO `creature_text` VALUES (96786, 0, 0, 'I haven\'t thought of that since... well since an old friend asked about it shortly before he was taken from this world.', 12, 0, 100, 0, 0, 0, 126045, 0, 0, 0, 'Верховный маг Селиндра to player');
INSERT INTO `creature_text` VALUES (96786, 1, 0, 'After Antonidas died, I decided to hide the disc away. There was no reason to continue my research into it.', 12, 0, 100, 0, 0, 0, 126046, 0, 0, 0, 'Верховный маг Селиндра to player');
INSERT INTO `creature_text` VALUES (96786, 2, 0, 'But if you think you can finish what Antonidas started, then the piece is yours. Good luck with it, Archmage $n.', 12, 0, 100, 0, 0, 0, 126047, 0, 0, 0, 'Верховный маг Селиндра to player');
INSERT INTO `creature_text` VALUES (96800, 1, 0, 'Свеча! Мое!', 12, 0, 100, 0, 0, 54340, 102745, 0, 0, 0, 'Черепоносец из племени Синего Воска to Player');
INSERT INTO `creature_text` VALUES (96806, 0, 0, 'Классно выглядишь, $n!', 12, 0, 100, 1, 0, 0, 121047, 0, 0, 0, 'Амиши Лазурный Взгляд to Player');
INSERT INTO `creature_text` VALUES (96806, 1, 0, 'Тьфу на тебя, $n! Снова щеголяешь в своих крутых очках?! Прекрасно!', 12, 0, 100, 1, 0, 0, 121046, 0, 0, 0, 'Амиши Лазурный Взгляд to Player');
INSERT INTO `creature_text` VALUES (96806, 2, 0, 'А вот и $n... ты глянь, какие очки!', 12, 0, 100, 1, 0, 0, 121048, 0, 0, 0, 'Амиши Лазурный Взгляд to Player');
INSERT INTO `creature_text` VALUES (96806, 3, 0, 'Не знаете, $n встречается с кем-нибудь? У меня есть шансы?', 12, 0, 100, 1, 0, 0, 121225, 0, 0, 0, 'Амиши Лазурный Взгляд to Player');
INSERT INTO `creature_text` VALUES (96808, 3, 0, '$n! Is it true you wield the Kingslayers? When I was just a girl, my gran told me the stories. The King of Stormwind grasping at his throat, red blood spattered on the white stones of the keep... I would hide under my covers at night, thinking assassins were everywhere.', 12, 0, 100, 5, 0, 0, 111910, 643325, 720164, 0, 'Сандра Бартан to Player');
INSERT INTO `creature_text` VALUES (96813, 0, 0, 'Мой лучший гиппогриф идеально подойдет для этой задачи.', 12, 0, 100, 0, 0, 0, 102319, 0, 0, 0, 'Алудан Белое Облако to Player');
INSERT INTO `creature_text` VALUES (96817, 0, 0, 'Ну и какая разница? В хранилище собраны наши самые ценные реликвии! Пропажа любой из них – катастрофа!', 12, 0, 100, 5, 0, 0, 125605, 0, 0, 0, 'Служащий банка Реймс to Player');
INSERT INTO `creature_text` VALUES (96818, 0, 0, 'Мы даже не знаем, миновала ли опасность, а ты спрашиваешь про хранилище?! Туда допускаются только члены Совета Шестерых!', 12, 0, 100, 1, 0, 0, 125606, 0, 0, 0, 'Служащая банка Ханнерс to Player');
INSERT INTO `creature_text` VALUES (96819, 0, 0, 'Боюсь, мы об этом узнаем еще не скоро. Внутренние помещения все еще объяты пламенем Скверны!', 12, 0, 100, 1, 0, 0, 125607, 0, 0, 0, 'Служащий банка Алтиеллис to Player');
INSERT INTO `creature_text` VALUES (96836, 0, 0, 'А вот и $n... ты глянь, какие очки!', 12, 0, 100, 1, 0, 0, 121048, 0, 0, 0, 'Волхв Фэнхзи Добродел to Player');
INSERT INTO `creature_text` VALUES (96842, 0, 0, 'Is $n seeing anyone? Do you think I\'d have a chance?', 12, 0, 100, 1, 0, 0, 121225, 0, 0, 0, 'Верховный маг Тенаж to Player');
INSERT INTO `creature_text` VALUES (96847, 0, 0, 'Надеюсь, Майев не совершила ошибку, освободив тебя, $gохотник на демонов:охотница на демонов;. Иди вперед, я задержу врага.', 12, 0, 100, 0, 0, 57291, 98753, 0, 0, 0, 'Дреланим Шелест Ветра to Player');
INSERT INTO `creature_text` VALUES (96853, 1, 0, 'Что ты делай здесь?! Все свечи – мои свечи! Видишь большие рога?!', 12, 0, 100, 0, 0, 0, 98865, 0, 0, 0, 'Свечной король to Player');
INSERT INTO `creature_text` VALUES (96853, 2, 0, 'Ты... не... брать... свечу...', 12, 0, 100, 0, 0, 0, 98866, 0, 0, 0, 'Свечной король to Player');
INSERT INTO `creature_text` VALUES (96853, 4, 0, 'Стой, ты не настоящий кобольд!', 12, 0, 100, 0, 0, 0, 98867, 18240, 18240, 0, 'Свечной король');
INSERT INTO `creature_text` VALUES (96877, 0, 0, 'Меня прислал военачальник.', 12, 0, 100, 0, 0, 55036, 99730, 0, 0, 0, 'Пеплоуст-ловец to Player');
INSERT INTO `creature_text` VALUES (96877, 0, 0, 'Мы сокрушим Пылающий Легион.', 12, 0, 100, 0, 0, 55037, 99731, 0, 0, 0, 'Пеплоуст-ловец to Player');
INSERT INTO `creature_text` VALUES (96877, 1, 0, 'I came to help you.', 12, 0, 100, 0, 0, 55041, 99735, 0, 0, 0, 'Пеплоуст-ловец to Player');
INSERT INTO `creature_text` VALUES (96884, 0, 0, 'I came to your aid.', 12, 0, 100, 0, 0, 55083, 99744, 0, 0, 0, 'Заклинательница моря из клана Змеиных Колец to Player');
INSERT INTO `creature_text` VALUES (96884, 0, 0, 'Мы не позволим Легиону уничтожить наш мир.', 12, 0, 100, 0, 0, 55085, 99746, 0, 0, 0, 'Заклинательница моря из клана Змеиных Колец to Player');
INSERT INTO `creature_text` VALUES (96884, 1, 0, 'Пылающий Легион будет уничтожен.', 12, 0, 100, 0, 0, 55079, 99740, 0, 0, 0, 'Заклинательница моря из клана Змеиных Колец to Player');
INSERT INTO `creature_text` VALUES (96888, 0, 0, 'Вместе мы разгромим Легион.', 12, 0, 100, 0, 0, 55353, 99749, 0, 0, 0, 'Шиваррская разрушительница to Player');
INSERT INTO `creature_text` VALUES (96888, 0, 0, 'Я буду сеять смерть.', 12, 0, 100, 0, 0, 55361, 99753, 0, 0, 0, 'Шиваррская разрушительница to Player');
INSERT INTO `creature_text` VALUES (96888, 1, 0, 'Верховная мать приказала помочь тебе.', 12, 0, 100, 0, 0, 55359, 99748, 0, 0, 0, 'Шиваррская разрушительница to Player');
INSERT INTO `creature_text` VALUES (96928, 0, 0, 'Сработало! $GОн жив:Она жива;!', 12, 0, 100, 71, 0, 0, 98740, 0, 0, 0, 'Аптекарь \"Черной Розы\" to Player');
INSERT INTO `creature_text` VALUES (96967, 1, 0, 'Да, конечно, я могу постирать это платье. Только я беру 2 золотых за работу.', 12, 0, 0, 0, 0, 0, 101095, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (96967, 2, 0, 'Славно. Сейчас я с него мигом всю грязь отстираю!', 12, 0, 0, 0, 0, 0, 101096, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (96967, 3, 0, 'Давай, Акван. Твой выход.', 12, 0, 0, 0, 0, 0, 101110, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (96967, 4, 0, 'Вот твое платье. Смотри – ни пятнышка; прямо как новое! Спасибо тебе за помощь.', 12, 0, 0, 0, 0, 0, 101097, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (96979, 0, 0, 'Well Met!', 12, 0, 100, 0, 0, 0, 53806, 0, 0, 0, 'Брагунд Яркая Кольчуга to Player');
INSERT INTO `creature_text` VALUES (96979, 1, 1, 'Ты что, думаешь, тебе кто-нибудь что-нибудь даст за здорово живешь? Извиняй, дорогуша, но услуга за услугу.', 12, 0, 0, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (96979, 1, 2, 'Я могу сменять тебе лом на несколько новых манекенов для доспехов. У Иминдрил Песни Копья в лавке \"Танки – наше все\" как раз должна найтись парочка.', 12, 0, 0, 0, 0, 0, 101078, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (96979, 1, 3, 'Принеси их мне, и лом твой.', 12, 0, 0, 0, 0, 0, 101079, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (96979, 4, 0, 'А ты не $gторопился!:торопилась!; Что, небось, на обратном пути остановилась пропустить кружечку? Ладно, вот тебе лом, как мы и договаривались.', 12, 0, 0, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (96984, 3, 0, 'Помоги нам, $gжрец:жрица;... Твой посох сияет невиданным светом...', 12, 0, 100, 20, 0, 0, 123338, 34322, 34322, 0, 'Эния Небесный Рог to Player');
INSERT INTO `creature_text` VALUES (96990, 3, 0, 'Yablochkin notices G\'khanir and throws you a fresh apple!', 16, 0, 100, 0, 0, 0, 101456, 29566, 29566, 196268, 'Яблочкин to Player');
INSERT INTO `creature_text` VALUES (96990, 3, 0, 'Yablochkin notices the Incinerator and throws you an apple!', 16, 0, 100, 0, 0, 0, 0, 4486, 4486, 196268, 'Яблочкин to Player');
INSERT INTO `creature_text` VALUES (96997, 1, 0, 'Твои усилия напрасны, жалкое существо. Повелитель Гул\'дан видит все.', 12, 0, 100, 0, 0, 57341, 98754, 0, 0, 0, 'Кетразор to Player');
INSERT INTO `creature_text` VALUES (96997, 2, 0, 'Легион все равно победит...', 12, 0, 100, 0, 0, 57368, 101222, 0, 0, 0, 'Кетразор to Player');
INSERT INTO `creature_text` VALUES (97004, 0, 0, 'Ваш заказ в секретной комнате, $gгосподин:госпожа;.', 12, 0, 100, 1, 0, 0, 108799, 0, 0, 0, '\"Рыжий\" Джек Финдл to Player');
INSERT INTO `creature_text` VALUES (97004, 1, 0, 'And we will answer from the shadows.', 12, 0, 100, 1, 0, 0, 108811, 0, 0, 0, '\"Рыжий\" Джек Финдл to Player');
INSERT INTO `creature_text` VALUES (97005, 0, 0, 'Well Met.', 12, 0, 100, 3, 0, 0, 116569, 0, 0, 0, 'Дебби Мур to Player');
INSERT INTO `creature_text` VALUES (97005, 1, 0, 'Yes, of course. There they are, on the table. Why, is there something wrong with them?', 12, 0, 100, 1, 0, 0, 102674, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (97005, 1, 0, 'Да, конечно. Вон они, на столе. А что, с ними что-то не так?', 12, 0, 100, 1, 0, 0, 102674, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (97009, 3, 0, 'Не знаете, $n встречается с кем-нибудь? У меня есть шансы?', 12, 0, 100, 1, 0, 0, 121225, 36739, 36739, 0, 'Джонни to Player');
INSERT INTO `creature_text` VALUES (97014, 0, 0, 'Kill the Illidari.', 12, 0, 100, 0, 0, 56992, 105112, 0, 0, 0, 'Злобный подчинитель душ to Player');
INSERT INTO `creature_text` VALUES (97027, 0, 0, 'Какой же ты шаман, если не можешь огня разжечь?!', 12, 0, 100, 0, 0, 0, 99107, 0, 0, 0, 'Мать племени Торра to Player');
INSERT INTO `creature_text` VALUES (97027, 1, 0, 'На открытом месте Легион нас уничтожит. Инферналы орудуют даже в Громовом Утесе.', 12, 0, 100, 0, 0, 0, 99109, 0, 0, 0, 'Мать племени Торра to Player');
INSERT INTO `creature_text` VALUES (97027, 2, 0, 'Over here, shaman! Take up my spear. The winds will guide your strike!', 12, 0, 100, 0, 0, 0, 99131, 0, 0, 0, 'Мать племени Торра to 1038');
INSERT INTO `creature_text` VALUES (97028, 0, 0, 'Спасибо, $gиноземец:чужестранка;. Я сейчас же отправлюсь на вершину.', 12, 0, 100, 0, 0, 0, 98832, 0, 0, 0, 'Алейфир to Player');
INSERT INTO `creature_text` VALUES (97031, 3, 0, 'Уж не знаю, что тебя сюда привело, но все равно – спасибо.', 12, 0, 100, 0, 0, 0, 98833, 1353, 1353, 0, 'Храфсир to Player');
INSERT INTO `creature_text` VALUES (97032, 3, 0, 'Эти гнусные демоны заплатят за свое коварство!', 12, 0, 100, 15, 0, 0, 98834, 1623, 1623, 0, 'Эрилар to Player');
INSERT INTO `creature_text` VALUES (97033, 3, 0, 'Мне изрядно досталось... Спасибо.', 12, 0, 100, 0, 0, 0, 98835, 1306, 1306, 0, 'Хридмогир to Player');
INSERT INTO `creature_text` VALUES (97034, 0, 0, 'Сейчас ты умрешь.', 12, 0, 100, 0, 0, 0, 98765, 0, 0, 0, 'Гневный защитник to Player');
INSERT INTO `creature_text` VALUES (97043, 0, 0, 'Молчать! Теперь твоя душа принадлежит Хелии.', 14, 0, 100, 0, 0, 0, 100494, 0, 0, 0, 'Проклятый морем работорговец to Player');
INSERT INTO `creature_text` VALUES (97043, 1, 0, 'Хочешь жить? Тогда убей этих глупцов.', 14, 0, 100, 0, 0, 0, 104366, 0, 0, 0, 'Проклятый морем работорговец to 0');
INSERT INTO `creature_text` VALUES (97043, 2, 0, 'Болван. Наше проклятие тебя погубит...', 14, 0, 100, 0, 0, 0, 100515, 0, 0, 0, 'Проклятый морем работорговец to Player');
INSERT INTO `creature_text` VALUES (97043, 3, 0, 'Сражайся с ними. Заслужи свою свободу!', 14, 0, 100, 0, 0, 0, 104438, 0, 0, 0, 'Проклятый морем работорговец to 0');
INSERT INTO `creature_text` VALUES (97044, 0, 0, 'Бежать некуда.', 12, 0, 100, 0, 0, 57377, 37177, 0, 0, 0, 'Инквизитор to Кассиель Ночной Шип');
INSERT INTO `creature_text` VALUES (97052, 3, 0, 'The trees whisper his name, but their words are madness to my ears!', 12, 0, 100, 0, 0, 58050, 107174, 107179, 107179, 0, 'Элотир to Player');
INSERT INTO `creature_text` VALUES (97052, 3, 0, 'Деревья шепчут его имя, но их слова – безумие для моих ушей!', 12, 0, 100, 0, 0, 58050, 107174, 22651, 22651, 0, 'Элотир to Player');
INSERT INTO `creature_text` VALUES (97052, 3, 0, 'Тебе это не по силам, но, может, твоей богине...', 12, 0, 100, 0, 0, 58049, 107176, 1159697, 1159697, 0, 'Элотир to Player');
INSERT INTO `creature_text` VALUES (97056, 1, 0, 'Никчемным смертным не одолеть меня! Я – Хранитель Земли!', 12, 0, 100, 0, 0, 54866, 98359, 0, 0, 0, 'Видение Смертокрыла to Player');
INSERT INTO `creature_text` VALUES (97059, 0, 0, 'My queen\'s brood will hatch soon.', 14, 0, 100, 0, 0, 55283, 103500, 0, 0, 0, 'Король Ворас to 0');
INSERT INTO `creature_text` VALUES (97059, 2, 0, 'They\'ve slain me, my queen...', 14, 0, 100, 0, 0, 55282, 99206, 0, 0, 0, 'Король Ворас to Player');
INSERT INTO `creature_text` VALUES (97059, 3, 0, 'Want your little, broken draenei back?', 12, 0, 100, 0, 0, 55281, 99173, 0, 0, 0, 'Король Ворас to Player');
INSERT INTO `creature_text` VALUES (97059, 5, 0, '|TInterface\\Icons\\inv_misc_monsterspidercarapace_01:20|tНаходящиеся неподалеку |cFFFF0000|Hspell:198235|h[паучьи яйца]|h|r вскоре вылупятся, если их не разбить!', 41, 0, 100, 0, 0, 55283, 103110, 0, 0, 0, 'Король Ворас to 0');
INSERT INTO `creature_text` VALUES (97061, 0, 0, 'Ты не из Штормхейма. Но это не помешало тебе спасти нас от страшной участи.', 12, 0, 100, 1, 0, 53036, 98429, 0, 0, 0, 'Тримьярис to Player');
INSERT INTO `creature_text` VALUES (97061, 3, 0, 'Подними же свое оружие и прими силу шторма в награду за свои подвиги.', 12, 0, 100, 1, 0, 53038, 98431, 15892, 15892, 0, 'Тримьярис to Player');
INSERT INTO `creature_text` VALUES (97061, 3, 0, 'Теперь мы твои друзья. Испытание воли пройдено.', 12, 0, 100, 1, 0, 53037, 98430, 8323, 8323, 0, 'Тримьярис to Player');
INSERT INTO `creature_text` VALUES (97064, 1, 0, 'Бррибитит!', 12, 0, 100, 467, 0, 54344, 102755, 0, 0, 0, 'Грязнорыл-падальщик to Player');
