INSERT INTO `creature_text` VALUES (97070, 0, 0, 'Кайн и Алтруис уже отправились наверх. Седлай этого сквернотопыря и догоняй их.', 12, 0, 100, 0, 0, 57299, 98783, 0, 0, 0, 'Джейс Темный Ткач to Player');
INSERT INTO `creature_text` VALUES (97072, 0, 0, 'I put so much strength and health in this forge, and at least someone would say thank you for all this time! No!', 12, 0, 100, 0, 0, 0, 98801, 0, 0, 0, 'Главный кузнец крепости Корвий to 0');
