INSERT INTO `creature_text` VALUES (97093, 4, 0, 'Тотем Скверны... тебя... уничтожит.', 12, 0, 100, 0, 0, 0, 98910, 21903, 21903, 0, 'Шарра Дыхание Скверны to Player');
INSERT INTO `creature_text` VALUES (97095, 0, 0, 'Розалин! Где Розалин?!', 14, 0, 100, 0, 0, 0, 98841, 0, 0, 0, 'Хранитель душ Урия to Player');
INSERT INTO `creature_text` VALUES (97095, 1, 0, 'Хранитель душ Урия с трудом делает вдох.', 16, 0, 100, 0, 0, 0, 99611, 0, 0, 0, 'Хранитель душ Урия to Player');
INSERT INTO `creature_text` VALUES (97095, 2, 0, 'Розалин, меня уже не спасти, Найди демонов, которых освободили из темниц душ, и снова отправь их в заточение, пока еще не поздно.', 12, 0, 100, 0, 0, 0, 99610, 0, 0, 0, 'Хранитель душ Урия to Player');
INSERT INTO `creature_text` VALUES (97095, 3, 0, 'Получится, Розалин, непременно получится. Ты даже не подозреваешь, насколько ты талантлива.', 12, 0, 100, 0, 0, 0, 99625, 0, 0, 0, 'Хранитель душ Урия to Player');
INSERT INTO `creature_text` VALUES (97095, 4, 0, 'Хранитель душ Урия устало вздыхает, и душа покидает его безжизненное тело.', 16, 0, 100, 0, 0, 0, 99626, 0, 0, 0, 'Хранитель душ Урия to Player');
INSERT INTO `creature_text` VALUES (97097, 0, 0, 'Харбарон не потерпит здесь присутствия смертных.', 14, 0, 100, 0, 0, 0, 105467, 0, 0, 0, 'Хеларьяр-защитник to Player');
INSERT INTO `creature_text` VALUES (97103, 1, 0, 'За Свечной Король!', 12, 0, 100, 0, 0, 54339, 102751, 0, 0, 0, 'Грязнорыл-собиратель to Player');
INSERT INTO `creature_text` VALUES (97103, 1, 0, 'Нет!', 12, 0, 100, 0, 0, 72571, 135514, 0, 0, 0, 'Грязнорыл-собиратель to Player');
INSERT INTO `creature_text` VALUES (97103, 3, 0, 'Ракинишу!', 12, 0, 100, 0, 0, 54336, 102747, 30433, 30433, 0, 'Грязнорыл-собиратель to Player');
INSERT INTO `creature_text` VALUES (97111, 0, 0, 'Это исследование весьма занимательно! Я как раз вписываю переведенную историю в книгу.', 12, 0, 100, 1, 0, 0, 120019, 0, 0, 0, 'Илланна Жуткая Пустошь to Player');
INSERT INTO `creature_text` VALUES (97111, 3, 0, 'Владыка Смерти, нам нужно поговорить.', 12, 0, 100, 396, 0, 0, 0, 1717, 1717, 0, 'Архивариус Дредмур to Player');
INSERT INTO `creature_text` VALUES (97111, 3, 0, 'Главный кузнец Корвий сейчас трудится наверху.', 12, 0, 100, 0, 0, 0, 0, 73107, 307076, 0, 'Архивариус Дредмур to Player');
INSERT INTO `creature_text` VALUES (97111, 3, 0, 'Я буду добавлять новые сведения по мере нашего исследования. Тебе стоит взглянуть, когда будет время.', 12, 0, 100, 1, 0, 0, 120260, 17281, 17281, 0, 'Илланна Жуткая Пустошь to Player');
INSERT INTO `creature_text` VALUES (97132, 0, 0, 'О, с триумфальным возвращением! Тебе пришлось приложить немало усилий, чтобы усвоить все, чему я тебя учил.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Амал\'тазад to Player');
INSERT INTO `creature_text` VALUES (97134, 0, 0, 'А это $n, само воплощение смерти! Моя школа. Когда-нибудь расскажешь мне о приемах, которым научишься в бою.', 12, 0, 100, 603, 0, 0, 0, 0, 0, 0, 'Лорд Торваль to Player');
INSERT INTO `creature_text` VALUES (97136, 0, 0, 'И доброе имя завоевано тобой по праву, $n. Рада, что мои наставления не пропали даром.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Леди Алистра to Player');
INSERT INTO `creature_text` VALUES (97140, 0, 0, 'Ох... Никогда к такому не привыкну.', 12, 0, 100, 0, 0, 67411, 99793, 0, 0, 0, 'Первая колдунья Талисра to Player');
INSERT INTO `creature_text` VALUES (97140, 1, 0, 'Окулет!', 12, 0, 100, 0, 0, 67243, 100883, 0, 0, 0, 'Первая колдунья Талисра to Player');
INSERT INTO `creature_text` VALUES (97140, 2, 0, 'Он иссыхает...', 12, 0, 100, 0, 0, 67244, 104371, 0, 0, 0, 'Первая колдунья Талисра to Player');
INSERT INTO `creature_text` VALUES (97140, 4, 0, 'Низина Анора где-то к северу отсюда. Новый телепорт быстро перенесет тебя на поверхность.', 12, 0, 100, 0, 0, 67483, 104886, 0, 0, 0, 'Первая колдунья Талисра to Player');
INSERT INTO `creature_text` VALUES (97140, 5, 0, 'В твое отсутствие я подготовлю место для содержания подопытных.', 12, 0, 100, 396, 0, 67262, 107469, 0, 0, 0, 'Первая колдунья Талисра to Player');
INSERT INTO `creature_text` VALUES (97140, 6, 0, 'И вот еще что, Вальтруа.', 12, 0, 100, 1, 0, 67255, 105901, 0, 0, 0, 'Первая колдунья Талисра to Player');
INSERT INTO `creature_text` VALUES (97140, 7, 0, 'Советую тебе перестать воротить нос от чужаков. Без их помощи нас бы здесь не было.', 12, 0, 100, 1, 0, 67256, 105902, 0, 0, 0, 'Первая колдунья Талисра to Player');
INSERT INTO `creature_text` VALUES (97140, 8, 0, 'Пора начинать Терин.', 12, 0, 100, 0, 0, 67445, 111647, 0, 0, 0, 'Первая колдунья Талисра to Player');
INSERT INTO `creature_text` VALUES (97140, 9, 0, 'Нар\'\'тала вас манари...', 12, 0, 100, 0, 0, 67446, 111655, 0, 0, 0, 'Первая колдунья Талисра to Player');
INSERT INTO `creature_text` VALUES (97140, 10, 0, 'Я вижу... его воспоминания.', 12, 0, 100, 0, 0, 67447, 112383, 0, 0, 0, 'Первая колдунья Талисра to Player');
INSERT INTO `creature_text` VALUES (97140, 11, 0, 'Когда его изгнали...', 12, 0, 100, 0, 0, 67449, 111663, 0, 0, 0, 'Первая колдунья Талисра to Player');
INSERT INTO `creature_text` VALUES (97140, 12, 0, 'Голод... Страх...', 12, 0, 100, 0, 0, 67452, 111664, 0, 0, 0, 'Первая колдунья Талисра to Player');
INSERT INTO `creature_text` VALUES (97140, 13, 0, 'I feel...', 12, 0, 100, 0, 0, 67453, 111662, 0, 0, 0, 'Первая колдунья Талисра to Player');
INSERT INTO `creature_text` VALUES (97140, 14, 0, 'Продолжим.', 12, 0, 100, 0, 0, 67454, 111812, 0, 0, 0, 'Первая колдунья Талисра to Player');
INSERT INTO `creature_text` VALUES (97140, 15, 0, 'Тала нар\'\'валас.', 12, 0, 100, 0, 0, 67455, 112341, 0, 0, 0, 'Первая колдунья Талисра to Player');
INSERT INTO `creature_text` VALUES (97140, 16, 0, 'Какое смятение... Какая ярость...', 12, 0, 100, 0, 0, 67456, 111667, 0, 0, 0, 'Первая колдунья Талисра to Player');
INSERT INTO `creature_text` VALUES (97140, 17, 0, 'I ... I\'m not doing it ...', 12, 0, 100, 0, 0, 67457, 111814, 0, 0, 0, 'Первая колдунья Талисра to Player');
INSERT INTO `creature_text` VALUES (97140, 18, 0, 'Аа-а! Прекратите! Пусть они прекратят!', 12, 0, 100, 0, 0, 67459, 111608, 0, 0, 0, 'Первая колдунья Талисра to Player');
INSERT INTO `creature_text` VALUES (97140, 20, 0, 'Мм-мх... ХВАТИТ!!!', 12, 0, 100, 0, 0, 67461, 112350, 0, 0, 0, 'Первая колдунья Талисра to Player');
INSERT INTO `creature_text` VALUES (97140, 21, 0, 'Тебе ведь... удалось что-то найти?', 12, 0, 100, 0, 0, 67463, 118575, 0, 0, 0, 'Первая колдунья Талисра to Player');
INSERT INTO `creature_text` VALUES (97140, 22, 0, 'Что-то, приносящее благость…', 12, 0, 100, 0, 0, 67464, 118576, 0, 0, 0, 'Первая колдунья Талисра to Player');
INSERT INTO `creature_text` VALUES (97140, 23, 0, 'Благость, которая так нужна нам самим.', 12, 0, 100, 0, 0, 67466, 118583, 0, 0, 0, 'Первая колдунья Талисра to Player');
INSERT INTO `creature_text` VALUES (97140, 24, 0, 'Да... все хорошо.', 12, 0, 100, 0, 0, 67465, 112398, 0, 0, 0, 'Первая колдунья Талисра to Player');
INSERT INTO `creature_text` VALUES (97140, 25, 0, 'Подарок? Мне?', 12, 0, 100, 0, 0, 67591, 113328, 0, 0, 0, 'Первая чародейка Талисра to Player');
INSERT INTO `creature_text` VALUES (97140, 26, 0, 'Талисра тепло улыбается.', 16, 0, 100, 0, 0, 0, 115439, 0, 0, 0, 'Первая чародейка Талисра to Player');
INSERT INTO `creature_text` VALUES (97140, 27, 0, 'Большое спасибо. Твоя помощь бесценна.', 12, 0, 100, 0, 0, 67590, 115440, 0, 0, 0, 'Первая чародейка Талисра to Player');
INSERT INTO `creature_text` VALUES (97141, 3, 0, 'Отлично выглядишь, $n! Просто отлично!', 12, 0, 100, 1, 0, 0, 121224, 10777, 10777, 0, 'Кород to Player');
INSERT INTO `creature_text` VALUES (97166, 0, 0, 'The Val\'kira is approaching! We must respectfully greet her. Come out, heroes!', 12, 0, 100, 396, 0, 0, 0, 0, 0, 0, 'Финна Торбьорнсдоттир to Начинающий герой-врайкул');
INSERT INTO `creature_text` VALUES (97166, 0, 0, 'Приближается валь\'кира! Мы должны почтительно поприветствовать ее. Выходите, герои!', 12, 0, 100, 396, 0, 0, 0, 0, 0, 0, 'Финна Торбьорнсдоттир to Начинающий герой-врайкул');
INSERT INTO `creature_text` VALUES (97171, 0, 0, 'Защита слабеет!', 14, 0, 100, 0, 0, 0, 104567, 0, 0, 0, 'Колдунья из клана Колец Ненависти to Player');
INSERT INTO `creature_text` VALUES (97171, 1, 0, 'Леди Кольцо Ненависти грозит опасность! Она не должна погибнуть!', 14, 0, 100, 0, 0, 0, 104568, 0, 0, 0, 'Колдунья из клана Колец Ненависти to Player');
INSERT INTO `creature_text` VALUES (97181, 1, 0, 'Вам не остановить меня!', 12, 0, 100, 0, 0, 0, 101761, 0, 0, 0, 'Эзерак');
INSERT INTO `creature_text` VALUES (97183, 1, 0, 'Меня не остановить!', 12, 0, 100, 0, 0, 0, 116784, 0, 0, 0, 'Ксориас');
INSERT INTO `creature_text` VALUES (97184, 1, 0, 'I will never return to this crystal!', 12, 0, 100, 0, 0, 0, 101768, 0, 0, 0, 'Лозар');
INSERT INTO `creature_text` VALUES (97184, 1, 0, 'А вот и наш обед пожаловал!', 12, 0, 100, 0, 0, 55203, 96654, 0, 0, 0, 'Лозар to Player');
INSERT INTO `creature_text` VALUES (97193, 0, 0, 'Вместе мы сможем вернуть демонов в темницы.', 12, 0, 100, 0, 0, 72571, 98887, 0, 0, 0, 'Ученица Розалин to Player');
INSERT INTO `creature_text` VALUES (97193, 3, 0, 'Счастливого пути, $n.', 12, 0, 100, 0, 0, 0, 101590, 54841, 54841, 0, 'Ученица Розалин to Player');
INSERT INTO `creature_text` VALUES (97193, 4, 0, 'Демон достаточно слаб. Я смогу провести ритуал заточения. Это не займет много времени.', 12, 0, 100, 0, 0, 0, 98915, 13977, 22306, 0, 'Ученица Розалин to Player');
INSERT INTO `creature_text` VALUES (97193, 4, 0, 'Демон заточен.', 12, 0, 100, 0, 0, 0, 100955, 7524, 10463, 0, 'Ученица Розалин to Player');
INSERT INTO `creature_text` VALUES (97197, 0, 0, 'Ну, все! Мой кулак, твоя морда!', 14, 0, 100, 0, 0, 0, 104680, 0, 0, 0, 'Валарьяр-очиститель to Валарьяр-очиститель');
INSERT INTO `creature_text` VALUES (97200, 0, 0, 'I will flay you!', 14, 0, 100, 0, 0, 0, 105657, 0, 0, 0, 'Проклятый морем тюремщик душ to Зловещий охотник');
INSERT INTO `creature_text` VALUES (97200, 1, 0, 'Vrykul dogs, get over here and fight or I\'ll kill you myself!', 14, 0, 100, 0, 0, 0, 104657, 0, 0, 0, 'Проклятый морем тюремщик душ to 0');
INSERT INTO `creature_text` VALUES (97202, 0, 0, 'Вы не помешаете перерождению Хирьи!', 14, 0, 100, 0, 0, 0, 102975, 0, 0, 0, 'Олмир Просвещенный to 0');
INSERT INTO `creature_text` VALUES (97202, 1, 0, 'Да озарит тебя вечный Свет, Хирья!', 14, 0, 100, 0, 0, 0, 102976, 0, 0, 0, 'Олмир Просвещенный to Player');
INSERT INTO `creature_text` VALUES (97203, 1, 0, 'Сейчас я покажу тебе, что мой огонь уничтожает не только дома, но и жизни!', 12, 0, 100, 0, 0, 0, 98913, 0, 0, 0, 'Тенпак Огненный Тотем to Player');
INSERT INTO `creature_text` VALUES (97203, 4, 0, 'Прах к праху, пепел к пеплу.', 12, 0, 100, 0, 0, 0, 98914, 73472, 73472, 0, 'Тенпак Огненный Тотем to Player');
INSERT INTO `creature_text` VALUES (97215, 1, 0, 'Он в пещере, $p. За мной!', 12, 0, 100, 0, 0, 0, 98944, 0, 0, 0, 'Повелитель зверей Пао\'лек to Player');
INSERT INTO `creature_text` VALUES (97215, 3, 0, 'Спасибо, $p. Арру больше не будет наводить ужас на Громовой Тотем. Наоборот – теперь он принесет нам большую пользу. Можешь забрать себе все, что он унес из города.', 12, 0, 100, 2, 0, 0, 99034, 51985, 51985, 0, 'Повелитель зверей Пао\'лек to Повелитель зверей Пао\'лек');
INSERT INTO `creature_text` VALUES (97215, 4, 0, 'Арру, ты будешь моим!', 12, 0, 100, 0, 0, 0, 98951, 35438, 35438, 0, 'Повелитель зверей Пао\'лек to Арру');
INSERT INTO `creature_text` VALUES (97219, 0, 0, 'Смертные, вы оскверняете ритуал!', 14, 0, 100, 0, 0, 0, 102977, 0, 0, 0, 'Солстен to 0');
INSERT INTO `creature_text` VALUES (97219, 1, 0, 'Хирья... Теперь ты можешь повелевать яростью бури!', 14, 0, 100, 0, 0, 0, 102978, 0, 0, 0, 'Солстен to Player');
INSERT INTO `creature_text` VALUES (97225, 0, 0, 'Мы – лишь первая волна прилива.', 12, 0, 100, 0, 0, 57350, 99861, 0, 0, 0, 'Страж гнева - легат to Освобожденная иллидари');
INSERT INTO `creature_text` VALUES (97225, 1, 0, 'Сопротивление только оттягивает неизбежное...', 12, 0, 100, 0, 0, 57354, 99865, 0, 0, 0, 'Страж гнева - легат to Освобожденный иллидари');
INSERT INTO `creature_text` VALUES (97225, 2, 0, 'Готовьтесь к смерти!', 12, 0, 100, 0, 0, 57352, 99863, 0, 0, 0, 'Страж гнева - легат to Освобожденный иллидари');
INSERT INTO `creature_text` VALUES (97225, 3, 0, 'Пришла ваша гибель.', 12, 0, 100, 0, 0, 57351, 83279, 0, 0, 0, 'Страж гнева - легат to Джейс Темный Ткач');
INSERT INTO `creature_text` VALUES (97225, 5, 0, 'Ваш мир превратится в пепел.', 12, 0, 100, 0, 0, 57355, 99866, 0, 0, 0, 'Страж гнева - легат to Освобожденная иллидари');
INSERT INTO `creature_text` VALUES (97225, 6, 0, 'I am your doom!', 12, 0, 100, 0, 0, 57355, 99864, 0, 0, 0, 'Страж гнева - легат to Освобожденная иллидари');
INSERT INTO `creature_text` VALUES (97225, 7, 0, 'Ты умрешь быстро и в страшных мучениях.', 12, 0, 100, 0, 0, 57353, 101972, 0, 0, 0, 'Страж гнева - легат to Освобожденная иллидари');
INSERT INTO `creature_text` VALUES (97241, 1, 0, 'Я выпущу тебе кишки.', 12, 0, 100, 0, 0, 54862, 96414, 0, 0, 0, 'Ломщик из Подкаменного разлома to Player');
INSERT INTO `creature_text` VALUES (97244, 3, 0, 'Забирай ключ и спускайся к порталу! Нам нужно вернуться в Черный храм!', 14, 0, 100, 0, 0, 55268, 102063, 128986, 128986, 0, 'Кайн Ярость Солнца to Королева стаи Тиранна');
