INSERT INTO `creature_text` VALUES (97675, 0, 0, 'Новые души! Идите сюда, примите свою судьбу!', 14, 0, 100, 0, 0, 0, 99494, 0, 0, 0, 'Марахан to Player');
INSERT INTO `creature_text` VALUES (97675, 1, 0, 'Сдохни!', 14, 0, 100, 0, 0, 0, 82906, 0, 0, 0, 'Марахан to Player');
INSERT INTO `creature_text` VALUES (97676, 0, 0, '$n! Осторожно! В Реакторе Душ тебя ждет повелитель ужаса.', 12, 0, 100, 0, 0, 55304, 99574, 0, 0, 0, 'Леди С\'тэно to Player');
INSERT INTO `creature_text` VALUES (97683, 0, 0, 'Урия! Я здесь! Ты где?', 12, 0, 100, 0, 0, 0, 99585, 0, 0, 0, 'Ученица Розалин to Player');
INSERT INTO `creature_text` VALUES (97683, 3, 0, 'Не могу поверить, что его больше нет…', 12, 0, 100, 0, 0, 0, 99642, 37729, 37729, 0, 'Ученица Розалин to Player');
INSERT INTO `creature_text` VALUES (97683, 3, 0, 'Урия, ты ранен!', 12, 0, 100, 0, 0, 0, 99586, 4006, 4006, 0, 'Ученица Розалин to Player');
INSERT INTO `creature_text` VALUES (97695, 0, 0, 'Ah, my loyal hero.', 12, 0, 100, 0, 0, 0, 98483, 0, 0, 0, 'Леди Сильвана Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (97695, 1, 0, 'You must not languish here. The Aegis cannot fall into enemy hands!', 12, 0, 100, 0, 0, 0, 98484, 0, 0, 0, 'Леди Сильвана Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (97695, 2, 0, 'The future of the Horde depends on you and me. Find your way back to Stormheim - I will be waiting.', 12, 0, 100, 0, 0, 0, 98485, 0, 0, 0, 'Леди Сильвана Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (97706, 0, 0, 'Come closer, my little friend.', 12, 0, 100, 0, 0, 55178, 103519, 0, 0, 0, 'Ткач Скверны to Player');
INSERT INTO `creature_text` VALUES (97706, 1, 0, 'Quickly, kill the demon hunter!', 12, 0, 100, 0, 0, 55185, 103528, 0, 0, 0, 'Ткач Скверны to Player');
INSERT INTO `creature_text` VALUES (97706, 2, 0, 'Your Ashtongue met with an untimely demise.', 12, 0, 100, 0, 0, 55176, 103527, 0, 0, 0, 'Ткач Скверны to Player');
INSERT INTO `creature_text` VALUES (97706, 3, 0, 'Предупредите короля Вораса!', 12, 0, 100, 0, 0, 55175, 103526, 0, 0, 0, 'Ткач Скверны to Player');
INSERT INTO `creature_text` VALUES (97717, 3, 0, 'Давайте, давайте! Ну же!', 14, 0, 100, 22, 0, 0, 99628, 5709, 5709, 0, 'Наставник Код');
INSERT INTO `creature_text` VALUES (97729, 1, 0, 'Познай холод... как мы.', 12, 0, 100, 0, 0, 66053, 116425, 0, 0, 0, 'Потревоженная тень to Player');
INSERT INTO `creature_text` VALUES (97729, 1, 0, 'Танде-ала...', 12, 0, 100, 0, 0, 66044, 116428, 0, 0, 0, 'Потревоженная тень to Player');
INSERT INTO `creature_text` VALUES (97729, 2, 0, 'I have found peace.', 12, 0, 100, 0, 0, 66047, 102391, 0, 0, 0, 'Потревоженная тень to Player');
INSERT INTO `creature_text` VALUES (97729, 2, 0, 'Наконец-то…', 12, 0, 100, 0, 0, 66049, 116419, 0, 0, 0, 'Потревоженная тень to Player');
INSERT INTO `creature_text` VALUES (97729, 2, 0, 'Наш народ... шу далис-на.', 12, 0, 100, 0, 0, 66051, 116421, 0, 0, 0, 'Потревоженная тень to Player');
INSERT INTO `creature_text` VALUES (97729, 2, 0, 'Останови... Элисанду...', 12, 0, 100, 0, 0, 66054, 102390, 0, 0, 0, 'Потревоженная тень to Player');
INSERT INTO `creature_text` VALUES (97729, 2, 0, 'Покой...', 12, 0, 100, 0, 0, 66050, 102397, 0, 0, 0, 'Потревоженная тень to Player');
INSERT INTO `creature_text` VALUES (97729, 3, 0, 'Тебе здесь не место...', 12, 0, 100, 0, 0, 66052, 116427, 28222, 28222, 0, 'Потревоженная тень to Player');
INSERT INTO `creature_text` VALUES (97729, 4, 0, 'Ощути холод могилы!', 12, 0, 100, 0, 0, 66046, 116424, 11496, 11496, 0, 'Потревоженная тень to Player');
INSERT INTO `creature_text` VALUES (97729, 4, 0, 'Святотатство...', 12, 0, 100, 0, 0, 66045, 116426, 1318320, 1318320, 0, 'Потревоженная тень to Player');
INSERT INTO `creature_text` VALUES (97736, 0, 0, 'Stay alert, $n.', 12, 0, 100, 0, 0, 0, 99925, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (97748, 0, 0, 'Just be ready in case anything happens, $n.', 12, 0, 100, 0, 0, 0, 99926, 0, 0, 0, 'Николо Скоропых to Player');
INSERT INTO `creature_text` VALUES (97748, 1, 0, 'Whew, this is a lot harder than it looks.', 12, 0, 100, 0, 0, 0, 99927, 0, 0, 0, 'Николо Скоропых to Player');
INSERT INTO `creature_text` VALUES (97748, 2, 0, 'This is looking pretty good so far. Don`t stop now, $n.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Николо Скоропых to Player');
INSERT INTO `creature_text` VALUES (97748, 3, 0, 'I`m almost finished, I swear.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Николо Скоропых to Player');
INSERT INTO `creature_text` VALUES (97748, 4, 0, 'Success! We did it, $n!', 12, 0, 100, 0, 0, 0, 99939, 0, 0, 0, 'Николо Скоропых to Player');
INSERT INTO `creature_text` VALUES (97750, 1, 0, 'Что такое? Что со мной произошло?!', 12, 0, 100, 0, 0, 65608, 102864, 0, 0, 0, 'Латара to Player');
INSERT INTO `creature_text` VALUES (97750, 2, 0, 'Мой разум... чист. Я посплю… Передай Таэдрису, что я люблю его.', 12, 0, 100, 0, 0, 65609, 102867, 0, 0, 0, 'Латара to Player');
INSERT INTO `creature_text` VALUES (97756, 0, 0, 'So Raethan sent you to do his dirty work for him?', 12, 0, 100, 1, 0, 0, 100154, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (97756, 1, 1, 'To bad it\'ll be your body he\'ll be scraping of the sewer floor!', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (97759, 3, 0, 'Найди меня, когда убьешь Каграксиса! Я ощущаю здесь еще большее зло...', 12, 0, 100, 0, 0, 52194, 97489, 1774, 1774, 0, 'Таон Лунный Коготь to Player');
INSERT INTO `creature_text` VALUES (97762, 0, 0, 'Go ahead and use the books on that table behind me to write your story.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (97766, 0, 0, 'You can use the books on the table ever there to begin writing your story.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (97768, 3, 0, 'В укрытие!', 12, 0, 100, 0, 0, 0, 140099, 2131, 2131, 0, 'Жрец-проклинатель to Player');
INSERT INTO `creature_text` VALUES (97774, 0, 0, 'Не трать силы попусту!', 14, 0, 100, 0, 0, 0, 99833, 0, 0, 0, 'Девятая Цзя to Виззнак');
INSERT INTO `creature_text` VALUES (97774, 1, 0, 'Убирайся, гнусная тварь! Я больше не позволю тебе уничтожать наши священные рукописи!', 14, 0, 100, 0, 0, 0, 100131, 0, 0, 0, 'Девятая Цзя to Player');
INSERT INTO `creature_text` VALUES (97774, 3, 0, 'Спасибо тебе, друг! Мои ученики жизни свои отдали, чтобы защитить эти свитки. В них хроника столетий нашей истории.', 12, 0, 100, 0, 0, 0, 99838, 0, 0, 0, 'Девятая Цзя to Player');
INSERT INTO `creature_text` VALUES (97774, 4, 0, 'Чэнь Буйный Портер вел занятие у своих юных учеников, когда произошло нападение. Он один из сильнейших монахов, но даже он не сможет держаться бесконечно. Скорее, мы должны спасти их.', 12, 0, 100, 0, 0, 0, 99847, 0, 0, 0, 'Девятая Цзя to Player');
INSERT INTO `creature_text` VALUES (97774, 5, 0, 'Плохо дело... Их так много, мы не успеем пробиться!', 12, 0, 100, 0, 0, 0, 99901, 0, 0, 0, 'Девятая Цзя to Player');
INSERT INTO `creature_text` VALUES (97774, 6, 0, 'Сосредоточив энергию ци, я проведу нас сквозь их ряды. Скорее, $n. Возьми меня за руку.', 12, 0, 100, 0, 0, 0, 100145, 0, 0, 0, 'Девятая Цзя to Player');
INSERT INTO `creature_text` VALUES (97774, 7, 0, 'Все хуже, чем я предполагала. $n, здесь нам придется разойтись. Моя помощь нужна в храме. Ученики Чэня должны быть за мостами к северу отсюда.', 12, 0, 100, 0, 0, 0, 100149, 0, 0, 0, 'Девятая Цзя to Player');
INSERT INTO `creature_text` VALUES (97774, 8, 0, 'Да хранит тебя Нефритовый змей, $n.', 12, 0, 100, 0, 0, 0, 100185, 0, 0, 0, 'Девятая Цзя to Player');
INSERT INTO `creature_text` VALUES (97778, 0, 0, '$n, рад снова тебя видеть. Что ж, все в сборе, давайте начнем.', 12, 0, 100, 0, 0, 0, 99767, 0, 0, 0, 'Мастер Хайт to Player');
INSERT INTO `creature_text` VALUES (97778, 1, 0, 'Как вы все знаете, поражение на Расколотом берегу было решающим. Потеря стольких доблестных героев стала для нас большим ударом.', 12, 0, 100, 0, 0, 0, 100182, 0, 0, 0, 'Мастер Хайт to Player');
INSERT INTO `creature_text` VALUES (97778, 2, 0, 'Пылающий легион – серьезная угроза. Мы должны остановить их натиск, прежде чем все, что нам дорого, будет...', 12, 0, 100, 0, 0, 0, 100183, 0, 0, 0, 'Мастер Хайт to Player');
INSERT INTO `creature_text` VALUES (97778, 3, 0, 'Нефритовый змей! Это еще что такое?!', 12, 0, 100, 0, 0, 0, 99776, 0, 0, 0, 'Мастер Хайт to Player');
INSERT INTO `creature_text` VALUES (97778, 4, 0, 'Чуан!', 14, 0, 100, 0, 0, 0, 107053, 0, 0, 0, 'Мастер Хайт to Инфернал-разрушитель');
INSERT INTO `creature_text` VALUES (97778, 5, 0, 'Чуан, ты жив? Очнись!', 12, 0, 100, 0, 0, 0, 100055, 0, 0, 0, 'Мастер Хайт to Монах школы Тигра');
INSERT INTO `creature_text` VALUES (97778, 6, 0, 'Заприте западную дверь, хватит с нас незваных гостей. Так, посмотрим, что тут у нас...', 12, 0, 100, 0, 0, 0, 100098, 0, 0, 0, 'Мастер Хайт to Монах школы Тигра');
INSERT INTO `creature_text` VALUES (97778, 7, 0, 'Нет... не может быть!', 12, 0, 100, 0, 0, 0, 100099, 0, 0, 0, 'Мастер Хайт to Монах школы Тигра');
INSERT INTO `creature_text` VALUES (97778, 8, 0, 'Их слишком много! Надо эвакуировать всех отсюда!', 12, 0, 100, 0, 0, 0, 100100, 0, 0, 0, 'Мастер Хайт to Монах школы Тигра');
INSERT INTO `creature_text` VALUES (97778, 9, 0, '$p, я прошу тебя спасти всех выживших и отправить их назад в храм. Чуан говорил про пещеру Журавля. Начни с нее. Скорее, пока нас не окружили со всех сторон!', 12, 0, 100, 0, 0, 0, 100101, 0, 0, 0, 'Мастер Хайт to Монах школы Тигра');
INSERT INTO `creature_text` VALUES (97778, 10, 0, 'Чэнь, как хорошо, что ты и твои ученики невредимы!', 12, 0, 100, 0, 0, 0, 100194, 0, 0, 0, 'Мастер Хайт to Девятая Цзя');
INSERT INTO `creature_text` VALUES (97778, 11, 0, 'Это был хороший бой, $n, но дело еще не закончено.', 12, 0, 100, 0, 0, 0, 100196, 0, 0, 0, 'Мастер Хайт to Девятая Цзя');
INSERT INTO `creature_text` VALUES (97778, 12, 0, 'Чэнь, уведи детей как можно дальше отсюда. Мы с |3-4($n) останемся и закроем портал. Да пребудет с тобой Белый Тигр, друг мой.', 12, 0, 100, 0, 0, 0, 100197, 0, 0, 0, 'Мастер Хайт to Девятая Цзя');
INSERT INTO `creature_text` VALUES (97778, 13, 0, '$n, нельзя позволить Легиону превратить это место в его форпост. Мы должны придумать, как закрыть портал.', 12, 0, 100, 0, 0, 0, 100199, 0, 0, 0, 'Мастер Хайт to Девятая Цзя');
INSERT INTO `creature_text` VALUES (97778, 14, 0, 'Этот демон просто создаст еще один портал, если мы не успеем с ним разобраться. Приготовься, $p!', 12, 0, 100, 0, 0, 0, 104052, 0, 0, 0, 'Мастер Хайт to Девятая Цзя');
INSERT INTO `creature_text` VALUES (97778, 15, 0, 'Молодец, $n! Портал получает энергию от камня Скверны, уничтожь камень!', 14, 0, 100, 0, 0, 0, 100449, 0, 0, 0, 'Мастер Хайт to Player');
INSERT INTO `creature_text` VALUES (97812, 1, 0, 'Do not touch the candle!', 12, 0, 100, 0, 0, 54334, 102744, 0, 0, 0, 'Грязнорыл-барахольщик to Player');
INSERT INTO `creature_text` VALUES (97812, 1, 0, 'Ракинишу!', 12, 0, 100, 0, 0, 54336, 102747, 0, 0, 0, 'Грязнорыл-барахольщик to Player');
INSERT INTO `creature_text` VALUES (97813, 1, 0, 'За Свечного Короля!', 12, 0, 100, 0, 0, 54339, 102751, 0, 0, 0, 'Грязнорыл-барахольщик to Player');
INSERT INTO `creature_text` VALUES (97817, 4, 0, 'Демон падет от моего копья, Песнь Теней. Развлекайся со своими бесами, пока я работаю.', 12, 0, 100, 0, 0, 54809, 100443, 21293, 21293, 0, 'Халн Крутогор to Player');
INSERT INTO `creature_text` VALUES (97827, 3, 0, 'Мой король, ты не всех убил.', 12, 0, 100, 0, 0, 54277, 99721, 9417, 9417, 0, 'Уларогг Скаломол to Player');
INSERT INTO `creature_text` VALUES (97847, 0, 0, 'Whoever is meddling here shall be dealt with shortly!', 14, 0, 100, 0, 0, 0, 99785, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (97863, 0, 0, 'Да, у меня есть то, что тебе нужно. Но просто так я это отдавать не собираюсь. Вот поменяться – это другое дело.', 12, 0, 100, 1, 0, 0, 104787, 0, 0, 0, 'Костолют to Player');
INSERT INTO `creature_text` VALUES (97863, 1, 0, 'Тут наверху лежит один красивый красный камушек, который мне сразу пришелся по душе. Раздобудь его для меня, и я тебе сменяю на него твой сердечник.', 12, 0, 100, 1, 0, 0, 104788, 0, 0, 0, 'Костолют to Player');
INSERT INTO `creature_text` VALUES (97863, 2, 0, 'Приятно иметь с тобой дело, $gприятель:подруга;.', 12, 0, 100, 1, 0, 0, 104789, 0, 0, 0, 'Костолют to Player');
INSERT INTO `creature_text` VALUES (97898, 0, 0, 'He put up quite a fight, but your archdruid belongs to me now...', 12, 0, 100, 0, 0, 52177, 97115, 0, 0, 0, 'Проекция Ксавия to Player');
INSERT INTO `creature_text` VALUES (97901, 0, 0, 'Aaaaah!', 14, 0, 100, 0, 0, 0, 99937, 0, 0, 0, 'Воитель Оро to Player');
INSERT INTO `creature_text` VALUES (97903, 3, 0, 'Халн! Отрадно видеть тебя на поле боя! Хм... ты подрос?', 12, 0, 100, 3, 0, 54808, 100442, 36978, 36978, 0, 'Джерод Песнь Теней to Player');
INSERT INTO `creature_text` VALUES (97923, 0, 0, 'Хорошо! В этом семени заключена магия еще тех времен, когда мир был молодым. Как ты скоро увидишь своими глазами, это нам очень пригодится.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Ренсар Большое Копыто to Player');
INSERT INTO `creature_text` VALUES (97923, 1, 0, 'Сила твоего оружия велика. И она будет продолжать расти вместе с твоей.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Ренсар Большое Копыто to Player');
INSERT INTO `creature_text` VALUES (97956, 0, 0, 'Вам не остановить меня! Никогда!', 14, 0, 100, 0, 0, 54813, 101048, 52279, 52279, 0, 'Тихондрий');
INSERT INTO `creature_text` VALUES (97956, 1, 0, 'Вам не победить! Имя нам – Легион!', 14, 0, 100, 0, 0, 54811, 101046, 115494, 115494, 0, 'Тихондрий');
INSERT INTO `creature_text` VALUES (97956, 2, 0, 'Склонитесь перед новыми владыками!', 14, 0, 100, 0, 0, 54810, 101045, 5479, 5479, 0, 'Тихондрий');
INSERT INTO `creature_text` VALUES (97968, 0, 0, 'Ха-ха! Еще штучки, которые горят!', 12, 0, 100, 0, 0, 0, 100130, 0, 0, 0, 'Виззнак to Player');
INSERT INTO `creature_text` VALUES (97973, 0, 0, 'Смотри-ка, что вороны принесли...', 12, 0, 100, 0, 0, 0, 99874, 0, 0, 0, 'Javi to Player');
INSERT INTO `creature_text` VALUES (97978, 0, 0, 'Я верховный маг Кадгар, глава Кирин-Тора.!', 12, 0, 100, 0, 0, 0, 99656, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (97978, 1, 0, 'Охотники на демонов, Азероту нужна ваша помощь.', 12, 0, 100, 0, 0, 0, 99658, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (97979, 0, 0, 'Спасибо! Я снова перед тобой в долгу!', 12, 0, 100, 0, 0, 53094, 98515, 0, 0, 0, 'Ветир to Player');
INSERT INTO `creature_text` VALUES (97979, 3, 0, 'I will not Give Up!', 12, 0, 100, 0, 0, 53093, 98514, 7810, 7810, 0, 'Ветир to Player');
INSERT INTO `creature_text` VALUES (97986, 0, 0, 'We are done here, champion. Let us return to Havi.', 12, 0, 100, 0, 0, 53084, 98527, 0, 0, 0, 'Ветир to Player');
INSERT INTO `creature_text` VALUES (97988, 3, 0, 'Щитоносец является тогда, когда он больше всего нужен.', 12, 0, 100, 1, 0, 71466, 121029, 37267, 37267, 0, 'Хави to Player');
INSERT INTO `creature_text` VALUES (97989, 0, 0, 'Г\'ханиру грозит опасность? В таком случае нельзя терять времени.', 12, 0, 100, 0, 0, 0, 106820, 0, 0, 0, 'Листобород Знаменитый to Player');
INSERT INTO `creature_text` VALUES (97992, 0, 0, 'They will make me helarjar for this!', 12, 0, 100, 0, 0, 0, 100359, 0, 0, 0, 'Опытный гарпунщик to Player');
INSERT INTO `creature_text` VALUES (97992, 1, 0, 'You\'ve come to the wrong place, hero.', 12, 0, 100, 0, 0, 0, 100358, 0, 0, 0, 'Опытный гарпунщик to Player');
INSERT INTO `creature_text` VALUES (97993, 0, 0, 'I\'ll see you in... Helheim.', 12, 0, 100, 0, 0, 0, 100363, 0, 0, 0, 'Опытный головорез to Player');
INSERT INTO `creature_text` VALUES (97993, 1, 0, 'Join the other souls in Helheim, fool!', 12, 0, 100, 0, 0, 0, 100360, 0, 0, 0, 'Опытный головорез to Player');
INSERT INTO `creature_text` VALUES (97993, 2, 0, 'You won\'t make it out of here alive!', 12, 0, 100, 0, 0, 0, 100361, 0, 0, 0, 'Опытный головорез to Player');
INSERT INTO `creature_text` VALUES (97993, 3, 0, 'They will make me helarjar for this!', 12, 0, 100, 0, 0, 0, 100359, 0, 0, 0, 'Опытный головорез to Player');
INSERT INTO `creature_text` VALUES (97993, 6, 0, 'You\'ve come to the wrong place, hero.', 12, 0, 100, 0, 0, 0, 100358, 0, 0, 0, 'Опытный головорез to Player');
INSERT INTO `creature_text` VALUES (97994, 0, 0, 'You won\'t make it out of here alive!', 12, 0, 100, 0, 0, 0, 100361, 0, 0, 0, 'Старший резчик рун to Player');
INSERT INTO `creature_text` VALUES (97994, 1, 0, 'You\'ve come to the wrong place, hero.', 12, 0, 100, 0, 0, 0, 100358, 0, 0, 0, 'Старший резчик рун to Player');
INSERT INTO `creature_text` VALUES (97999, 1, 0, 'Жалкий каркан! Не лезь, куда не надо!', 12, 0, 100, 0, 0, 57967, 107086, 0, 0, 0, 'Глубокоскальный укротитель червей to Player');
INSERT INTO `creature_text` VALUES (97999, 1, 0, 'Шан ронир! Вали к тауренам!', 12, 0, 100, 0, 0, 57968, 107087, 0, 0, 0, 'Глубокоскальный укротитель червей to Player');
INSERT INTO `creature_text` VALUES (97999, 4, 0, 'Пора кушать!', 12, 0, 100, 0, 0, 0, 101572, 8271, 8271, 0, 'Глубокоскальный укротитель червей to Player');
INSERT INTO `creature_text` VALUES (98001, 3, 0, 'I ... Something I feel bad.', 12, 0, 100, 462, 0, 62045, 100067, 66988, 66988, 0, 'Послушник Чуан to Чэнь Буйный Портер');
