INSERT INTO `creature_text` VALUES (98007, 7, 0, 'A little farther before we fight, hero! I have a surprise for you...', 14, 0, 100, 0, 0, 0, 100316, 0, 0, 0, 'Вигфус Вихрь Клинков to Player');
