INSERT INTO `creature_text` VALUES (98266, 3, 0, 'Time is short. If you have found this message, you are capable of finding me as well.', 12, 0, 100, 1, 0, 67268, 100094, 0, 0, 0, 'Echo of First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (98266, 4, 0, 'Make haste for Suramar. You may be our last hope.', 12, 0, 100, 25, 0, 67269, 100096, 0, 0, 0, 'Echo of First Arcanist Thalyssra to Player');
