INSERT INTO `creature_text` VALUES (98312, 13, 0, 'Thank you...', 12, 0, 100, 0, 0, 67380, 101211, 0, 0, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (98312, 14, 0, 'What is this place?', 12, 0, 100, 0, 0, 67381, 101212, 0, 0, 0, 'First Arcanist Thalyssra to Player');
