INSERT INTO `creature_text` VALUES (98312, 16, 0, 'For now, at least, we know the withered have learned to avoid this place.', 12, 0, 100, 0, 0, 67395, 101214, 0, 0, 0, 'First Arcanist Thalyssra to Player');
