INSERT INTO `creature_text` VALUES (98358, 3, 0, 'Кровавый след предательства, что тянулся за тобой, сам привел меня к тебе!', 14, 0, 100, 603, 0, 0, 100161, 76616, 76616, 0, 'Эйлин Ворона to Player');
