INSERT INTO `creature_text` VALUES (98361, 2, 0, 'Будь ты проклята вместе со своими черными птицами...', 12, 0, 100, 0, 0, 0, 100166, 0, 0, 213738, 'Кровавый Ворон to Player');
INSERT INTO `creature_text` VALUES (98361, 3, 0, 'I see you are not alone. However, it doesn’t matter. I will kill you and throw your bodies off the rocks!', 14, 0, 100, 396, 0, 0, 100163, 15100, 15100, 0, 'Кровавый Ворон to Player');
