INSERT INTO `creature_text` VALUES (98367, 0, 0, 'Если ты хочешь узнать секреты моих чар, тебе стоит только попросить. Ну и принести мне материалы для работы, само собой.', 12, 0, 100, 1, 0, 0, 111295, 0, 0, 0, 'Чаровница Тигрид to Player');
INSERT INTO `creature_text` VALUES (98378, 0, 0, 'Here! That demon must\'ve crawled this way. There\'s fel blood splattered on the stones.', 12, 0, 100, 0, 0, 0, 100384, 0, 0, 0, 'Буревестница Милра to Тралл');
INSERT INTO `creature_text` VALUES (98378, 1, 0, 'Бесы! Наверно, этих паразитов Гет\'зун призвал.', 12, 0, 100, 0, 0, 0, 100387, 0, 0, 0, 'Буревестница Милра to Dave\'s Industrial Light and Magic Bunny (Small)(Sessile)');
INSERT INTO `creature_text` VALUES (98378, 2, 0, 'Watch yourself! More imps!', 12, 0, 100, 0, 0, 0, 100389, 0, 0, 0, 'Буревестница Милра to Dave\'s Industrial Light and Magic Bunny (Small)(Sessile)');
INSERT INTO `creature_text` VALUES (98378, 3, 0, 'Тралл, этот демон внизу все еще жив.', 12, 0, 100, 0, 0, 0, 100390, 0, 0, 0, 'Буревестница Милра to Dave\'s Industrial Light and Magic Bunny (Small)(Sessile)');
