INSERT INTO `creature_text` VALUES (98378, 5, 0, 'Keep your eyes on Thrall. He\'s wounded. He\'s like to kill himself down here, and I don\'t wanna be the one who has to tell his wife!', 12, 0, 100, 0, 0, 0, 106993, 0, 0, 0, 'Буревестница Милра to Dave\'s Industrial Light and Magic Bunny (Small)(Sessile)');
