INSERT INTO `creature_text` VALUES (98378, 8, 0, 'Shaman, we can\'t let that weapon fall to the wrong hands. I\'ll distract him. You grab the Doomhammer. No time to argue! Go!', 12, 0, 100, 0, 0, 0, 100397, 0, 0, 0, 'Буревестница Милра to Player');
INSERT INTO `creature_text` VALUES (98378, 9, 0, '\'Ey! You there! Me bird craps scarier demons than you!', 12, 0, 100, 0, 0, 0, 100398, 0, 0, 0, 'Буревестница Милра to Player');
INSERT INTO `creature_text` VALUES (98378, 10, 0, 'Давай покажу!', 14, 0, 100, 0, 0, 0, 100399, 0, 0, 0, 'Буревестница Милра to Player');
INSERT INTO `creature_text` VALUES (98378, 11, 0, 'You got it! OOF!', 12, 0, 100, 0, 0, 0, 100400, 0, 0, 0, 'Буревестница Милра to Player');
INSERT INTO `creature_text` VALUES (98378, 12, 0, '...похоже, дальше ты пойдешь без меня, шаман... Скоро конец туннеля...', 12, 0, 100, 0, 0, 0, 100401, 0, 0, 0, 'Буревестница Милра to Молот Рока');
INSERT INTO `creature_text` VALUES (98378, 13, 0, 'Спасибо...', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Буревестница Милра to Player');
INSERT INTO `creature_text` VALUES (98378, 14, 0, 'Идем. Только с такими героями, как ты, мы еще сможем спасти этот мир. В Водоворот!', 12, 0, 100, 0, 0, 0, 100403, 0, 0, 0, 'Буревестница Милра to Клюв Бури');
INSERT INTO `creature_text` VALUES (98379, 0, 0, 'Pathetic puny shaman! That hammer did little to help its last master.', 12, 0, 100, 0, 0, 0, 100407, 0, 0, 0, 'Гет\'зун to Player');
