INSERT INTO `creature_text` VALUES (98632, 0, 0, 'Для меня всегда было честью служить Иллидану. Но стать настоящей иллидари... Не смотри на меня, $n. Похоже, я сейчас заплачу.', 12, 0, 100, 66, 0, 66979, 115913, 0, 0, 0, 'Верховная мать Злоба to Player');
