INSERT INTO `creature_text` VALUES (98632, 2, 0, 'Врата уже совсем скоро откроются, дорогуша.', 12, 0, 100, 1, 0, 66883, 113224, 0, 0, 0, 'Верховная мать Злоба to Player');
INSERT INTO `creature_text` VALUES (98646, 0, 0, 'Не хватает лишь пары деталей из других командных центров. В остальном \"Молот Скверны\" готов к прыжку в Нискару.', 12, 0, 100, 0, 0, 66982, 115930, 0, 0, 0, 'Джейс Темный Ткач to Player');
INSERT INTO `creature_text` VALUES (98648, 0, 0, 'Клянусь, $n, я брошу все свои силы, пущу каждую крупицу энергии Косы душ на то, чтобы узнать, где находятся Кария и Варедис.', 12, 0, 100, 1, 0, 66980, 115914, 0, 0, 0, 'Аллари Пожирательница Душ to Player');
INSERT INTO `creature_text` VALUES (98648, 0, 0, 'Не вздумай испытывать мое терпение, демон. Отвечай!', 12, 0, 100, 0, 0, 0, 106284, 0, 0, 0, 'Аллари Пожирательница Душ');
INSERT INTO `creature_text` VALUES (98648, 3, 0, 'Cooperate or die.', 12, 0, 100, 0, 0, 0, 106294, 235326, 235326, 205350, 'Аллари Пожирательница Душ');
INSERT INTO `creature_text` VALUES (98648, 3, 0, 'I\'m not going to spend all day on you. Talk and get it over with!', 12, 0, 100, 0, 0, 0, 106289, 42217, 42217, 205350, 'Аллари Пожирательница Душ');
INSERT INTO `creature_text` VALUES (98648, 3, 0, 'Мы через столько прошли вместе. Конечно, я согласна!', 12, 0, 100, 0, 0, 66872, 115935, 3363, 3363, 0, 'Аллари Пожирательница Душ to Player');
INSERT INTO `creature_text` VALUES (98648, 3, 0, 'От тебя останется только грязная душа.', 12, 0, 100, 0, 0, 0, 106286, 8984, 8984, 205350, 'Аллари Пожирательница Душ');
INSERT INTO `creature_text` VALUES (98648, 3, 0, 'Помоги нам или умри!', 12, 0, 100, 0, 0, 0, 106294, 31571, 31571, 205350, 'Аллари Пожирательница Душ');
INSERT INTO `creature_text` VALUES (98648, 3, 0, 'Расскажи мне все, что знаешь, или умри.', 12, 0, 100, 0, 0, 0, 106296, 2226469, 2226469, 205350, 'Аллари Пожирательница Душ');
INSERT INTO `creature_text` VALUES (98648, 3, 0, 'Я знаю способы развязать тебе язык.', 12, 0, 100, 0, 0, 0, 0, 42615, 42615, 205350, 'Аллари Пожирательница Душ');
INSERT INTO `creature_text` VALUES (98683, 0, 0, 'Elune, grant me the blessing of your purity one last time.', 12, 0, 0, 0, 0, 0, 100422, 0, 0, 0, '');
