INSERT INTO `creature_text` VALUES (98700, 0, 0, 'Дворфу ясно, что мои таланты тебе точно пригодятся. Давай вместе отправимся на поиски Варгота и камня души!', 12, 0, 100, 0, 0, 67664, 113344, 0, 0, 0, 'Миллхаус Манашторм to Player');
