INSERT INTO `creature_text` VALUES (98706, 0, 0, 'Братья и сестры, эти захватчики пришли по наши души. Дадим же им отпор! В атаку!', 14, 0, 100, 0, 0, 0, 106226, 0, 0, 0, 'Командир Шемда\'сон to Восставший чародей');
INSERT INTO `creature_text` VALUES (98711, 0, 0, 'Мать бесов – прямо в этой пещере.', 12, 0, 100, 0, 0, 55290, 100666, 0, 0, 0, 'Кор\'вас Кровавый Шип to Player');
INSERT INTO `creature_text` VALUES (98711, 1, 0, 'I have to go. Kayn\'s called me back to handle something. He probably needs help killing a demon.', 12, 0, 100, 0, 0, 55291, 98845, 0, 0, 0, 'Кор\'вас Кровавый Шип to Player');
INSERT INTO `creature_text` VALUES (98717, 3, 0, 'Идем. Королева-ведьма засиделась на своем троне.', 12, 0, 100, 0, 0, 54298, 96944, 1360, 1360, 0, 'Ласан Небесный Рог to Player');
INSERT INTO `creature_text` VALUES (98727, 3, 0, '$n, мои топоры... и мои питомцы... к твоим услугам.', 12, 0, 100, 113, 0, 0, 105188, 123426, 123426, 0, 'Рексар to Player');
INSERT INTO `creature_text` VALUES (98737, 0, 0, 'Когда ночные эльфы, основатели Незримого пути, покинули гору Хиджал и вернулись на Расколотые острова, таурены Крутогорья прониклись важностью их миссии и предложили им свою дружбу. Именно потомки Хална Крутогора привели Незримый путь на эту вершину.$b$bС того самого дня, как в наш мир вернулся Легион, таурены Крутогорья принимали самое деятельное участие в восстановлении Приюта – например, они построили новые здания на месте двух разрушенных сторожевых башен.', 12, 0, 100, 1, 0, 0, 105030, 0, 0, 0, 'Хольт Громовой Рог to Player');
INSERT INTO `creature_text` VALUES (98738, 3, 0, 'Часовые готовы оказать тебе помощь.', 12, 0, 100, 66, 0, 0, 105671, 122687, 122687, 0, 'Шандриса Оперенная Луна to Player');
INSERT INTO `creature_text` VALUES (98739, 3, 0, 'Странники будут сражаться за тебя!', 12, 0, 100, 603, 0, 61861, 105217, 8274, 8274, 0, 'Халдарон Светлое Крыло to Player');
INSERT INTO `creature_text` VALUES (98740, 3, 0, 'Она... так прекрасна!', 12, 0, 100, 1, 0, 61862, 106256, 123954, 123954, 0, 'Вериса Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (98754, 0, 0, 'НЕ-Е-ЕТ! СПАСАЙСЯ!', 14, 0, 100, 0, 0, 0, 100440, 0, 0, 0, 'Неряха Джо to Player');
INSERT INTO `creature_text` VALUES (98760, 0, 0, 'You step into the land of the dead, mortal. The Lich King has decreed that the living shall not enter Icecrown!', 14, 0, 100, 0, 0, 0, 101127, 0, 0, 0, 'Рожденная во льдах кудесница to 0');
INSERT INTO `creature_text` VALUES (98760, 1, 0, '%s shatters all remaining ice blocks, freeing the geists inside!', 41, 0, 100, 0, 0, 0, 103184, 0, 0, 0, 'Рожденная во льдах кудесница to 0');
