INSERT INTO `creature_text` VALUES (98784, 3, 0, 'Здорово, дорогуша! Это теперь твой дом. Для верховных друидов – все самое лучшее!', 12, 0, 100, 0, 0, 0, 106704, 1308, 1308, 0, 'Зен\'кики to Player');
