INSERT INTO `creature_text` VALUES (98854, 3, 0, 'I heard the harpies hit Skyhorn hard. Lasan has been pulling back all of his forces to defend their home.', 12, 0, 100, 1, 0, 0, 101455, 138669, 138669, 0, 'Displaced Rivermane to Player');
INSERT INTO `creature_text` VALUES (98854, 3, 0, 'Говорят, гарпии нанесли мощный удар по Небесному Рогу. Ласан отвел все свои войска, чтобы защитить родной дом.', 12, 0, 100, 1, 0, 0, 101455, 158981, 158981, 0, 'Беженец из племени Речной Гривы to Player');
INSERT INTO `creature_text` VALUES (98855, 3, 0, 'Речной Рог уничтожен, и мы должны с этим смириться. Когда-нибудь мы восстановим наш дом. Но сейчас мы должны ждать и оказывать посильную помощь Громовому Тотему – так же, как они помогают нам.', 12, 0, 100, 1, 0, 0, 100507, 3606336, 3606336, 0, 'Беженец из племени Речной Гривы to Player');
