INSERT INTO `creature_text` VALUES (98904, 0, 0, 'The chief is expecting you down in the cave, Rivermane.', 12, 0, 100, 0, 0, 0, 99088, 0, 0, 0, 'Завоевательница из племени Кровавого Тотема to Player');
