INSERT INTO `creature_text` VALUES (99109, 3, 0, 'Славная охота! А теперь идем в лагерь, пока я не забыла деталей.', 12, 0, 100, 1, 0, 54857, 100811, 97279, 97279, 0, 'Адди Шипотопчик to Player');
INSERT INTO `creature_text` VALUES (99126, 3, 0, 'Куда направляешься, Великий мастер?', 12, 0, 100, 509, 0, 0, 108376, 8581, 8581, 0, 'Послушница школы Журавля to Player');
INSERT INTO `creature_text` VALUES (99126, 3, 0, 'Нам нельзя сдаваться ни в коем случае. Я полностью полагаюсь на твою мудрость.', 12, 0, 100, 0, 0, 0, 108378, 11460, 11460, 0, 'Послушница школы Журавля to Player');
INSERT INTO `creature_text` VALUES (99126, 3, 0, 'Фу Линь победит. В скорости его превосходит лишь великий мастер.', 12, 0, 100, 1, 0, 33646, 100785, 1319356, 1319356, 0, 'Послушница школы Журавля');
INSERT INTO `creature_text` VALUES (99134, 3, 0, 'Ты уж извини, но Цао явно боец получше. Только глянь, какие мускулы!', 12, 0, 100, 0, 0, 0, 100779, 1363948, 1363948, 0, 'Монахиня школы Змеи');
INSERT INTO `creature_text` VALUES (99135, 3, 0, 'The Order of the Broken Temple will survive under your leadership. It must...', 12, 0, 100, 6, 0, 0, 108375, 1331903, 1331903, 0, 'Послушница школы Тигра to Player');
INSERT INTO `creature_text` VALUES (99141, 0, 0, 'А, ты $gочнулся:очнулась;! Надеюсь, тебе лучше? Ты так долго $gпролежал:пролежала; без сознания!', 12, 0, 100, 603, 0, 0, 0, 0, 0, 0, 'Сестра Ян to Player');
INSERT INTO `creature_text` VALUES (99141, 3, 0, 'To be a monk is to strive for harmony and balance. There is no one better to lead us than you, $n.', 12, 0, 100, 603, 0, 0, 108379, 12268, 12268, 0, 'Сестра Ян to Player');
INSERT INTO `creature_text` VALUES (99141, 3, 0, 'Остальные выжившие сейчас наверху, в храме. Если уже чувствуешь себя получше, тебе следует тоже отправиться туда.', 12, 0, 100, 2, 0, 0, 0, 10621, 10621, 0, 'Сестра Ян to Player');
INSERT INTO `creature_text` VALUES (99178, 0, 0, 'Беги, $gдруг:подруга;! Надо спасаться через врата!', 14, 0, 100, 0, 0, 0, 103429, 0, 0, 0, 'Калид to Player');
INSERT INTO `creature_text` VALUES (99178, 1, 0, '$gДруг:Подруга;, Зловещий Шрам в твоем распоряжении!', 14, 0, 100, 0, 0, 0, 103868, 0, 0, 0, 'Калид to Player');
INSERT INTO `creature_text` VALUES (99179, 0, 0, 'Чэнь и Ли Ли – сильные союзники нашего ордена. Я знаю, куда направить их таланты.', 12, 0, 100, 1, 0, 0, 116315, 0, 0, 0, 'Мастер Хсу to Player');
INSERT INTO `creature_text` VALUES (99181, 0, 0, '%s жестом велит вам следовать за собой.', 16, 0, 100, 0, 0, 0, 102052, 0, 0, 0, 'Грозный Цзан to Player');
INSERT INTO `creature_text` VALUES (99181, 1, 0, '%s одобрительно кивает.', 16, 0, 100, 0, 0, 0, 102056, 0, 0, 0, 'Грозный Цзан to Player');
INSERT INTO `creature_text` VALUES (99181, 2, 0, 'But as we teetered upon the brink of annihilation, $n stayed with Grand Master Hight and fought the demons to ensure our survival.', 12, 0, 100, 0, 0, 0, 102076, 0, 0, 0, 'Грозный Цзан to Player');
INSERT INTO `creature_text` VALUES (99181, 3, 0, 'Grand Master Hight has passed on to join the ancestors, and we are left with a void in our ranks.', 12, 0, 100, 0, 0, 0, 102078, 0, 0, 0, 'Грозный Цзан to Player');
INSERT INTO `creature_text` VALUES (99181, 4, 0, 'It seems fitting that the hero who fought at his side, who displayed courage and valor immeasurable, should succeed him.', 12, 0, 100, 0, 0, 0, 102089, 0, 0, 0, 'Грозный Цзан to Player');
INSERT INTO `creature_text` VALUES (99188, 0, 0, 'Почему ты не в клетке, |3-6($c)?', 14, 0, 100, 0, 0, 0, 100454, 0, 0, 0, 'Промокший насквозь страж душ to Player');
