INSERT INTO `creature_text` VALUES (99213, 2, 0, 'This is not your business, outlander. Thalyssra has betrayed the Grand Magistrix and must be put down!', 12, 0, 100, 0, 0, 67550, 100921, 0, 0, 0, 'Erasmus Moonblade to 0');
