INSERT INTO `creature_text` VALUES (99247, 3, 0, '$p, Отчаяние Тьмы и Гребень Ворона проводят тебя в Сурамар. У маленького отряда больше шансов незаметно проникнуть в крепость.', 12, 0, 100, 0, 0, 57566, 96561, 305917, 305917, 0, 'Кайн Ярость Солнца to Player');
